      writePrompt();
      return;
    }

    setLoadingCommand(true);
    setTerminalError(null);

    try {
      const response = await bridgeRef.current.executeCommand(trimmed);
      if (response.success) {
        const result = normalizeCliResult(response.data, trimmed);
        if (result.stdout) {
          result.stdout.replace(/\r?\n/g, '\n').split('\n').forEach((line) => term.writeln(line));
        }
        if (result.stderr) {
          result.stderr.replace(/\r?\n/g, '\n').split('\n').forEach((line) => {
            term.writeln(`\x1b[31m${line}\x1b[0m`);
          });
        }
        term.writeln(`exit code ${result.exitCode}`);
        setLastResult(result);
      } else {
        term.writeln(`Command failed: ${response.error}`);
      }
    } catch (error: unknown) {
      term.writeln(`Unexpected error: ${getErrorMessage(error)}`);
    } finally {
      setLoadingCommand(false);
      writePrompt();
    }
  }, [normalizeCliResult, runBuiltInCommand, writeLine, writePrompt]);

  const appendHistory = useCallback((command: string) => {
