    }
  }, [newCommand, newDescription, newResponse, loadCustomCommands]);

  const handleDeleteCustomCommand = useCallback(
    async (commandId: string) => {
      if (!bridgeRef.current) return;
      try {
        const response = await bridgeRef.current.deleteCustomCommand(commandId, userId);
        if (response.success) {
          await loadCustomCommands();
