  }, [userId, loadCommandHistory, loadCustomCommands]);

  useEffect(() => {
    if (!containerRef.current || terminalRef.current) {
      return;
    }

    const term = new Terminal({
      cursorBlink: true,
      cursorStyle: 'block',
      fontFamily: '"Cascadia Code", "Fira Code", "Courier New", monospace',
