/**
 * AldenDashboardGrid - CSS Grid-based responsive dashboard layout
 * Redesigned with 3-row layout for improved space utilization
 *
 * Layout:
 * +-------------------------------------------+
 * | Active Tasks | Schedule | Email Summary   |  <- Row 1 (3 compact metric cards)
 * +-------------------------------------------+
 * | Mission Queue    | Smart Suggestions      |  <- Row 2 (Main content area)
 * | (span 2)         | (span 2)               |
 * +-------------------------------------------+
 * | Top Skills       | Unread by Account      |  <- Row 3 (Supplementary data)
 * | (span 1)         | (span 3)               |
 * +-------------------------------------------+
 */
import React, { memo, useMemo } from 'react';
import { Plus, Calendar, CheckCircle2, Mail, Zap, Sparkles, Star, Link2Off } from '@/components/ui/HearthlinkIcons';
import type { LucideIcon } from 'lucide-react';

import type { Task, CalendarEvent } from '../../types/agents';
import { MetricCard } from '@/components/shared/premium/MetricCard';

// ========================================
// DashboardEmptyState - Reusable empty state component
// ========================================
interface DashboardEmptyStateProps {
  icon: LucideIcon;
  message: string;
  variant?: 'default' | 'compact';
}

const DashboardEmptyState: React.FC<DashboardEmptyStateProps> = memo(({
  icon: Icon,
  message,
  variant = 'default'
}) => {
  const isCompact = variant === 'compact';

  return (
    <div className={`flex flex-col items-center justify-center ${isCompact ? 'py-3' : 'py-8'} text-[#9fb4d6]/40 animate-fadeIn`}>
      <div className={`${isCompact ? 'mb-1.5' : 'mb-3'} relative`}>
        <Icon
          size={isCompact ? 24 : 32}
          className="opacity-40 animate-pulse"
          style={{ animationDuration: '3s' }}
        />
        <div
          className="absolute inset-0 blur-xl opacity-20"
          style={{
            background: 'radial-gradient(circle, rgba(0,240,255,0.3) 0%, transparent 70%)'
          }}
        />
      </div>
      <span className={`${isCompact ? 'text-[10px]' : 'text-xs'} font-medium tracking-wide uppercase`}>
        {message}
      </span>
    </div>
  );
});

DashboardEmptyState.displayName = 'DashboardEmptyState';

export interface ExpandedAldenStats {
  totalTasks: number;
  completedToday: number;
  overdue: number;
  upcomingEvents: number;
  unreadEmails: number;
  smartTasks: Array<{ taskId: string; title: string; reason: string }>;
  skillUsage: Array<{ skillId: string; name: string; usageCount: number }>;
  emailCounts: Array<{ accountId: string; accountName: string; unread: number }>;
}

interface AldenDashboardGridProps {
  stats: ExpandedAldenStats;
  tasks: Task[];
  events: CalendarEvent[];
  onTaskClick: (task: Task) => void;
  onEventClick: (event: CalendarEvent) => void;
  onNewTask: () => void;
  onNewEvent: () => void;
  onSmartTaskApply: (task: { taskId: string; title: string; reason: string }) => void;
  onSkillClick: (skillId: string) => void;
  onEmailAccountClick: (accountId: string) => void;
}

// Frame style for panels
const panelStyle: React.CSSProperties = {
  border: '1px solid rgba(0, 205, 255, 0.35)',
  boxShadow: '0 0 20px rgba(0, 240, 255, 0.08), inset 0 0 12px rgba(197,160,89,0.08)',
  background: 'linear-gradient(135deg, rgba(11,17,36,0.95), rgba(0,240,255,0.02))',
};

// Priority pill styling
const priorityPill = (priority: string) => {
  const colors: Record<string, string> = {
    high: 'bg-red-500/20 text-red-400 border-red-500/30',
    medium: 'bg-yellow-500/20 text-yellow-400 border-yellow-500/30',
    low: 'bg-green-500/20 text-green-400 border-green-500/30',
  };
  return colors[priority] || colors.low;
};

// Status pill styling
const statusPill = (status: string) => {
  const colors: Record<string, string> = {
    pending: 'bg-slate-500/20 text-slate-400 border-slate-500/30',
    in_progress: 'bg-blue-500/20 text-blue-400 border-blue-500/30',
    completed: 'bg-green-500/20 text-green-400 border-green-500/30',
  };
  return colors[status] || colors.pending;
};

// Mock data for smart suggestions
const mockSuggestions = [
  { id: '1', title: 'Schedule Focus Time', type: 'suggestion', priority: 'high' },
  { id: '2', title: 'Review overdue tasks', type: 'reminder', priority: 'medium' },
  { id: '3', title: 'Check calendar conflicts', type: 'automation', priority: 'low' },
];

// Mock data for email accounts
const mockEmailAccounts = [
  { account: 'work@company.com', unread: 12, color: 'cyan' },
  { account: 'personal@gmail.com', unread: 5, color: 'gold' },
  { account: 'newsletter@sub.com', unread: 23, color: 'slate' },
];

// Mock data for top skills
const mockTopSkills = [
  { name: 'Email Triage', uses: 47, icon: Mail },
  { name: 'Task Batch', uses: 32, icon: CheckCircle2 },
  { name: 'Quick Reply', uses: 28, icon: Zap },
];

export const AldenDashboardGrid: React.FC<AldenDashboardGridProps> = memo(({
  stats,
  tasks,
  events,
  onTaskClick,
  onEventClick,
  onNewTask,
  onNewEvent,
  onSmartTaskApply,
  onSkillClick,
  onEmailAccountClick,
}) => {
  // Memoized metric data for Row 1
  const metrics = useMemo(() => [
    {
      id: 'tasks',
      title: 'Active Tasks',
      value: stats.totalTasks,
      subtitle: `${stats.overdue} overdue`,
      accent: 'cyan' as const,
      icon: <CheckCircle2 size={20} />,
      active: true
    },
    {
      id: 'schedule',
      title: 'Schedule',
      value: stats.upcomingEvents,
      subtitle: 'Next 7 Days',
      accent: 'gold' as const,
      icon: <Calendar size={20} />,
      active: stats.upcomingEvents > 0
    },
    {
      id: 'email',
      title: 'Email Summary',
      value: stats.unreadEmails,
      subtitle: 'Unread messages',
      accent: stats.unreadEmails > 10 ? 'gold' as const : 'cyan' as const,
      icon: <Mail size={20} />,
      active: stats.unreadEmails > 0
    },
  ], [stats]);

  return (
    <div className="w-full h-full p-3 overflow-auto">
      {/* CSS Grid Layout - 3 Rows */}
      <div
        className="grid gap-3 h-full"
        style={{
          gridTemplateColumns: 'repeat(4, 1fr)',
          gridTemplateRows: 'auto minmax(0, 1fr) auto',
        }}
      >
        {/* Æ’?Æ’?Æ’?Æ’?Æ’?Æ’?Æ’?Æ’?Æ’?Æ’?Æ’? ROW 1: Metric Cards (span 4 as 3 items) Æ’?Æ’?Æ’?Æ’?Æ’?Æ’?Æ’?Æ’?Æ’?Æ’?Æ’? */}
        {metrics.map((metric, idx) => (
          <div
            key={metric.id}
            className="min-w-0"
            style={{ gridColumn: idx === 2 ? 'span 2' : 'span 1' }}
          >
            <MetricCard
              title={metric.title}
              value={metric.value}
              subtitle={metric.subtitle}
              accentColor={metric.accent}
              icon={metric.icon}
              active={metric.active}
            />
          </div>
        ))}

        {/* Æ’?Æ’?Æ’?Æ’?Æ’?Æ’?Æ’?Æ’?Æ’?Æ’?Æ’? ROW 2: Mission Queue (span 2) + Smart Suggestions (span 2) Æ’?Æ’?Æ’?Æ’?Æ’?Æ’?Æ’?Æ’?Æ’?Æ’?Æ’? */}

        {/* Mission Queue Panel */}
        <div
          className="rounded-xl p-4 flex flex-col overflow-hidden min-h-0"
          style={{ ...panelStyle, gridColumn: 'span 2' }}
        >
          <div className="flex items-center justify-between mb-3 shrink-0">
            <h2 className="text-sm font-semibold text-alden-gold uppercase tracking-widest flex items-center gap-2 font-orbitron-force">
              <CheckCircle2 size={16} />
              Mission Queue
            </h2>
            <button
              onClick={onNewTask}
              className="flex items-center gap-1 px-3 py-1 bg-alden-cyan/10 border border-alden-cyan/30 rounded-full text-[10px] uppercase tracking-wider text-alden-cyan hover:bg-alden-cyan/20 transition-all font-semibold"
            >
              <Plus size={10} />
              New
            </button>
          </div>

          <div className="flex-1 overflow-y-auto space-y-2 min-h-0">
            {tasks.length > 0 ? (
              tasks.slice(0, 5).map((task, idx) => (
                <div
                  key={task.id}
                  className="p-3 bg-alden-bg/50 border border-alden-border rounded flex items-center justify-between group hover:bg-alden-bg/70 hover:border-alden-cyan/30 hover:shadow-[0_0_12px_rgba(0,240,255,0.1)] transition-all duration-200 cursor-pointer animate-fadeIn"
                  style={{ animationDelay: `${idx * 50}ms` }}
                  onClick={() => onTaskClick(task)}
                >
                  <div className="flex items-center gap-3 min-w-0">
                    <div className={`w-1.5 h-1.5 rounded-full shrink-0 ${task.priority === 'high' ? 'bg-[#ef4444] shadow-[0_0_5px_rgba(239,68,68,0.5)]' : 'bg-alden-cyan'}`} />
                    <span className="text-sm text-[#c5d2e8] font-medium truncate">{task.title}</span>
                  </div>
                  <div className="flex items-center gap-2 shrink-0">
                    <span className={`px-2 py-0.5 rounded-full text-[9px] uppercase border ${priorityPill(task.priority)}`}>
                      {task.priority}
                    </span>
                    <span className={`px-2 py-0.5 rounded-full text-[9px] uppercase border ${statusPill(task.status)}`}>
                      {task.status}
                    </span>
                  </div>
                </div>
              ))
            ) : (
              <DashboardEmptyState icon={CheckCircle2} message="No active tasks" />
            )}
          </div>
        </div>

        {/* Smart Suggestions Panel */}
        <div
          className="rounded-xl p-4 flex flex-col overflow-hidden min-h-0"
          style={{ ...panelStyle, gridColumn: 'span 2' }}
        >
          <div className="flex items-center justify-between mb-3 shrink-0">
            <h2 className="text-sm font-semibold text-alden-gold uppercase tracking-widest flex items-center gap-2 font-orbitron-force">
              <Sparkles size={16} />
              Smart Suggestions
            </h2>
            <span className="text-[10px] text-alden-cyan/60 uppercase tracking-wider">AI-Powered</span>
          </div>

          <div className="flex-1 overflow-y-auto space-y-2 min-h-0">
            {stats.smartTasks.length > 0 ? (
              stats.smartTasks.map((task, idx) => {
                const priorityColor =
                  task.reason === 'Overdue' || task.reason === 'High Priority' ? 'bg-red-500/20 text-red-400' :
                  task.reason === 'Due Today' ? 'bg-yellow-500/20 text-yellow-400' :
                  'bg-green-500/20 text-green-400';

                return (
                  <div
                    key={task.taskId}
                    className="p-3 bg-alden-bg/50 border border-alden-border rounded flex items-center gap-3 group hover:bg-alden-bg/70 hover:border-alden-gold/30 hover:shadow-[0_0_12px_rgba(197,160,89,0.1)] transition-all duration-200 cursor-pointer animate-fadeIn"
                    style={{ animationDelay: `${idx * 50}ms` }}
                  >
                    <div className={`w-8 h-8 rounded-lg flex items-center justify-center shrink-0 ${priorityColor}`}>
                      <Sparkles size={14} />
                    </div>
                    <div className="flex-1 min-w-0">
                      <span className="text-sm text-[#c5d2e8] font-medium block truncate">{task.title}</span>
                      <span className="text-[10px] text-[#9fb4d6]/60 uppercase tracking-wider">{task.reason}</span>
                    </div>
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        onSmartTaskApply(task);
                      }}
                      className="px-2 py-1 bg-alden-cyan/10 border border-alden-cyan/30 rounded text-[9px] uppercase tracking-wider text-alden-cyan hover:bg-alden-cyan/20 transition-all duration-200 opacity-0 group-hover:opacity-100"
                    >
                      Apply
                    </button>
                  </div>
                );
              })
            ) : (
              <DashboardEmptyState icon={Sparkles} message="No suggestions" />
            )}
          </div>
        </div>

        {/* Æ’?Æ’?Æ’?Æ’?Æ’?Æ’?Æ’?Æ’?Æ’?Æ’?Æ’? ROW 3: Top Skills (span 1) + Unread by Account (span 3) Æ’?Æ’?Æ’?Æ’?Æ’?Æ’?Æ’?Æ’?Æ’?Æ’?Æ’? */}

        {/* Top Skills Panel */}
        <div
          className="rounded-xl p-3 flex flex-col overflow-hidden"
          style={{ ...panelStyle, gridColumn: 'span 1' }}
        >
          <h2 className="text-xs font-semibold text-alden-gold uppercase tracking-widest flex items-center gap-2 mb-2 font-orbitron-force">
            <Star size={12} />
            Top Skills
          </h2>

          <div className="space-y-1.5">
            {stats.skillUsage.length > 0 ? stats.skillUsage.map((skill) => (
              <div
                key={skill.skillId}
                onClick={() => onSkillClick(skill.skillId)}
                className="flex items-center gap-2 p-2 bg-alden-bg/30 rounded hover:bg-alden-bg/50 cursor-pointer transition-colors"
              >
                <div className="w-6 h-6 rounded flex items-center justify-center bg-alden-cyan/10 text-alden-cyan shrink-0">
                  <Zap size={12} />
                </div>
                <span className="text-xs text-[#c5d2e8] truncate flex-1">{skill.name}</span>
                <span className="text-[10px] text-alden-cyan/70">{skill.usageCount}x</span>
              </div>
            )) : (
              <div className="text-center text-[10px] text-slate-500 py-2">No data</div>
            )}
          </div>
        </div>

        {/* Unread by Account Panel */}
        <div
          className="rounded-xl p-3 flex flex-col overflow-hidden"
          style={{ ...panelStyle, gridColumn: 'span 3' }}
        >
          <h2 className="text-xs font-semibold text-alden-gold uppercase tracking-widest flex items-center gap-2 mb-2 font-orbitron-force">
            <Mail size={12} />
            Unread by Account
          </h2>

          <div className="flex gap-3 overflow-x-auto">
            {stats.emailCounts.length > 0 ? stats.emailCounts.map((account, idx) => {
              const colors = ['cyan', 'gold', 'slate'];
              const color = colors[idx % colors.length];
              const displayColor = 
                  color === 'cyan' ? 'bg-alden-cyan/20 text-alden-cyan' :
                  color === 'gold' ? 'bg-alden-gold/20 text-alden-gold' :
                  'bg-slate-500/20 text-slate-400';

              return (
              <div
                key={account.accountId}
                onClick={() => onEmailAccountClick(account.accountId)}
                className="flex items-center gap-3 p-2 bg-alden-bg/30 rounded flex-1 min-w-[140px] cursor-pointer hover:bg-alden-bg/50 transition-colors"
                role="button"
                tabIndex={0}
              >
                <div className={`w-8 h-8 rounded-full flex items-center justify-center shrink-0 ${displayColor}`}>
                  <Mail size={14} />
                </div>
                <div className="flex-1 min-w-0">
                  <span className="text-xs text-[#c5d2e8] block truncate">{account.accountName}</span>
                  <span className="text-[10px] text-[#9fb4d6]/60 truncate block">{account.accountId.includes('@') ? account.accountId : 'Synced'}</span>
                </div>
                <div className={`px-2 py-0.5 rounded-full text-xs font-semibold ${
                  account.unread > 15 ? 'bg-red-500/20 text-red-400' :
                  account.unread > 5 ? 'bg-yellow-500/20 text-yellow-400' :
                  'bg-green-500/20 text-green-400'
                }`}>
                  {account.unread}
                </div>
              </div>
              );
            }) : (
               <div className="text-center text-xs text-slate-500 w-full py-4">No accounts linked</div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
});

AldenDashboardGrid.displayName = 'AldenDashboardGrid';

export default AldenDashboardGrid;

