import { useCallback, useEffect, useMemo, useState } from 'react';
import { FirstSevenState, TutorialStep, TutorialEventId } from '@/types/tutorial';
import { emitTutorialEvent, subscribeTutorialEvents, subscribeTutorialReset } from '@/utils/tutorialEvents';

const STORAGE_KEY = 'hl_first7_state';

const DEFAULT_STEPS: TutorialStep[] = [
  { id: 'day1-chat', day: 1, title: 'Send a hello', description: 'Send any message in chat.', completionEvent: 'hl:event:chat:sent', targetSelector: '[data-hl-tour=\"chat-input\"]' },
  { id: 'day1-switch', day: 1, title: 'Switch persona', description: 'Open the persona switcher or radial menu.', completionEvent: 'hl:event:persona:navigated', targetSelector: '[data-hl-tour=\"persona-switcher\"]' },
  { id: 'day1-help', day: 1, title: 'Visit Help center', description: 'Open the Help center card.', completionEvent: 'hl:event:help:opened', targetSelector: '[data-hl-tour=\"help-entry\"]' },

  { id: 'day2-quick-capture', day: 2, title: 'Quick Capture', description: 'Create a quick capture note.', completionEvent: 'hl:event:vault:quick_capture', targetSelector: '[data-hl-tour=\"quick-capture\"]' },
  { id: 'day2-vault-view', day: 2, title: 'Open Vault', description: 'Open the Vault dashboard or search.', completionEvent: 'hl:event:vault:viewed', targetSelector: '[data-hl-tour=\"vault-nav\"]' },
  { id: 'day2-search', day: 2, title: 'Search memories', description: 'Run a search in MemorySearchBar.', completionEvent: 'hl:event:vault:search', targetSelector: '[data-hl-tour=\"vault-search\"]' },

  { id: 'day3-alden-open', day: 3, title: 'Open Alden', description: 'Open Alden Command Center.', completionEvent: 'hl:event:alden:opened' },
  { id: 'day3-task-change', day: 3, title: 'Update a task', description: 'Create or mark a task.', completionEvent: 'hl:event:alden:task_change' },
  { id: 'day3-briefing', day: 3, title: 'Ask for briefing', description: 'Ask Alden for a briefing.', completionEvent: 'hl:event:alden:briefing' },

  { id: 'day4-alice-open', day: 4, title: 'Open Alice', description: 'Open the Alice dashboard.', completionEvent: 'hl:event:alice:opened' },
  { id: 'day4-focus-coach', day: 4, title: 'Trigger focus coach', description: 'Use focus coach or insight card.', completionEvent: 'hl:event:alice:focus_coach' },
  { id: 'day4-escalation', day: 4, title: 'Acknowledge support', description: 'Acknowledge an escalation/support tip.', completionEvent: 'hl:event:alice:escalation_ack' },

  { id: 'day5-synapse-open', day: 5, title: 'Open Synapse', description: 'Open Synapse dashboard/command center.', completionEvent: 'hl:event:synapse:opened' },
  { id: 'day5-insight', day: 5, title: 'Generate an insight', description: 'Create one insight or summary.', completionEvent: 'hl:event:synapse:insight_created' },
  { id: 'day5-save', day: 5, title: 'Save to Vault', description: 'Save an insight to Vault.', completionEvent: 'hl:event:synapse:saved' },

  { id: 'day6-delegate', day: 6, title: 'Route to another agent', description: 'Route a message to a second agent.', completionEvent: 'hl:event:chat:delegated' },
  { id: 'day6-compare', day: 6, title: 'Compare agent suggestions', description: 'Compare two agent suggestions or toggle persona tabs.', completionEvent: 'hl:event:persona:compare' },
  { id: 'day6-pin', day: 6, title: 'Pin a response', description: 'Pin a preferred response.', completionEvent: 'hl:event:chat:pinned' },

  { id: 'day7-settings', day: 7, title: 'Open Settings', description: 'Open the Settings panel.', completionEvent: 'hl:event:settings:opened', targetSelector: '[data-hl-tour=\"settings\"]' },
  { id: 'day7-toggle', day: 7, title: 'Toggle a preference', description: 'Update theme or notifications.', completionEvent: 'hl:event:settings:updated' },
  { id: 'day7-recap', day: 7, title: 'Review weekly recap', description: 'View the weekly recap card.', completionEvent: 'hl:event:tutorial:weekly_recap' },
];

const DEFAULT_STATE: FirstSevenState = {
  currentDay: 1,
  completedDays: [],
  completedSteps: [],
  lastActive: new Date().toISOString(),
  dismissed: false,
  autoOpened: false,
};

export function useFirstSevenDaysTour() {
  // Skip tutorial entirely in E2E test environment
  const isE2ETest = typeof window !== 'undefined' && (
    window.location.search.includes('skipTutorial=true') ||
    window.localStorage.getItem('hl_e2e_skip_tutorial') === 'true'
  );

  const [state, setState] = useState<FirstSevenState>(() => {
    if (typeof window === 'undefined') return DEFAULT_STATE;
    // For E2E tests, return completed state to skip tutorial
    if (isE2ETest) {
      return { ...DEFAULT_STATE, completedDays: [1, 2, 3, 4, 5, 6, 7], autoOpened: true };
    }
    try {
      const cached = window.localStorage.getItem(STORAGE_KEY);
      if (cached) {
        const parsed = JSON.parse(cached) as FirstSevenState;
        return { ...DEFAULT_STATE, ...parsed };
      }
    } catch (error) {
      console.warn('[First7Days] Failed to parse stored state', error);
    }
    return DEFAULT_STATE;
  });

  // In E2E mode, never auto-open
  const [isOpen, setIsOpen] = useState(false);

  const stepsByDay = useMemo(() => {
    return DEFAULT_STEPS.reduce<Record<number, TutorialStep[]>>((acc, step) => {
      acc[step.day] = acc[step.day] ? [...acc[step.day], step] : [step];
      return acc;
    }, {});
  }, []);

  useEffect(() => {
    if (typeof window === 'undefined') return;
    window.localStorage.setItem(STORAGE_KEY, JSON.stringify(state));
  }, [state]);

  useEffect(() => {
    return subscribeTutorialEvents((eventId: TutorialEventId) => {
      const targetStep = DEFAULT_STEPS.find((step) => step.completionEvent === eventId);
      if (targetStep) {
        completeStep(targetStep.id);
      }
    });
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  useEffect(() => {
    return subscribeTutorialReset(() => {
      reset();
    });
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const markLastActive = useCallback(() => {
    setState((prev) => ({ ...prev, lastActive: new Date().toISOString() }));
  }, []);

  const openTour = useCallback(() => {
    setIsOpen(true);
    markLastActive();
  }, [markLastActive]);

  const closeTour = useCallback(() => {
    setIsOpen(false);
    markLastActive();
  }, [markLastActive]);

  const goToDay = useCallback((day: number) => {
    setState((prev) => ({ ...prev, currentDay: Math.min(Math.max(day, 1), 7) }));
  }, []);

  const completeStep = useCallback((stepId: string) => {
    setState((prev) => {
      if (prev.completedSteps.includes(stepId)) return prev;
      const updatedSteps = [...prev.completedSteps, stepId];
      const step = DEFAULT_STEPS.find((s) => s.id === stepId);
      let updatedDays = prev.completedDays;

      if (step) {
        const allForDay = DEFAULT_STEPS.filter((s) => s.day === step.day).map((s) => s.id);
        const allCompleted = allForDay.every((id) => updatedSteps.includes(id));
        if (allCompleted && !prev.completedDays.includes(step.day)) {
          updatedDays = [...prev.completedDays, step.day];
        }
      }

      return {
        ...prev,
        completedSteps: updatedSteps,
        completedDays: updatedDays,
        lastActive: new Date().toISOString(),
      };
    });
  }, []);

  const completeDay = useCallback((day: number) => {
    setState((prev) => ({
      ...prev,
      completedDays: prev.completedDays.includes(day) ? prev.completedDays : [...prev.completedDays, day],
      currentDay: Math.min(day + 1, 7),
      lastActive: new Date().toISOString(),
    }));
  }, []);

  const skipDay = useCallback((day: number) => {
    setState((prev) => ({
      ...prev,
      completedDays: prev.completedDays.includes(day) ? prev.completedDays : [...prev.completedDays, day],
      currentDay: Math.min(day + 1, 7),
      lastActive: new Date().toISOString(),
    }));
  }, []);

  const reset = useCallback(() => {
    if (typeof window !== 'undefined') {
      window.localStorage.removeItem(STORAGE_KEY);
    }
    setState(DEFAULT_STATE);
    setIsOpen(true);
  }, []);

  const shouldPrompt = useMemo(() => {
    return state.completedDays.length < 7;
  }, [state.completedDays.length]);

  useEffect(() => {
    if (!shouldPrompt || state.autoOpened) return;
    setIsOpen(true);
    setState((prev) => ({ ...prev, autoOpened: true }));
  }, [shouldPrompt, state.autoOpened]);

  const currentSteps = stepsByDay[state.currentDay] || [];

  const progress = useMemo(() => {
    return {
      completedSteps: state.completedSteps.length,
      totalSteps: DEFAULT_STEPS.length,
      completedDays: state.completedDays.length,
    };
  }, [state.completedDays.length, state.completedSteps.length]);

  return {
    isOpen,
    openTour,
    closeTour,
    state,
    steps: DEFAULT_STEPS,
    currentSteps,
    stepsByDay,
    completeStep,
    completeDay,
    skipDay,
    reset,
    goToDay,
    shouldPrompt,
    progress,
    emitTutorialEvent,
  };
}

