  /**
   * Update persona traits
   */
  async updateTraits(traits: Partial<AldenTraits>): Promise<BridgeResponse<AldenPersonaMemory>> {
    try {
      await this.invokeCommand('alden_update_trait', { traits });

      if (this.personaMemory) {
        this.personaMemory.traits = { ...this.personaMemory.traits, ...traits };
      }

      this.notifySubscribers({ type: 'traits_updated', data: traits });

      return { success: true, data: this.personaMemory! };
    } catch (error: any) {
      return {
        success: false,
        error: error.message || 'Failed to update traits',
        code: 'TRAIT_UPDATE_ERROR',
      };
    }
  }

  /**
   * Record a correction event (positive or negative feedback)
   */
  async recordCorrection(correction: CorrectionEvent): Promise<BridgeResponse<void>> {
    try {
      // Note: This command may need to be implemented in backend
      await this.invokeCommand('alden_record_correction', { correction });

      if (this.personaMemory) {
        this.personaMemory.correction_events.push(correction);
      }

      this.notifySubscribers({ type: 'correction_recorded', data: correction });

      return { success: true, data: undefined };
    } catch (error: any) {
      console.warn('[AldenBridge] Correction recording not yet implemented in backend');
      // Graceful degradation - store locally
      if (this.personaMemory) {
        this.personaMemory.correction_events.push(correction);
      }
      return { success: true, data: undefined };
    }
  }

  /**
   * Export persona memory
   */
  async exportPersonaMemory(): Promise<BridgeResponse<AldenPersonaMemory>> {
    try {
      const exported = await this.invokeCommand<string>('alden_export_memory', {});

      this.notifySubscribers({ type: 'persona_exported', data: exported });

      return { success: true, data: this.personaMemory! };
    } catch (error: any) {
      return {
        success: false,
        error: error.message || 'Failed to export persona memory',
        code: 'EXPORT_ERROR',
      };
    }
  }

