  async sendChatMessage(message: string): Promise<BridgeResponse<ChatMessage>> {
    const trimmed = message.trim();
    if (!trimmed) {
      return {
        success: false,
        error: 'Message cannot be empty',
        code: 'CHAT_MESSAGE_EMPTY',
      };
    }

    try {
      const request = {
        user_id: this.userId,
        message: trimmed,
      };

      const response = await this.invokeCommand<ChatMessage>('alden_chat_send', { request });
      this.notifySubscribers({ type: 'chat_message_sent', data: response });
      return { success: true, data: response };
    } catch (error: any) {
      return {
        success: false,
        error: error.message || 'Failed to send chat message',
        code: 'CHAT_SEND_ERROR',
      };
    }
  }

