  constructor(config?: Partial<BridgeConfig>) {
    super({
      agentId: 'alden',
      ...config,
    });
  }

  /**
   * Get userId from auth context (if available)
   * Falls back to 'default_user' for backwards compatibility
   */
  private static getUserIdStatic(): string {
    return (window as unknown as { __hearthlink_userId?: string }).__hearthlink_userId || 'default_user';
  }

  /**
   * Helper to wrap requests with authentication token (static version)
   */
  private static createAuthenticatedRequest<T>(payload: T): { auth_token: string; payload: T } {
    return {
      auth_token: `demo_token_${this.getUserIdStatic()}`,
      payload,
    };
  }

  /**
   * Override getMockResponse to provide Alden-specific mock data for browser mode
   */
  protected getMockResponse<T>(command: AgentCommand, params?: any): T {
    const mock = getAldenMockResponse<T>(command, params, this.userId);
    if (mock !== null) return mock;
    return super.getMockResponse<T>(command, params);
  }

  /**
   * Initialize Alden bridge - sets up persona memory only (no IPC calls)
   */
  protected async onInitialize(): Promise<void> {
    this.personaMemory = {
      persona_id: 'alden',
      user_id: this.userId || 'default_user',
      schema_version: '1.0.0',
      timestamp: new Date().toISOString(),
      traits: {
        openness: 75,
