import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import { UnifiedCommandCenterOptimized as UnifiedCommandCenter } from '../shared/premium/UnifiedCommandCenter.optimized';
import { CoreBridge } from '../../bridges/CoreBridge';
import { useChatStateWithOrchestration } from '../shared/chat/useChatStateWithOrchestration';
import { MODULE_RESIDENT_AI, type SystemPersonaId } from '../shared/chat/types';
import { PrivateChannel } from '../shared/chat/PrivateChannel';
import type { Conference, CoreSession, ConferenceType } from '../../types/agents';
import { getComponentsForSlot, type ComponentRegistryEntry } from './ComponentRegistry';

interface CoreDashboardProps {
  userId: string;
  onBackToLauncher?: () => void;
  onNavigateToModule?: (moduleName: string) => void;
  onFocusAssist?: () => void;
}

interface CoreEvent {
  event_id: string;
  event_type: 'message' | 'handoff' | 'tool_call';
  timestamp: string;
  from_agent?: string;
  to_agent?: string;
  agent_id?: string;
  details?: Record<string, unknown>;
  priority?: 'low' | 'medium' | 'high' | 'urgent';
}

// MythTech Design Tokens
const MYTHTECH_COLORS = {
  navy: '#0b1221',
  cyan: '#00e5ff',
  gold: '#d6b369',
  slate: {
    50: '#f8fafc',
    100: '#f1f5f9',
    200: '#e2e8f0',
    300: '#cbd5e1',
    400: '#94a3b8',
    500: '#64748b',
    600: '#475569',
    700: '#334155',
    800: '#1e293b',
    900: '#0f172a',
  }
};

export const CoreDashboard: React.FC<CoreDashboardProps> = ({
  userId,
  onBackToLauncher,
  onNavigateToModule,
  onFocusAssist,
}) => {
  const navigate = useNavigate();
  // --- 1. Infrastructure ---
  const [activeTab, setActiveTab] = useState('overview');
  const [conferences, setConferences] = useState<Conference[]>([]);
  const [currentSession, setCurrentSession] = useState<CoreSession | null>(null);
  const [events, setEvents] = useState<CoreEvent[]>([]);
  const [loading, setLoading] = useState(true);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [conferenceForm, setConferenceForm] = useState({
    title: '',
    purpose: '',
    conferenceType: 'brainstorm' as ConferenceType
  });
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);

  // --- 2. Registry Integration (Modularity) ---
  const primaryComponents = useMemo(() => getComponentsForSlot('primary'), []);
  
  const activeComponentDef = useMemo(() => 
    primaryComponents.find(c => c.id === activeTab), 
    [activeTab, primaryComponents]
  );
  
  const ActiveComponent = activeComponentDef?.component;

  // --- 3. Chat Integration ---
  const moduleId = 'core';
  const residentPersonaId = (MODULE_RESIDENT_AI[moduleId] || 'alden') as SystemPersonaId;

  const {
    personas: chatPersonas,
    privateMessages,
    privateLoading,
    privateError,
    privateTyping,
    sendPrivateMessage,
  } = useChatStateWithOrchestration({
    userId,
    moduleId,
    residentPersonaId,
    onError: (err) => console.error('Core Chat Error:', err),
  });

  const residentPersona = chatPersonas.find(p => p.id === residentPersonaId);
  const bridge = useMemo(() => new CoreBridge({ userId }), [userId]);

  // --- 4. Data Loading ---
  const loadDashboardData = useCallback(async () => {
    try {
      setLoading(true);
      await bridge.initialize();
      const props = await bridge.getProps();
      setCurrentSession(props.currentSession);

      const transformedEvents: CoreEvent[] = props.messages.map(msg => ({
        event_id: msg.message_id,
        event_type: 'message',
        timestamp: msg.timestamp,
        from_agent: msg.from_agent,
        to_agent: msg.to_agent,
        details: { content: msg.content },
        priority: msg.priority === 'normal' ? 'medium' : msg.priority,
      }));
      setEvents(transformedEvents);
      setConferences(props.conferences);
    } catch (err) {
      console.error('[Core] Failed to load dashboard:', err);
    } finally {
      setLoading(false);
    }
  }, [bridge]);

  useEffect(() => {
    loadDashboardData();
  }, [loadDashboardData]);

  // --- 5. Actions ---
  const handleCreateConference = async () => {
    if (!conferenceForm.title.trim() || !conferenceForm.purpose.trim()) return;

    try {
      const result = await bridge.createConference({
        userId,
        title: conferenceForm.title,
        purpose: conferenceForm.purpose,
        conferenceType: conferenceForm.conferenceType,
        participants: [],
      });

      if (result.success && result.data) {
        setConferences([...conferences, result.data]);
        setShowCreateModal(false);
        setConferenceForm({ title: '', purpose: '', conferenceType: 'brainstorm' });
      }
    } catch (err) {
      console.error('[Core] Failed to create conference:', err);
    }
  };

  const handleBackToLauncher = useCallback(() => {
    if (onBackToLauncher) {
      onBackToLauncher();
      return;
    }
    if (onNavigateToModule) {
      onNavigateToModule('launch');
      return;
    }
    navigate('/');
  }, [onBackToLauncher, onNavigateToModule, navigate]);

  // --- 6. Render Helpers ---
  const chatSidebar = useMemo(() => {
    if (activeTab === 'chat' || !residentPersona) return null;
    return (
      <PrivateChannel
        residentPersona={residentPersona}
        messages={privateMessages}
        isLoading={privateLoading}
        error={privateError}
        isTyping={privateTyping}
        onSendMessage={sendPrivateMessage}
        isCollapsed={sidebarCollapsed}
        onToggleCollapse={() => setSidebarCollapsed(prev => !prev)}
        onStandardView={() => setActiveTab('chat')}
        className="h-full border-l"
        onFocusAssist={onFocusAssist}
      />
    );
  }, [activeTab, residentPersona, sidebarCollapsed, privateMessages, privateLoading, privateError, privateTyping, sendPrivateMessage, onFocusAssist]);

  // Unified Props Bucket for Dynamic Components
  const commonProps = {
    userId,
    conferences,
    events,
    embedded: true,
    showCreateModal,
    conferenceForm,
    onCreateConference: handleCreateConference,
    onSetShowCreateModal: setShowCreateModal,
    onSetConferenceForm: setConferenceForm,
    moduleId: 'core',
    residentPersonaId,
    onClose: () => setActiveTab('overview'),
    className: "h-full w-full"
  };

  return (
    <UnifiedCommandCenter
      title="Core"
      subtitle="System Orchestrator"
      iconPath="/assets/Core.png"
      tabs={primaryComponents}
      activeTab={activeTab}
