/**
 * Sentry Dashboard Component - Security & Compliance
 *
 * Provides interface for security monitoring, compliance rules, and threat detection.
 * Follows the AldenCommandCenter pattern for consistency.
 *
 * @version 2.0.0
 * @date 2025-11-08
 */

import React, { useState, useEffect } from 'react';
import { SentryBridge } from '../../bridges/SentryBridge';
import type { SentrySecurityEvent, SentryComplianceRule, SentryAuditReport } from '../../types/agents';
import { BackToLauncher } from '../shared/BackToLauncher';

const toErrorMessage = (err: unknown): string => (
  err instanceof Error ? err.message : String(err)
);

export interface SentryDashboardProps {
  userId: string;
  onBackToLauncher?: () => void;
}

export const SentryDashboard: React.FC<SentryDashboardProps> = ({ userId, onBackToLauncher }) => {
  const [events, setEvents] = useState<SentrySecurityEvent[]>([]);
  const [rules, setRules] = useState<SentryComplianceRule[]>([]);
  const [reports, setReports] = useState<SentryAuditReport[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [actionSuccess, setActionSuccess] = useState<string | null>(null);

  useEffect(() => {
    loadDashboardData();
  }, [userId]);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      setError(null);

      const bridge = new SentryBridge();
      await bridge.initialize();

      const props = await bridge.getProps();
      setEvents(props.events);
      setRules(props.rules);
      setReports(props.reports);
    } catch (err: unknown) {
      console.error('[Sentry] Failed to load dashboard:', err);
      setError(toErrorMessage(err) || 'Failed to load security data');
    } finally {
      setLoading(false);
    }
  };

  const handleResolveEvent = async (eventId: string) => {
    try {
      const bridge = new SentryBridge();
      await bridge.initialize();

      const response = await bridge.resolveEvent(eventId, 'Resolved by user');
      if (response.success) {
        setEvents(prev => prev.map(e =>
          e.event_id === eventId ? { ...e, resolved: true, resolution: 'Resolved by user' } : e
        ));
        setActionSuccess('Security event resolved');
        setTimeout(() => setActionSuccess(null), 3000);
      } else {
        setError(response.error || 'Failed to resolve event');
      }
    } catch (err: unknown) {
      setError(toErrorMessage(err) || 'Failed to resolve event');
    }
  };

  const handleToggleRule = async (ruleId: string, enabled: boolean) => {
    try {
      const bridge = new SentryBridge();
      await bridge.initialize();

      const response = await bridge.toggleRule(ruleId, enabled);
      if (response.success) {
        setRules(prev => prev.map(r =>
          r.rule_id === ruleId ? { ...r, enabled } : r
        ));
        setActionSuccess(`Rule ${enabled ? 'enabled' : 'disabled'}`);
        setTimeout(() => setActionSuccess(null), 3000);
      } else {
        setError(response.error || 'Failed to toggle rule');
      }
    } catch (err: unknown) {
      setError(toErrorMessage(err) || 'Failed to toggle rule');
    }
  };

  const renderHeader = () => (
    <div className="flex items-center justify-between p-6 border-b border-border">
      <div className="flex items-center gap-4">
        {/* Back to Launcher Button */}
        {onBackToLauncher && (
          <BackToLauncher onClick={onBackToLauncher} variant="minimal" />
        )}

        <div className="flex items-center gap-3">
          <img
            src="/assets/Sentry.png"
            alt="Sentry"
            className="w-12 h-12 object-contain"
            style={{
              filter: 'drop-shadow(0 0 15px color-mix(in oklch, var(--sentry-accent) 40%, transparent))'
            }}
          />
          <div>
            <h1 className="text-2xl font-semibold">Sentry Security Center</h1>
            <p className="text-xs text-muted-foreground">Compliance & Threat Detection</p>
          </div>
        </div>
      </div>
      <div className="flex items-center gap-2">
        <span className="w-2 h-2 rounded-full animate-pulse" style={{ backgroundColor: 'var(--sentry-accent)' }}></span>
        <span className="text-sm" style={{ color: 'var(--sentry-accent)' }}>Monitoring</span>
      </div>
    </div>
  );

  const renderMetrics = () => {
    const unresolvedEvents = events.filter(e => !e.resolved).length;
    const criticalEvents = events.filter(e => e.severity === 'critical' && !e.resolved).length;
    const enabledRules = rules.filter(r => r.enabled).length;

    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
        <div className="stat-card">
          <span className="stat-value">{unresolvedEvents}</span>
          <span className="stat-label">Unresolved Events</span>
          <div className="mt-2 flex items-center gap-2" style={{ color: unresolvedEvents > 0 ? 'var(--warning)' : 'var(--success)' }}>
            <span className="w-1.5 h-1.5 rounded-full" style={{ backgroundColor: unresolvedEvents > 0 ? 'var(--warning)' : 'var(--success)' }}></span>
            <span className="text-xs">{unresolvedEvents > 0 ? 'Needs attention' : 'All clear'}</span>
          </div>
        </div>

        <div className="stat-card">
          <span className="stat-value">{criticalEvents}</span>
          <span className="stat-label">Critical Alerts</span>
          <div className="mt-2 flex items-center gap-2" style={{ color: criticalEvents > 0 ? 'var(--error)' : 'var(--success)' }}>
            <span className="w-1.5 h-1.5 rounded-full" style={{ backgroundColor: criticalEvents > 0 ? 'var(--error)' : 'var(--success)' }}></span>
            <span className="text-xs">{criticalEvents > 0 ? 'Urgent' : 'Secure'}</span>
          </div>
        </div>

        <div className="stat-card">
          <span className="stat-value">{enabledRules}/{rules.length}</span>
          <span className="stat-label">Active Rules</span>
          <div className="mt-2 text-xs text-muted-foreground">Compliance enforcement</div>
        </div>

        <div className="stat-card">
          <span className="stat-value">{reports.length}</span>
          <span className="stat-label">Audit Reports</span>
          <div className="mt-2 text-xs text-muted-foreground">Generated</div>
        </div>
      </div>
    );
  };

  const renderSecurityEvents = () => (
    <div className="module-card mb-6">
      <div className="p-4 border-b border-border">
        <h2 className="text-xl font-semibold">Security Events</h2>
      </div>
      <div className="p-4">
        {loading ? (
          <div className="text-center py-8 text-muted-foreground">
            <div className="inline-block w-6 h-6 border-2 rounded-full animate-spin border-t-transparent"
              style={{ borderColor: 'var(--sentry-accent)', borderTopColor: 'transparent' }}></div>
            <p className="mt-2">Loading security events...</p>
          </div>
        ) : events.filter(e => !e.resolved).length === 0 ? (
          <div className="text-center py-12 text-muted-foreground">
            <p className="text-lg mb-2">No unresolved security events</p>
            <p className="text-sm">System is secure</p>
          </div>
        ) : (
          <div className="space-y-3">
            {events.filter(e => !e.resolved).slice(0, 10).map(event => (
              <div
                key={event.event_id}
                className="module-card p-4 hover:shadow-md transition-all duration-300"
                style={{
                  borderLeft: `4px solid ${
                    event.severity === 'critical' || event.severity === 'high'
                      ? 'var(--error)'
                      : event.severity === 'medium'
                      ? 'var(--warning)'
                      : 'var(--sentry-accent)'
                  }`
                }}
              >
                <div className="flex justify-between items-start">
                  <div className="flex-1">
                    <div className="flex items-center gap-3 mb-2">
                      <h3 className="font-medium text-foreground capitalize">{event.type.replace('_', ' ')}</h3>
                      <span className={
                        event.severity === 'critical' ? 'priority-critical' :
                        event.severity === 'high' ? 'priority-high' :
                        event.severity === 'medium' ? 'priority-medium' :
                        'priority-low'
                      }>
                        {event.severity}
                      </span>
                    </div>
                    <p className="text-sm text-muted-foreground mb-2">{event.description}</p>
                    {event.agent_id && (
                      <p className="text-xs text-muted-foreground">
                        Agent: <span className="capitalize">{event.agent_id}</span>
                      </p>
                    )}
                    <div className="text-xs text-muted-foreground mt-2">
                      {new Date(event.timestamp).toLocaleString()}
                    </div>
                  </div>
                  <button
                    onClick={() => handleResolveEvent(event.event_id)}
                    className="px-3 py-1 rounded-md text-sm font-medium transition-all"
                    style={{
                      backgroundColor: 'var(--success)',
                      color: 'var(--success-foreground)',
                    }}
                  >
                    Resolve
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );

