        setSystemAlerts(alerts);

        // Get context diagnostics
        try {
          const contextDiag = (await SentryClient.getContextDiagnostics()) as { success: boolean; data: ContextDiagnostics };
          if (contextDiag.success && contextDiag.data) {
            setContextDiagnostics(contextDiag.data);
