use serde::{Deserialize, Serialize};
use std::sync::Arc;
use std::time::Instant;
use tauri::State;
use tokio::sync::RwLock;

use crate::auth::SecureAuthenticator;
use crate::db_manager::DatabaseManager;
use crate::key_manager::SecureKeyManager;

#[derive(Debug, Clone, Serialize, Deserialize)]
#[serde(tag = "status", rename_all = "snake_case")]
pub enum ProbeResult {
    Healthy { latency_ms: u64 },
    Degraded { reason: String, latency_ms: u64 },
    Critical { error: String },
    Unreachable,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct HealthProbe {
    pub probe_id: String,
    pub target_module: String,
    pub check_type: String, // "connectivity", "latency", "auth"
    pub threshold_ms: u64,
    pub interval_seconds: u64,
}

pub struct HealthProbeManager {
    probes: Arc<RwLock<Vec<HealthProbe>>>,
}

impl HealthProbeManager {
    pub fn new() -> Self {
        Self {
            probes: Arc::new(RwLock::new(Self::default_probes())),
        }
    }

    fn default_probes() -> Vec<HealthProbe> {
        vec![
            HealthProbe {
                probe_id: "database_connectivity".to_string(),
                target_module: "database".to_string(),
                check_type: "connectivity".to_string(),
                threshold_ms: 250,
                interval_seconds: 60,
            },
            HealthProbe {
                probe_id: "auth_service".to_string(),
                target_module: "auth".to_string(),
                check_type: "auth".to_string(),
                threshold_ms: 150,
                interval_seconds: 60,
            },
            HealthProbe {
                probe_id: "vault_encryption".to_string(),
                target_module: "vault".to_string(),
                check_type: "latency".to_string(),
                threshold_ms: 300,
                interval_seconds: 60,
            },
        ]
    }

    pub async fn register_probe(&self, probe: HealthProbe) -> Result<(), String> {
        if probe.probe_id.trim().is_empty() {
            return Err("probe_id cannot be empty".to_string());
        }

        let mut probes = self.probes.write().await;
        if let Some(existing) = probes
            .iter_mut()
            .find(|existing| existing.probe_id == probe.probe_id)
        {
            *existing = probe;
        } else {
            probes.push(probe);
        }

        Ok(())
    }

    pub async fn run_probe(
        &self,
        probe_id: &str,
        db_state: &Arc<RwLock<Option<DatabaseManager>>>,
        auth: &SecureAuthenticator,
        key_manager: &SecureKeyManager,
    ) -> Result<ProbeResult, String> {
        let probe = {
            let probes = self.probes.read().await;
            probes
                .iter()
                .find(|probe| probe.probe_id == probe_id)
                .cloned()
        };

        let probe = probe.ok_or_else(|| format!("Probe '{probe_id}' is not registered"))?;
        Ok(self
            .run_probe_for(&probe, db_state, auth, key_manager)
            .await)
    }

    pub async fn run_all_probes(
        &self,
        db_state: &Arc<RwLock<Option<DatabaseManager>>>,
        auth: &SecureAuthenticator,
        key_manager: &SecureKeyManager,
    ) -> Result<Vec<(String, ProbeResult)>, String> {
        let probes = { self.probes.read().await.clone() };
        let mut results = Vec::with_capacity(probes.len());

        for probe in probes {
            let result = self
                .run_probe_for(&probe, db_state, auth, key_manager)
                .await;
            results.push((probe.probe_id, result));
        }

        Ok(results)
    }

    async fn run_probe_for(
        &self,
        probe: &HealthProbe,
        db_state: &Arc<RwLock<Option<DatabaseManager>>>,
        auth: &SecureAuthenticator,
        key_manager: &SecureKeyManager,
    ) -> ProbeResult {
        match probe.probe_id.as_str() {
            "database_connectivity" => {
                let start = Instant::now();
                let db_guard = db_state.read().await;
                let db = match db_guard.as_ref() {
                    Some(db) => db,
                    None => return ProbeResult::Unreachable,
                };

                match sqlx::query("SELECT 1").execute(&db.read_pool).await {
                    Ok(_) => {
                        let latency_ms = start.elapsed().as_millis() as u64;
                        if latency_ms > probe.threshold_ms {
                            ProbeResult::Degraded {
                                reason: format!(
                                    "Database latency {}ms exceeded {}ms",
                                    latency_ms, probe.threshold_ms
                                ),
                                latency_ms,
                            }
                        } else {
                            ProbeResult::Healthy { latency_ms }
                        }
                    }
                    Err(e) => ProbeResult::Critical {
                        error: format!("Database probe failed: {e}"),
                    },
                }
            }
            "auth_service" => {
                let start = Instant::now();
                auth.cleanup_expired_tokens().await;
                let latency_ms = start.elapsed().as_millis() as u64;

                if latency_ms > probe.threshold_ms {
                    ProbeResult::Degraded {
                        reason: format!(
                            "Auth service latency {}ms exceeded {}ms",
                            latency_ms, probe.threshold_ms
                        ),
                        latency_ms,
                    }
                } else {
                    ProbeResult::Healthy { latency_ms }
                }
            }
            "vault_encryption" => {
                let start = Instant::now();
                match key_manager.get_vault_key().await {
                    Ok(_) => {
                        let latency_ms = start.elapsed().as_millis() as u64;
                        if latency_ms > probe.threshold_ms {
                            ProbeResult::Degraded {
                                reason: format!(
                                    "Vault key latency {}ms exceeded {}ms",
                                    latency_ms, probe.threshold_ms
                                ),
                                latency_ms,
                            }
                        } else {
                            ProbeResult::Healthy { latency_ms }
                        }
                    }
                    Err(e) => ProbeResult::Critical {
                        error: format!("Vault encryption probe failed: {e}"),
                    },
                }
            }
            _ => ProbeResult::Unreachable,
        }
    }
}

#[tauri::command]
pub async fn sentry_register_health_probe(
    probe: HealthProbe,
    manager: State<'_, HealthProbeManager>,
) -> Result<(), String> {
    manager.register_probe(probe).await
}

#[tauri::command]
pub async fn sentry_run_health_probe(
    probe_id: String,
    manager: State<'_, HealthProbeManager>,
    db_state: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
    auth: State<'_, Arc<SecureAuthenticator>>,
    key_manager: State<'_, Arc<SecureKeyManager>>,
) -> Result<ProbeResult, String> {
    manager
        .run_probe(
            &probe_id,
            db_state.inner(),
            auth.inner(),
            key_manager.inner(),
        )
        .await
}

#[tauri::command]
pub async fn sentry_get_all_probe_results(
    manager: State<'_, HealthProbeManager>,
    db_state: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
    auth: State<'_, Arc<SecureAuthenticator>>,
    key_manager: State<'_, Arc<SecureKeyManager>>,
) -> Result<Vec<(String, ProbeResult)>, String> {
    manager
        .run_all_probes(db_state.inner(), auth.inner(), key_manager.inner())
        .await
}

