  const containerRef = useRef<HTMLDivElement>(null);
  const sentryBridge = useRef<SentryBridge | null>(null);
  const actionTimerRef = useRef<ReturnType<typeof setTimeout> | null>(null);

  // Chat integration
  const moduleId = 'sentry';
  const residentPersonaId = (MODULE_RESIDENT_AI[moduleId] || 'sentry') as SystemPersonaId;
  const {
    personas: chatPersonas,
    privateMessages,
    privateLoading,
    privateError,
    privateTyping,
    sendPrivateMessage,
  } = useChatStateWithOrchestration({
    userId,
    moduleId,
    residentPersonaId,
    onError: (err) => console.error('Sentry Chat Error:', err),
  });

  const residentPersona = chatPersonas.find((p) => p.id === residentPersonaId);

  useEffect(() => {
    const bridge = new SentryBridge();
    sentryBridge.current = bridge;
    
    const init = async () => {
      await bridge.initialize();
      loadData();
    };
    init();
  }, []);

  useEffect(() => {
    if (activeView !== 'overview' && activeView !== 'rules') {
      setDataEditMode(false);
    }
  }, [activeView]);

  useEffect(() => () => {
    if (actionTimerRef.current) {
      clearTimeout(actionTimerRef.current);
    }
  }, []);

  const loadData = async () => {
    if (!sentryBridge.current) return;
    try {
      const props = await sentryBridge.current.getProps();
      setEvents(props.events || []);
      setRules(props.rules || []);
      setReports(props.reports || []);
    } catch (err) {
      console.error('Failed to load Sentry data', err);
    }
  };

  const pushActionMessage = (message: string, isError = false) => {
    if (actionTimerRef.current) {
      clearTimeout(actionTimerRef.current);
    }
    if (isError) {
      setActionError(message);
      setActionMessage(null);
    } else {
      setActionMessage(message);
      setActionError(null);
    }
    actionTimerRef.current = setTimeout(() => {
      setActionMessage(null);
      setActionError(null);
    }, 3500);
  };

  const parseJsonObject = (value: string, label: string): { ok: true; value: JsonObject } | { ok: false; error: string } => {
    const trimmed = value.trim();
    if (!trimmed) {
      return { ok: true, value: {} };
    }
    try {
      const parsed = JSON.parse(trimmed);
      if (!parsed || typeof parsed !== 'object' || Array.isArray(parsed)) {
        return { ok: false, error: `${label} must be a JSON object.` };
      }
      return { ok: true, value: parsed as JsonObject };
    } catch (error) {
      return { ok: false, error: `${label} must be valid JSON.` };
    }
  };

  const mapReportType = (schedule: SentryAuditReport['type']): 'security' | 'compliance' | 'activity' | 'system' => {
    switch (schedule) {
      case 'weekly':
      case 'monthly':
        return 'compliance';
      case 'on-demand':
        return 'activity';
      case 'daily':
      default:
        return 'security';
    }
  };

  const handleResolveEvent = async (eventId: string) => {
    if (!sentryBridge.current) return;
    const resolution = (eventResolutionDrafts[eventId] || '').trim();
    if (!resolution) {
      pushActionMessage('Resolution details are required.', true);
      return;
    }
    try {
      setIsSubmitting(true);
      const response = await sentryBridge.current.resolveEvent(eventId, resolution);
      if (response.success && response.data) {
        setEvents(prev => prev.map(event => (
          event.event_id === eventId ? { ...event, resolved: true, resolution } : event
        )));
        pushActionMessage('Security event resolved.');
      } else {
        pushActionMessage(response.error || 'Failed to resolve event.', true);
      }
    } catch (error) {
      pushActionMessage('Failed to resolve event.', true);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleCreateEvent = async () => {
    if (!sentryBridge.current) return;
    const description = newEventForm.description.trim();
    if (!description) {
      pushActionMessage('Event description is required.', true);
      return;
    }
    const detailsResult = parseJsonObject(newEventForm.detailsJson, 'Details');
    if (!detailsResult.ok) {
      pushActionMessage(detailsResult.error, true);
      return;
    }
    try {
      setIsSubmitting(true);
      const response = await sentryBridge.current.createEvent({
        userId,
        agentId: newEventForm.agentId.trim() || undefined,
        eventType: newEventForm.eventType,
        severity: newEventForm.severity,
        description,
        details: detailsResult.value,
      });
      if (response.success && response.data) {
        setEvents(prev => [response.data, ...prev]);
        setNewEventForm({
          eventType: EVENT_TYPES[0],
          severity: EVENT_SEVERITIES[1],
          description: '',
          agentId: '',
          detailsJson: '{}',
        });
        pushActionMessage('Security event created.');
      } else {
        pushActionMessage(response.error || 'Failed to create event.', true);
      }
    } catch (error) {
      pushActionMessage('Failed to create event.', true);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleToggleRule = async (ruleId: string, enabled: boolean) => {
    if (!sentryBridge.current) return;
    try {
      setIsSubmitting(true);
      const response = await sentryBridge.current.toggleRule(ruleId, enabled);
      if (response.success && response.data) {
        setRules(prev => prev.map(rule => (
          rule.rule_id === ruleId ? { ...rule, enabled } : rule
        )));
        pushActionMessage(`Rule ${enabled ? 'enabled' : 'disabled'}.`);
      } else {
        pushActionMessage(response.error || 'Failed to toggle rule.', true);
      }
    } catch (error) {
      pushActionMessage('Failed to toggle rule.', true);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleCreateRule = async () => {
    if (!sentryBridge.current) return;
    const name = newRuleForm.name.trim();
    const description = newRuleForm.description.trim();
    if (!name || !description) {
      pushActionMessage('Rule name and description are required.', true);
      return;
    }
    const conditionsResult = parseJsonObject(newRuleForm.conditionsJson, 'Conditions');
    if (!conditionsResult.ok) {
      pushActionMessage(conditionsResult.error, true);
      return;
    }
    const actionsResult = parseJsonObject(newRuleForm.actionsJson, 'Actions');
    if (!actionsResult.ok) {
      pushActionMessage(actionsResult.error, true);
      return;
    }
    try {
      setIsSubmitting(true);
      const response = await sentryBridge.current.createRule({
        name,
        description,
        severity: newRuleForm.severity,
        conditions: conditionsResult.value,
        actions: actionsResult.value,
      });
      if (response.success && response.data) {
        setRules(prev => [response.data, ...prev]);
        setNewRuleForm({
          name: '',
          description: '',
          severity: RULE_SEVERITIES[1],
          conditionsJson: '{}',
          actionsJson: '{}',
        });
        pushActionMessage('Compliance rule created.');
      } else {
        pushActionMessage(response.error || 'Failed to create rule.', true);
      }
    } catch (error) {
      pushActionMessage('Failed to create rule.', true);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleGenerateReport = async () => {
    if (!sentryBridge.current) return;
    try {
      setIsSubmitting(true);
      const response = await sentryBridge.current.generateReport({
        userId,
        type: mapReportType(newReportForm.schedule),
        startDate: newReportForm.startDate || undefined,
        endDate: newReportForm.endDate || undefined,
        filters: {
          schedule: newReportForm.schedule,
          startDate: newReportForm.startDate || undefined,
          endDate: newReportForm.endDate || undefined,
        },
      });
      if (response.success && response.data) {
        setReports(prev => [response.data, ...prev]);
        pushActionMessage('Audit report generated.');
      } else {
        pushActionMessage(response.error || 'Failed to generate report.', true);
      }
    } catch (error) {
      pushActionMessage('Failed to generate report.', true);
    } finally {
      setIsSubmitting(false);
    }
  };

  const frameStyle = useMemo(() => ({
    border: '2px solid rgba(239, 68, 68, 0.75)', // Red border for Sentry
    boxShadow: '0 0 22px rgba(239, 68, 68, 0.18), inset 0 0 18px rgba(0,0,0,0.55)',
    background: 'linear-gradient(135deg, rgba(20,5,5,0.92), rgba(28,10,10,0.82))',
  }), []);

  const renderEditToggle = useCallback(() => (
    <button
      onClick={() => setDataEditMode(prev => !prev)}
      className={`px-3 py-1.5 rounded-md text-xs font-semibold transition-all flex items-center gap-2 ${
        dataEditMode
          ? 'bg-red-500/20 text-red-300 border border-red-500/60 shadow-[0_0_12px_rgba(239,68,68,0.3)]'
          : 'bg-black/70 text-red-400 border border-red-500/30 hover:bg-red-500/10'
      }`}
    >
      {dataEditMode ? <Unlock size={14} /> : <Lock size={14} />}
      {dataEditMode ? 'Exit Edit Mode' : 'Edit Mode'}
    </button>
  ), [dataEditMode]);

  const renderEventsView = useCallback(() => (
    <div className="h-full bg-alden-panel ring-1 ring-[rgba(255,255,255,0.1)] rounded-xl p-6 relative overflow-hidden flex flex-col w-full" style={frameStyle}>
      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center gap-3">
          <div className="p-2 rounded-lg bg-red-500/10 border border-red-500/20 text-red-400">
            <Activity size={20} />
          </div>
          <h2 className="text-xl font-semibold text-red-100 uppercase tracking-widest font-orbitron-force">Security Events</h2>
        </div>
      </div>

      <div className="flex-1 overflow-y-auto space-y-3 pr-2">
        {events.length === 0 && (
          <div className="text-center text-sm text-red-200/60 py-10 border border-dashed border-red-900/40 rounded-lg">
            No security events logged.
          </div>
        )}
        {events.map(event => {
          return (
            <div key={event.event_id} className="rounded-lg border border-red-900/40 bg-black/30 p-4 space-y-2">
              <div className="flex items-start justify-between gap-4">
                <div>
                  <div className="flex items-center gap-3">
                    <h3 className="text-sm font-semibold text-red-100 capitalize">{event.type.replace('_', ' ')}</h3>
                    <span className={`text-[10px] px-2 py-0.5 rounded uppercase tracking-wider ${
                      event.severity === 'critical' ? 'bg-red-500/20 text-red-300 border border-red-500/40' :
                      event.severity === 'high' ? 'bg-orange-500/20 text-orange-300 border border-orange-500/40' :
                      event.severity === 'medium' ? 'bg-yellow-500/20 text-yellow-200 border border-yellow-500/40' :
                      'bg-cyan-500/20 text-cyan-300 border border-cyan-500/40'
                    }`}>{event.severity}</span>
                    {event.resolved && (
                      <span className="text-[10px] px-2 py-0.5 rounded uppercase tracking-wider bg-green-500/20 text-green-300 border border-green-500/40">
                        Resolved
                      </span>
                    )}
                  </div>
                  <p className="text-xs text-red-200/80 mt-1">{event.description}</p>
                  <div className="text-[11px] text-red-200/50 mt-2">
                    {new Date(event.timestamp).toLocaleString()}
                  </div>
                </div>
              </div>
              {event.resolved && event.resolution && (
                <div className="text-xs text-green-200/80 border-t border-red-900/40 pt-2">
                  Resolution: {event.resolution}
                </div>
              )}
            </div>
          );
        })}
      </div>
    </div>
  ), [frameStyle, events]);

  const renderRulesView = () => (
    <div className="h-full bg-alden-panel ring-1 ring-[rgba(255,255,255,0.1)] rounded-xl p-6 relative overflow-hidden flex flex-col w-full" style={frameStyle}>
      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center gap-3">
          <div className="p-2 rounded-lg bg-red-500/10 border border-red-500/20 text-red-400">
            <CheckCircle2 size={20} />
          </div>
          <h2 className="text-xl font-semibold text-red-100 uppercase tracking-widest font-orbitron-force">Compliance Rules</h2>
        </div>
        {renderEditToggle()}
      </div>

      {dataEditMode && (
        <div className="mb-6 rounded-lg border border-red-500/20 bg-black/30 p-4 space-y-4">
          <div className="text-xs uppercase tracking-widest text-red-300 font-semibold">Create Rule</div>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
            <label className="text-xs text-red-200 space-y-1 md:col-span-2">
              <span className="block">Name</span>
              <input
                value={newRuleForm.name}
                onChange={(e) => setNewRuleForm(prev => ({ ...prev, name: e.target.value }))}
                className="w-full rounded-md bg-black/40 border border-red-500/30 text-red-100 text-sm px-3 py-2 focus:outline-none focus:ring-2 focus:ring-red-500/40"
                placeholder="Rule name"
              />
            </label>
            <label className="text-xs text-red-200 space-y-1 md:col-span-2">
              <span className="block">Description</span>
              <textarea
                value={newRuleForm.description}
                onChange={(e) => setNewRuleForm(prev => ({ ...prev, description: e.target.value }))}
                rows={2}
                className="w-full rounded-md bg-black/40 border border-red-500/30 text-red-100 text-sm px-3 py-2 focus:outline-none focus:ring-2 focus:ring-red-500/40"
                placeholder="Describe the compliance rule"
              />
            </label>
            <label className="text-xs text-red-200 space-y-1">
              <span className="block">Severity</span>
              <select
                value={newRuleForm.severity}
                onChange={(e) => setNewRuleForm(prev => ({ ...prev, severity: e.target.value as SentryComplianceRule['severity'] }))}
                className="w-full rounded-md bg-black/40 border border-red-500/30 text-red-100 text-sm px-3 py-2 focus:outline-none focus:ring-2 focus:ring-red-500/40"
              >
                {RULE_SEVERITIES.map(severity => (
                  <option key={severity} value={severity}>{severity}</option>
                ))}
              </select>
            </label>
            <label className="text-xs text-red-200 space-y-1 md:col-span-2">
              <span className="block">Conditions (JSON)</span>
              <textarea
                value={newRuleForm.conditionsJson}
                onChange={(e) => setNewRuleForm(prev => ({ ...prev, conditionsJson: e.target.value }))}
                rows={2}
                className="w-full rounded-md bg-black/40 border border-red-500/30 text-red-100 text-xs px-3 py-2 font-mono focus:outline-none focus:ring-2 focus:ring-red-500/40"
                placeholder='{"risk":"high"}'
              />
            </label>
            <label className="text-xs text-red-200 space-y-1 md:col-span-2">
              <span className="block">Actions (JSON)</span>
              <textarea
                value={newRuleForm.actionsJson}
                onChange={(e) => setNewRuleForm(prev => ({ ...prev, actionsJson: e.target.value }))}
                rows={2}
                className="w-full rounded-md bg-black/40 border border-red-500/30 text-red-100 text-xs px-3 py-2 font-mono focus:outline-none focus:ring-2 focus:ring-red-500/40"
                placeholder='{"alert":"sentry"}'
              />
            </label>
          </div>
          <button
            onClick={handleCreateRule}
            disabled={isSubmitting}
            className="px-4 py-2 rounded-md text-sm font-semibold bg-red-500/20 text-red-200 border border-red-500/50 hover:bg-red-500/30 disabled:opacity-60"
          >
            Create Rule
          </button>
        </div>
      )}

      <div className="flex-1 overflow-y-auto space-y-3 pr-2">
        {rules.length === 0 && (
          <div className="text-center text-sm text-red-200/60 py-10 border border-dashed border-red-900/40 rounded-lg">
            No compliance rules configured.
          </div>
        )}
        {rules.map(rule => (
          <div key={rule.rule_id} className="rounded-lg border border-red-900/40 bg-black/30 p-4 space-y-2">
            <div className="flex items-start justify-between gap-4">
              <div>
                <div className="flex items-center gap-3">
                  <h3 className="text-sm font-semibold text-red-100">{rule.name}</h3>
                  <span className={`text-[10px] px-2 py-0.5 rounded uppercase tracking-wider ${
                    rule.severity === 'high' ? 'bg-red-500/20 text-red-300 border border-red-500/40' : 'bg-yellow-500/20 text-yellow-200 border border-yellow-500/40'
                  }`}>{rule.severity}</span>
                  <span className={`text-[10px] px-2 py-0.5 rounded uppercase tracking-wider ${
                    rule.enabled ? 'bg-green-500/20 text-green-300 border border-green-500/40' : 'bg-gray-500/20 text-gray-300 border border-gray-500/40'
                  }`}>{rule.enabled ? 'Enabled' : 'Disabled'}</span>
                </div>
                <p className="text-xs text-red-200/80 mt-1">{rule.description}</p>
              </div>
              {dataEditMode && (
                <button
                  onClick={() => handleToggleRule(rule.rule_id, !rule.enabled)}
                  disabled={isSubmitting}
                  className={`px-3 py-1.5 rounded-md text-xs font-semibold border ${
                    rule.enabled
                      ? 'bg-red-500/20 text-red-200 border-red-500/40 hover:bg-red-500/30'
                      : 'bg-green-500/20 text-green-200 border-green-500/40 hover:bg-green-500/30'
                  } disabled:opacity-60`}
                >
                  {rule.enabled ? 'Disable' : 'Enable'}
                </button>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  const renderReportsView = () => {
    const unresolvedCount = events.filter(e => !e.resolved).length;
    const systemStatus = unresolvedCount === 0 ? 'SECURE' : 'ALERT';
    const statusTone = systemStatus === 'SECURE' ? 'text-green-400' : 'text-red-400';
    const statusDot = systemStatus === 'SECURE'
      ? 'bg-green-500 shadow-[0_0_10px_rgba(34,197,94,0.7)]'
      : 'bg-red-500 shadow-[0_0_10px_rgba(239,68,68,0.7)]';

    return (
      <div className="h-full bg-alden-panel ring-1 ring-[rgba(255,255,255,0.1)] rounded-xl p-6 relative overflow-hidden flex flex-col w-full" style={frameStyle}>
      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center gap-3">
          <div className="p-2 rounded-lg bg-red-500/10 border border-red-500/20 text-red-400">
            <FileText size={20} />
          </div>
          <h2 className="text-xl font-semibold text-red-100 uppercase tracking-widest font-orbitron-force">Audit Reports</h2>
        </div>
      </div>

      <div className="flex-1 overflow-y-auto space-y-3 pr-2">
        {reports.length === 0 && (
          <div className="text-center text-sm text-red-200/60 py-10 border border-dashed border-red-900/40 rounded-lg">
            No audit reports generated yet.
          </div>
        )}
        {reports.map(report => (
          <div key={report.report_id} className="rounded-lg border border-red-900/40 bg-black/30 p-4">
            <div className="flex items-center justify-between gap-4">
              <div>
                <h3 className="text-sm font-semibold text-red-100 capitalize">{report.type} report</h3>
                <p className="text-xs text-red-200/70 mt-1">Generated {new Date(report.generated_at).toLocaleString()}</p>
              </div>
              <div className="text-right">
                <div className="flex items-center justify-end gap-2 text-[10px] uppercase tracking-wider text-red-200/60">
                  <span className={`h-2 w-2 rounded-full ${statusDot}`} />
                  <span>System Status</span>
                </div>
                <div className={`text-2xl font-bold ${statusTone}`}>{systemStatus}</div>
              </div>
            </div>
            <div className="mt-3 grid grid-cols-2 gap-3 text-xs text-red-200/70">
              <div className="rounded border border-red-900/40 bg-black/40 p-2">
                <div className="uppercase text-[10px] tracking-wider text-red-200/60 mb-1">By Severity</div>
                <div className="space-y-1">
                  {Object.entries(report.summary.by_severity).map(([key, value]) => (
                    <div key={key} className="flex justify-between">
                      <span className="capitalize">{key}</span>
                      <span className="font-semibold">{value}</span>
                    </div>
                  ))}
                </div>
              </div>
              <div className="rounded border border-red-900/40 bg-black/40 p-2">
                <div className="uppercase text-[10px] tracking-wider text-red-200/60 mb-1">By Type</div>
                <div className="space-y-1">
                  {Object.entries(report.summary.by_type).map(([key, value]) => (
                    <div key={key} className="flex justify-between">
                      <span className="capitalize">{key.replace('_', ' ')}</span>
                      <span className="font-semibold">{value}</span>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
    );
  };

  const sidebar = useMemo(() => {
    if (activeView === 'chat' || !residentPersona) return null;

    return (
      <PrivateChannel
        residentPersona={residentPersona}
        messages={privateMessages}
        isLoading={privateLoading}
        error={privateError}
        isTyping={privateTyping}
        onSendMessage={sendPrivateMessage}
        isCollapsed={isSidebarCollapsed}
        onToggleCollapse={() => setIsSidebarCollapsed(prev => !prev)}
        onStandardView={() => setActiveView('chat')}
        onFocusAssist={onFocusAssist}
        className="h-full border-l border-red-400/20"
      />
    );
  }, [activeView, residentPersona, isSidebarCollapsed, privateMessages, privateLoading, privateError, privateTyping, sendPrivateMessage, onFocusAssist]);

  return (
    <>
      {actionMessage && (
        <div className="fixed top-4 right-4 z-[60] px-4 py-3 rounded-md text-sm font-semibold text-green-200 bg-green-500/20 border border-green-500/50 shadow-[0_0_18px_rgba(34,197,94,0.35)]">
          {actionMessage}
        </div>
      )}
      {actionError && (
        <div className="fixed top-4 right-4 z-[60] px-4 py-3 rounded-md text-sm font-semibold text-red-200 bg-red-500/20 border border-red-500/50 shadow-[0_0_18px_rgba(239,68,68,0.35)]">
          {actionError}
        </div>
      )}
      <UnifiedCommandCenter
      title="SENTRY"
      subtitle="Security Center"
      iconPath="/assets/Sentry.png"
      tabs={[
        { id: 'overview', label: 'Overview' },
        { id: 'events', label: 'Events' },
        { id: 'rules', label: 'Compliance' },
        { id: 'reports', label: 'Reports' },
        { id: 'chat', label: 'Chat' }
      ]}
      activeTab={activeView}
      onTabChange={(id) => setActiveView(id as ViewMode)}
      onBackToLauncher={onBackToLauncher}
      onNavigateToModule={onNavigateToModule}
      onNavigate={(module) => {
        if (onNavigateToModule) {
           onNavigateToModule(module);
           return true;
        }
      }}
      themeColor="#fecaca"
      accentColor="#ef4444"
      titleColor="#ef4444"
      subtitleColor="#991b1b"
      statusTone="error"
      statusLabel="System Defense"
      sidebar={sidebar}
      sidebarCollapsed={isSidebarCollapsed}
      headerStyle={{
        background: 'linear-gradient(90deg, #0f0505 0%, #1a0505 50%, #0f0505 100%)',
        borderBottom: '3px solid #ef4444',
        boxShadow: '0 8px 20px rgba(0,0,0,0.6), inset 0 -2px 0 rgba(239,68,68,0.3), inset 0 2px 0 rgba(153,27,27,0.4)'
      }}
      fullWidthContent={activeView === 'chat'}
    >
      {activeView === 'chat' && (
        <UnifiedChatContainer
          userId={userId}
          moduleId="sentry"
          residentPersonaId={residentPersonaId}
          onClose={() => setActiveView('overview')}
          className="h-full w-full"
        />
      )}
      {activeView === 'overview' && (
        <SentryOverview
          events={events}
          rules={rules}
          reports={reports}
          dataEditMode={dataEditMode}
          onToggleEditMode={() => setDataEditMode(prev => !prev)}
          ruleForm={newRuleForm}
          setRuleForm={setNewRuleForm}
          onCreateRule={handleCreateRule}
          onToggleRule={handleToggleRule}
          isSubmitting={isSubmitting}
          ruleSeverities={RULE_SEVERITIES}
        />
      )}
      {activeView === 'events' && renderEventsView()}
      {activeView === 'rules' && renderRulesView()}
      {activeView === 'reports' && renderReportsView()}
      </UnifiedCommandCenter>
    </>
  );
};

