import React, { useEffect, useState, useMemo } from "react";
import { invoke } from "@tauri-apps/api/tauri";
import {
  AlertCircle,
  AlertTriangle,
  AlertOctagon,
  Check,
  Filter,
  RefreshCw,
  XCircle,
  Clock,
  Activity,
} from "../ui/HearthlinkIcons";

interface Alert {
  id: string; // Mapped from alert_id
  severity: "info" | "warning" | "critical";
  module: string; // Mapped from component
  message: string;
  timestamp: string;
  acknowledged: boolean;
}

// Rust backend types
interface SystemAlert {
  alert_id: string;
  severity: string;
  component: string;
  message: string;
  timestamp: string;
  acknowledged: boolean;
}

interface AlertPanelProps {
  userId?: string;
}

type TimeRange = "1h" | "24h" | "7d" | "all";
type SeverityFilter = "all" | "info" | "warning" | "critical";

export const AlertPanel: React.FC<AlertPanelProps> = ({ userId }) => {
  const [alerts, setAlerts] = useState<Alert[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [lastUpdated, setLastUpdated] = useState<Date>(new Date());

  // Filters
  const [severityFilter, setSeverityFilter] = useState<SeverityFilter>("all");
  const [moduleFilter, setModuleFilter] = useState<string>("all");
  const [timeRange, setTimeRange] = useState<TimeRange>("24h");

  // Local state for dismissed alerts (to hide them immediately)
  const [dismissedIds, setDismissedIds] = useState<Set<string>>(new Set());

  // Derived available modules
  const availableModules = useMemo(() => {
    const modules = new Set(alerts.map((a) => a.module));
    return Array.from(modules).sort();
  }, [alerts]);

  const fetchAlerts = async () => {
    try {
      setLoading(true);
      const token = `demo_token_${userId || "default_user"}`;

      const response = await invoke<SystemAlert[]>("sentry_get_alerts", {
        request: {
          auth_token: token,
          payload: {},
        },
      });

      // Map Rust SystemAlert to Frontend Alert interface
      const mappedAlerts: Alert[] = response.map((raw) => ({
        id: raw.alert_id,
        severity: raw.severity.toLowerCase() as any,
        module: raw.component,
        message: raw.message,
        timestamp: raw.timestamp,
        acknowledged: raw.acknowledged,
      }));

      // Sort by timestamp desc
      mappedAlerts.sort(
        (a, b) =>
          new Date(b.timestamp).getTime() - new Date(a.timestamp).getTime(),
      );

      setAlerts(mappedAlerts);
      setLastUpdated(new Date());
      setError(null);
    } catch (err) {
      console.error("Failed to fetch alerts:", err);
      setError("Failed to load alerts.");
    } finally {
      setLoading(false);
    }
  };

  const acknowledgeAlert = async (id: string) => {
    try {
      const token = `demo_token_${userId || "default_user"}`;
      await invoke("sentry_acknowledge_alert", {
        request: {
          auth_token: token,
          payload: { alert_id: id },
        },
      });

      // Update local state
      setAlerts((prev) =>
        prev.map((a) => (a.id === id ? { ...a, acknowledged: true } : a)),
      );
    } catch (err) {
      console.error("Failed to acknowledge alert:", err);
    }
  };

  const dismissAlert = async (id: string) => {
    // Dismiss creates a local hide + acknowledge
    setDismissedIds((prev) => {
      const newSet = new Set(prev);
      newSet.add(id);
      return newSet;
    });
    // Also acknowledge on backend so it doesn't show up again if we filter by acknowledged
    await acknowledgeAlert(id);
  };

  // Initial fetch and polling
  useEffect(() => {
    fetchAlerts();
    const interval = setInterval(fetchAlerts, 30000);
    return () => clearInterval(interval);
  }, [userId]);

  // Filter Logic
  const filteredAlerts = useMemo(() => {
    const now = new Date();
    return alerts.filter((alert) => {
      if (dismissedIds.has(alert.id)) return false;

      // Filter out acknowledged (unless we want a toggle to show them, but "Dismiss" implies hiding)
      // Standard behavior: show unacknowledged.
      if (alert.acknowledged) return false;

      // Severity
      if (severityFilter !== "all" && alert.severity !== severityFilter)
        return false;

      // Module
      if (moduleFilter !== "all" && alert.module !== moduleFilter) return false;

      // Time Range
      const alertTime = new Date(alert.timestamp);
      const diffMs = now.getTime() - alertTime.getTime();
      const diffHours = diffMs / (1000 * 60 * 60);

      if (timeRange === "1h" && diffHours > 1) return false;
      if (timeRange === "24h" && diffHours > 24) return false;
      if (timeRange === "7d" && diffHours > 168) return false;

      return true;
    });
  }, [alerts, severityFilter, moduleFilter, timeRange, dismissedIds]);

  // Styling Helpers
  const getSeverityStyles = (severity: string) => {
    switch (severity) {
      case "critical":
        return {
          border: "1px solid #ef4444", // Red-500
          icon: <AlertOctagon className="w-5 h-5 text-red-500" />,
          animation: "animate-pulse",
          bg: "rgba(239, 68, 68, 0.1)",
        };
      case "warning":
        return {
          border: "1px solid var(--mythtech-gold)",
          icon: (
            <AlertTriangle
              className="w-5 h-5"
              style={{ color: "var(--mythtech-gold)" }}
            />
          ),
          animation: "",
          bg: "rgba(214, 179, 105, 0.1)",
        };
      case "info":
      default:
        return {
          border: "1px solid var(--mythtech-cyan)",
          icon: (
            <AlertCircle
              className="w-5 h-5"
              style={{ color: "var(--mythtech-cyan)" }}
            />
          ),
          animation: "",
          bg: "rgba(0, 229, 255, 0.1)",
        };
    }
  };

  // CSS Variables for MythTech colors
  const mythTechStyle = {
    "--mythtech-navy": "#0b1221",
    "--mythtech-cyan": "#00e5ff",
    "--mythtech-gold": "#d6b369",
  } as React.CSSProperties;

  return (
    <div
      className="p-4 rounded-xl flex flex-col h-full overflow-hidden"
      style={{
        ...mythTechStyle,
        backgroundColor: "var(--mythtech-navy)",
        color: "#e2e8f0", // Slate 200
        border: "1px solid rgba(0, 229, 255, 0.2)",
        boxShadow: "0 0 20px rgba(0, 229, 255, 0.05)",
      }}
    >
      {/* Header & Filters */}
      <div className="flex flex-col gap-4 mb-4">
        <div className="flex items-center justify-between">
          <h2
            className="text-lg font-bold flex items-center gap-2"
            style={{ color: "var(--mythtech-cyan)" }}
          >
            <Activity className="w-5 h-5" />
            System Alerts
          </h2>
          <div className="flex items-center gap-2 text-xs text-slate-400">
            <Clock className="w-3 h-3" />
            Updated: {lastUpdated.toLocaleTimeString()}
            <button
              onClick={fetchAlerts}
              disabled={loading}
              className={`p-1 hover:text-white transition-colors ${loading ? "animate-spin" : ""}`}
            >
              <RefreshCw className="w-4 h-4" />
            </button>
          </div>
        </div>

        <div className="flex flex-wrap gap-2 items-center">
          <div className="flex items-center gap-2 bg-slate-800/50 p-1 rounded-lg border border-slate-700">
            <Filter className="w-4 h-4 ml-1 text-slate-400" />

            {/* Severity Filter */}
            <select
              value={severityFilter}
              onChange={(e) =>
                setSeverityFilter(e.target.value as SeverityFilter)
              }
              className="bg-transparent border-none text-xs focus:ring-0 text-slate-200 cursor-pointer"
            >
              <option value="all">All Severities</option>
              <option value="info">Info</option>
              <option value="warning">Warning</option>
              <option value="critical">Critical</option>
            </select>

            <div className="w-px h-4 bg-slate-600 mx-1" />

            {/* Module Filter */}
            <select
              value={moduleFilter}
              onChange={(e) => setModuleFilter(e.target.value)}
              className="bg-transparent border-none text-xs focus:ring-0 text-slate-200 cursor-pointer"
            >
              <option value="all">All Modules</option>
              {availableModules.map((m) => (
                <option key={m} value={m}>
                  {m}
                </option>
              ))}
            </select>
          </div>

          <div className="flex gap-1 bg-slate-800/50 p-1 rounded-lg border border-slate-700">
            {(["1h", "24h", "7d", "all"] as TimeRange[]).map((range) => (
              <button
                key={range}
                onClick={() => setTimeRange(range)}
                className={`px-2 py-0.5 text-xs rounded transition-colors ${
                  timeRange === range
                    ? "bg-slate-600 text-white"
                    : "text-slate-400 hover:text-slate-200"
                }`}
              >
                {range}
              </button>
            ))}
          </div>
        </div>
      </div>

      {/* Alert List */}
      <div className="flex-1 overflow-y-auto space-y-3 pr-2 scrollbar-thin scrollbar-thumb-slate-700 scrollbar-track-transparent">
        {loading && alerts.length === 0 ? (
          <div className="flex items-center justify-center h-20 text-slate-500 text-sm">
            Loading alerts...
          </div>
        ) : error ? (
          <div className="flex items-center justify-center h-20 text-red-400 text-sm">
            {error}
          </div>
        ) : filteredAlerts.length === 0 ? (
          <div className="flex flex-col items-center justify-center h-32 text-slate-500 gap-2">
            <Check className="w-8 h-8 opacity-20" />
            <p className="text-sm">No active alerts</p>
          </div>
        ) : (
          filteredAlerts.map((alert) => {
            const styles = getSeverityStyles(alert.severity);
            return (
              <div
                key={alert.id}
                className={`p-3 rounded-lg relative transition-all ${styles.animation}`}
                style={{
                  borderLeft: `4px solid ${styles.border.split(" ")[2]}`, // Extract color from border definition
                  border: styles.border, // Fallback/Full border
                  background:
                    "linear-gradient(90deg, rgba(11,18,33,0.8), rgba(11,18,33,0.4))",
                }}
              >
                {/* Pulse overlay for critical */}
                {alert.severity === "critical" && (
                  <div className="absolute inset-0 bg-red-500/5 pointer-events-none animate-pulse rounded-lg" />
                )}

                <div className="flex items-start gap-3 relative z-10">
                  <div className="mt-0.5 flex-shrink-0">{styles.icon}</div>

                  <div className="flex-1 min-w-0">
                    <div className="flex items-center justify-between gap-2 mb-1">
                      <div className="flex items-center gap-2">
                        <span
                          className="text-xs font-bold uppercase tracking-wider opacity-80"
                          style={{ color: styles.border.split(" ")[2] }}
                        >
                          {alert.severity}
                        </span>
                        <span className="text-xs text-slate-500">Æ’?â€º</span>
