/// Report intelligence connection failure to Sentry
#[tauri::command]
pub async fn sentry_report_intelligence_failure(
    auth: State<'_, Arc<SecureAuthenticator>>,
    request: AuthenticatedRequest<ReportFailureRequest>,
    app_handle: AppHandle,
) -> Result<serde_json::Value, String> {
    let authenticated_user = auth
        .verify_token(&request.auth_token)
        .await
        .map_err(|e| format!("Authentication failed: {e}"))?;

    if authenticated_user != request.payload.user_id {
        return Err("Unauthorized: Cannot report failures for other users".to_string());
    }

    let component = request.payload.component;
    let error_message = request.payload.error_message;
    let user_id = request.payload.user_id;
    let context = request.payload.context;
    println!("[Sentry] Reporting intelligence connection failure: {component}");

    // Create security event for Sentry
    let event_data = serde_json::json!({
        "type": "intelligence_connection_failure",
        "origin": "intelligence_backend",
        "target_id": component,
        "description": format!("Intelligence backend connection failure: {}", error_message),
        "error": error_message,
        "user_id": user_id,
        "context": context,
        "timestamp": chrono::Utc::now().to_rfc3339()
    });

    // Queue event to Sentry using stdin/stdout CLI
    match execute_python_diagnostics(
        &app_handle,
        "src/personas/sentry.py",
        vec![
            "--queue-event",
            &serde_json::to_string(&event_data).unwrap(),
        ],
    ) {
        Ok(_data) => {
            println!("[Sentry] Intelligence failure reported successfully");
            Ok(serde_json::json!({
                "success": true,
                "message": "Failure reported to Sentry for investigation",
                "event_data": event_data
            }))
        }
        Err(e) => {
            eprintln!("[Sentry] Failed to report to Sentry: {e}");
            // Don't fail if Sentry reporting fails - log and continue
            Ok(serde_json::json!({
                "success": false,
                "message": format!("Failed to report to Sentry: {}", e),
                "event_data": event_data
            }))
        }
    }
}

/// Investigate intelligence failure and get recommendations
/// OPTIONAL: Uses Python investigator if available, gracefully fails otherwise
#[tauri::command]
pub async fn sentry_investigate_failure(
    auth: State<'_, Arc<SecureAuthenticator>>,
    request: AuthenticatedRequest<InvestigateFailureRequest>,
    app_handle: tauri::AppHandle,
) -> Result<serde_json::Value, String> {
    let authenticated_user = auth
        .verify_token(&request.auth_token)
        .await
        .map_err(|e| format!("Authentication failed: {e}"))?;

    if authenticated_user != request.payload.user_id {
        return Err("Unauthorized: Cannot investigate failures for other users".to_string());
    }

    let component = request.payload.component;
    let error_message = request.payload.error_message;
    let user_id = request.payload.user_id;
    println!("[Sentry] Investigating failure for component: {component}");

    // Create event data for investigation
    let event_data = serde_json::json!({
        "type": "intelligence_connection_failure",
        "origin": "intelligence_backend",
        "target_id": component,
        "error": error_message,
        "user_id": user_id,
        "timestamp": chrono::Utc::now().to_rfc3339()
    });

    // Run investigation via CLI
    let python_exe = if cfg!(windows) { "python" } else { "python3" };

    // Get script path from bundled resources or development path
    let ipc_script = match app_handle
        .path_resolver()
        .resolve_resource("src/cli/sentry_investigator.py")
    {
        Some(path) => path,
        Option::None => {
            return Ok(serde_json::json!({
                "component": component,
                "error": error_message,
                "investigation_available": false,
                "message": "Sentry investigation tools are not bundled with this build."
            }));
        }
    };

    if !ipc_script.exists() {
        return Ok(serde_json::json!({
            "component": component,
            "error": error_message,
            "investigation_available": false,
            "message": format!("Investigation script missing: {}", ipc_script.display())
        }));
    }

    // Try to spawn Python process - gracefully fail if Python not available
    let mut child = match std::process::Command::new(python_exe)
        .arg(ipc_script.to_string_lossy().to_string())
        .stdin(std::process::Stdio::piped())
        .stdout(std::process::Stdio::piped())
        .stderr(std::process::Stdio::piped())
        .spawn()
    {
        Ok(child) => child,
        Err(e) => {
            eprintln!("[sentry] Python investigator not available: {e}");
            // Return basic error info without Python investigation
            return Ok(serde_json::json!({
                "component": component,
                "error": error_message,
                "investigation_available": false,
                "message": "Python diagnostic tools not available. Install Python 3.8+ for detailed investigation."
            }));
        }
    };

    // Write event data to stdin
    if let Some(mut stdin) = child.stdin.take() {
        use std::io::Write;
        let event_json = serde_json::to_string(&event_data)
            .map_err(|e| format!("Failed to serialize event: {e}"))?;
        stdin
            .write_all(event_json.as_bytes())
            .map_err(|e| format!("Failed to write to stdin: {e}"))?;
    }

    // Read stdout
    let output = child
        .wait_with_output()
        .map_err(|e| format!("Failed to wait for investigation: {e}"))?;

    if !output.status.success() {
        return Err(format!(
            "Investigation failed with status: {:?}",
            output.status
        ));
    }

    // Parse investigation results
    let investigation_results = serde_json::from_slice(&output.stdout)
        .map_err(|e| format!("Failed to parse investigation results: {e}"))?;

    println!("[Sentry] Investigation completed successfully");
    Ok(investigation_results)
}

