+use serde::{Deserialize, Serialize};
+use std::collections::HashMap;
+use std::fs;
+use std::path::{Path, PathBuf};
+use std::sync::Arc;
+use tauri::State;
+use tokio::sync::RwLock;
+
+use crate::auth::SecureAuthenticator;
+use crate::guards::AuthenticatedRequest;
+use crate::require_auth;
+use crate::secure_vault::SecureVault;
+
+const CATALOG_PATH: &str = "src-tauri/src/synapse/marketplace_catalog.json";
+const PLUGIN_DIR_NAME: &str = "synapse_plugins";
+
+#[derive(Debug, Clone, Serialize, Deserialize)]
+pub struct PluginMetadata {
+    pub id: String,
+    pub name: String,
+    pub version: String,
+    pub description: String,
+    pub author: String,
+    pub category: PluginCategory,
+    pub trust_level: TrustLevel,
+    pub dependencies: Vec<String>,
+    pub permissions: Vec<Permission>,
+}
+
+#[derive(Debug, Clone, Serialize, Deserialize)]
+#[serde(rename_all = "snake_case")]
+pub enum PluginCategory {
+    Productivity,
+    Health,
+    IoT,
+    Developer,
+    Custom,
+}
+
+#[derive(Debug, Clone, Serialize, Deserialize)]
+#[serde(rename_all = "snake_case")]
+pub enum TrustLevel {
+    Verified,
+    Community,
+    Experimental,
+}
+
+#[derive(Debug, Clone, Serialize, Deserialize, PartialEq, Eq)]
+#[serde(rename_all = "snake_case")]
+pub enum Permission {
+    NetworkAccess,
+    FileSystemRead,
+    FileSystemWrite,
+    SystemEvents,
+    SensitiveData,
+}
+
+#[derive(Debug, Clone, Serialize, Deserialize)]
+#[serde(rename_all = "snake_case")]
+pub enum InstallStatus {
+    Available,
+    Installed,
+    UpdateAvailable,
+    Installing,
+    Failed(String),
+}
+
+#[derive(Debug, Clone, Serialize, Deserialize)]
+pub struct MarketplaceEntry {
+    pub metadata: PluginMetadata,
+    pub status: InstallStatus,
+}
+
+#[derive(Debug)]
+pub enum MarketplaceError {
+    Io(String),
+    Serde(String),
+    NotFound(String),
+    InstallFailed(String),
+}
+
+impl std::fmt::Display for MarketplaceError {
+    fn fmt(&self, f: &mut std::fmt::Formatter<'_>) -> std::fmt::Result {
+        match self {
+            MarketplaceError::Io(message) => write!(f, "IO error: {message}"),
+            MarketplaceError::Serde(message) => write!(f, "Serialization error: {message}"),
+            MarketplaceError::NotFound(message) => write!(f, "Not found: {message}"),
+            MarketplaceError::InstallFailed(message) => write!(f, "Install failed: {message}"),
+        }
+    }
+}
+
+impl std::error::Error for MarketplaceError {}
+
+impl From<std::io::Error> for MarketplaceError {
+    fn from(error: std::io::Error) -> Self {
+        MarketplaceError::Io(error.to_string())
+    }
+}
+
+impl From<serde_json::Error> for MarketplaceError {
+    fn from(error: serde_json::Error) -> Self {
+        MarketplaceError::Serde(error.to_string())
+    }
+}
+
+fn default_plugin_dir() -> PathBuf {
+    std::env::current_dir()
+        .unwrap_or_else(|_| PathBuf::from("."))
+        .join(PLUGIN_DIR_NAME)
+}
+
+fn read_metadata(path: &Path) -> Option<PluginMetadata> {
+    let content = fs::read_to_string(path).ok()?;
+    serde_json::from_str(&content).ok()
+}
+
+pub fn scan_local_plugins(plugin_dir: &Path) -> Vec<PluginMetadata> {
+    let mut plugins = Vec::new();
+    let entries = match fs::read_dir(plugin_dir) {
+        Ok(entries) => entries,
+        Err(_) => return plugins,
+    };
+
+    for entry in entries.flatten() {
+        let path = entry.path();
+        if path.is_dir() {
+            let manifest_path = path.join("manifest.json");
+            if let Some(metadata) = read_metadata(&manifest_path) {
+                plugins.push(metadata);
+            }
+        } else if path.extension().and_then(|ext| ext.to_str()) == Some("json") {
+            if let Some(metadata) = read_metadata(&path) {
+                plugins.push(metadata);
+            }
+        }
+    }
+
+    plugins
+}
+
+pub fn get_catalog() -> Vec<PluginMetadata> {
+    let content = match fs::read_to_string(CATALOG_PATH) {
+        Ok(content) => content,
+        Err(err) => {
+            eprintln!("[Marketplace] Failed to read catalog: {err}");
+            return Vec::new();
+        }
+    };
+
+    serde_json::from_str::<Vec<PluginMetadata>>(&content).unwrap_or_else(|err| {
+        eprintln!("[Marketplace] Failed to parse catalog: {err}");
+        Vec::new()
+    })
+}
+
+pub fn install_plugin(id: &str) -> Result<(), MarketplaceError> {
+    let catalog = get_catalog();
+    let plugin = catalog
+        .into_iter()
+        .find(|entry| entry.id == id)
+        .ok_or_else(|| MarketplaceError::NotFound(format!("Plugin {id} not found in catalog")))?;
+
+    let plugin_dir = default_plugin_dir();
+    fs::create_dir_all(&plugin_dir)?;
+
+    let target_dir = plugin_dir.join(&plugin.id);
+    if target_dir.exists() {
+        return Err(MarketplaceError::InstallFailed(format!(
+            "Plugin {id} already installed"
+        )));
+    }
+
+    fs::create_dir_all(&target_dir)?;
+    let manifest_path = target_dir.join("manifest.json");
+    let manifest_json = serde_json::to_string_pretty(&plugin)?;
+    fs::write(&manifest_path, manifest_json)?;
+
+    Ok(())
+}
+
+pub fn uninstall_plugin(id: &str) -> Result<(), MarketplaceError> {
+    let plugin_dir = default_plugin_dir();
+    let target_dir = plugin_dir.join(id);
+    if !target_dir.exists() {
+        return Err(MarketplaceError::NotFound(format!(
+            "Plugin {id} not installed"
+        )));
+    }
+
+    fs::remove_dir_all(target_dir)?;
+    Ok(())
+}
+
+// --- Authenticated Request Payloads ---
+
+#[derive(Debug, Clone, Serialize, Deserialize)]
+pub struct EmptyPayload {}
+
+#[derive(Debug, Clone, Serialize, Deserialize)]
+pub struct PluginIdPayload {
+    pub id: String,
+}
+
+#[derive(Debug, Clone, Serialize, Deserialize)]
+pub struct InstallPluginPayload {
+    pub id: String,
+    /// Permissions that the user has consented to
+    pub consented_permissions: Vec<Permission>,
+}
+
+fn catalog_with_status() -> Vec<MarketplaceEntry> {
+    let catalog = get_catalog();
+    let installed = scan_local_plugins(&default_plugin_dir());
+
+    let installed_map: HashMap<String, PluginMetadata> = installed
+        .into_iter()
+        .map(|item| (item.id.clone(), item))
+        .collect();
+
+    let mut entries = Vec::new();
+    for plugin in catalog {
+        let status = match installed_map.get(&plugin.id) {
+            Some(installed_plugin) => {
+                if installed_plugin.version != plugin.version {
+                    InstallStatus::UpdateAvailable
+                } else {
+                    InstallStatus::Installed
+                }
+            }
+            None => InstallStatus::Available,
+        };
+
+        entries.push(MarketplaceEntry {
+            metadata: plugin,
+            status,
+        });
+    }
+
+    for (_, plugin) in installed_map {
+        if entries.iter().any(|entry| entry.metadata.id == plugin.id) {
+            continue;
+        }
+
+        entries.push(MarketplaceEntry {
+            metadata: plugin,
+            status: InstallStatus::Installed,
+        });
+    }
+
+    entries
+}
+
+/// List all marketplace plugins with their installation status
+/// Requires authentication (read operation)
+#[tauri::command]
+pub async fn synapse_marketplace_list(
+    auth: State<'_, Arc<RwLock<SecureAuthenticator>>>,
+    request: AuthenticatedRequest<EmptyPayload>,
+) -> Result<Vec<MarketplaceEntry>, String> {
+    let auth_guard = auth.read().await;
+    let _authenticated_user = require_auth!(auth_guard, &request.auth_token);
+
+    println!("[Marketplace] User authenticated, listing plugins");
+    Ok(catalog_with_status())
+}
+
+/// Install a plugin from the marketplace
+/// Requires authentication (write operation) and permission consent validation
+#[tauri::command]
+pub async fn synapse_marketplace_install(
+    auth: State<'_, Arc<RwLock<SecureAuthenticator>>>,
+    vault: State<'_, SecureVault>,
+    request: AuthenticatedRequest<InstallPluginPayload>,
+) -> Result<(), String> {
+    let auth_guard = auth.read().await;
+    let authenticated_user = require_auth!(auth_guard, &request.auth_token);
+
+    let plugin_id = &request.payload.id;
+    let consented_permissions = &request.payload.consented_permissions;
+
+    println!(
+        "[Marketplace] User {} installing plugin: {}",
+        authenticated_user, plugin_id
+    );
+
+    // Validate that user has consented to all required permissions
+    let catalog = get_catalog();
+    let plugin = catalog
+        .iter()
+        .find(|entry| entry.id == *plugin_id)
+        .ok_or_else(|| format!("Plugin {} not found in catalog", plugin_id))?;
+
+    // Check that all required permissions are consented
+    for required_perm in &plugin.permissions {
+        if !consented_permissions.contains(required_perm) {
+            eprintln!(
+                "[SECURITY] Permission consent missing for {:?} during install of {}",
+                required_perm, plugin_id
+            );
+            return Err(format!(
+                "Permission consent required: {:?}. Please review and approve all permissions.",
+                required_perm
+            ));
+        }
+    }
+
+    // Special validation for SensitiveData permission
+    if plugin.permissions.contains(&Permission::SensitiveData) {
+        if !consented_permissions.contains(&Permission::SensitiveData) {
+            eprintln!(
+                "[SECURITY] SensitiveData permission not explicitly consented for plugin {}",
+                plugin_id
+            );
+            return Err(
+                "This plugin requires access to sensitive Vault data. Explicit consent is required."
+                    .to_string(),
+            );
+        }
+        println!(
+            "[Marketplace] User {} explicitly consented to SensitiveData access for {}",
+            authenticated_user, plugin_id
+        );
+    }
+
+    install_plugin(plugin_id).map_err(|err| err.to_string())?;
+
+    // Log the permission grant to the persistent audit log for compliance
+    let metadata = serde_json::json!({
+        "plugin_id": plugin_id,
+        "consented_permissions": consented_permissions,
+    });
+
+    vault
+        .log_audit_event(
+            &authenticated_user,
+            "permission_grant",
+            Some(plugin_id),
+            "success",
+            None,
+            Some(metadata),
+        )
+        .await
+        .map_err(|e| format!("Failed to log audit event: {}", e))?;
+
+    Ok(())
+}
+
+/// Uninstall a plugin
+/// Requires authentication (write operation)
+#[tauri::command]
+pub async fn synapse_marketplace_uninstall(
+    auth: State<'_, Arc<RwLock<SecureAuthenticator>>>,
+    request: AuthenticatedRequest<PluginIdPayload>,
+) -> Result<(), String> {
+    let auth_guard = auth.read().await;
+    let authenticated_user = require_auth!(auth_guard, &request.auth_token);
+
+    let plugin_id = &request.payload.id;
+    println!(
+        "[Marketplace] User {} uninstalling plugin: {}",
+        authenticated_user, plugin_id
+    );
+
+    uninstall_plugin(plugin_id).map_err(|err| err.to_string())
+}
+
+/// Get details for a specific plugin
+/// Requires authentication (read operation)
+#[tauri::command]
+pub async fn synapse_marketplace_get_details(
+    auth: State<'_, Arc<RwLock<SecureAuthenticator>>>,
+    request: AuthenticatedRequest<PluginIdPayload>,
+) -> Result<PluginMetadata, String> {
+    let auth_guard = auth.read().await;
+    let _authenticated_user = require_auth!(auth_guard, &request.auth_token);
+
+    let plugin_id = &request.payload.id;
+
+    let catalog = get_catalog();
+    if let Some(metadata) = catalog.into_iter().find(|entry| entry.id == *plugin_id) {
+        return Ok(metadata);
+    }
+
+    let installed = scan_local_plugins(&default_plugin_dir());
+    installed
+        .into_iter()
+        .find(|entry| entry.id == *plugin_id)
+        .ok_or_else(|| format!("Plugin {} not found", plugin_id))
+}
