+import { invoke } from '@tauri-apps/api/tauri';
+import { createAuthenticatedRequest, getStaticUserId } from './alden/ipc';
+
+export interface PluginMetadata {
+  id: string;
+  name: string;
+  version: string;
+  description: string;
+  author: string;
+  category: PluginCategory;
+  trust_level: TrustLevel;
+  dependencies: string[];
+  permissions: Permission[];
+}
+
+export type PluginCategory = 'productivity' | 'health' | 'iot' | 'developer' | 'custom';
+export type TrustLevel = 'verified' | 'community' | 'experimental';
+export type Permission =
+  | 'network_access'
+  | 'file_system_read'
+  | 'file_system_write'
+  | 'system_events'
+  | 'sensitive_data';
+
+export interface MarketplaceEntry {
+  metadata: PluginMetadata;
+  status: InstallStatus;
+}
+
+export type InstallStatus = 'available' | 'installed' | 'update_available' | 'installing' | { failed: string };
+
+export const MarketplaceBridge = {
+  list: (userId: string = getStaticUserId()) =>
+    invoke<MarketplaceEntry[]>('synapse_marketplace_list', {
+      request: createAuthenticatedRequest({}, userId),
+    }),
+  install: (id: string, consentedPermissions: Permission[], userId: string = getStaticUserId()) =>
+    invoke<void>('synapse_marketplace_install', {
+      request: createAuthenticatedRequest({ id, consented_permissions: consentedPermissions }, userId),
+    }),
+  uninstall: (id: string, userId: string = getStaticUserId()) =>
+    invoke<void>('synapse_marketplace_uninstall', {
+      request: createAuthenticatedRequest({ id }, userId),
+    }),
+  getDetails: (id: string, userId: string = getStaticUserId()) =>
+    invoke<PluginMetadata>('synapse_marketplace_get_details', {
+      request: createAuthenticatedRequest({ id }, userId),
+    }),
+};
