import React, { useEffect, useMemo, useState } from 'react';
import { Download, Loader2, Search, Shield, X, Zap } from '@/components/ui/HearthlinkIcons';
import { MarketplaceBridge, MarketplaceEntry, InstallStatus } from '@/bridges/MarketplaceBridge';

export interface SkillMarketplaceProps {
  personaId?: string;
  personaName?: string;
  onSkillToggle?: (skillId: string, enabled: boolean) => void;
  mode?: 'marketplace' | 'library';
  onCheckout?: (skillId: string) => void;
  initialSearch?: string;
}

const statusLabel = (status: InstallStatus) => {
  if (typeof status === 'string') return status;
  return 'failed';
};

const statusMessage = (status: InstallStatus) => {
  if (typeof status === 'string') return null;
  return status.failed;
};

const resolveErrorMessage = (err: unknown, fallback: string) => {
  if (err instanceof Error) return err.message || fallback;
  if (typeof err === 'string') return err || fallback;
  return fallback;
};

// Extracted Sub-component for Action Logic (KISS Rule 1: Screen Test)
const PluginActionButtons: React.FC<{
  entry: MarketplaceEntry;
  operatingId: string | null;
  onInstall: (entry: MarketplaceEntry) => void;
  onUninstall: (id: string) => void;
  mode: 'marketplace' | 'library';
  onCheckout?: (id: string) => void;
}> = ({ entry, operatingId, onInstall, onUninstall, mode, onCheckout }) => {
  const { metadata, status } = entry;
  const isBusy = operatingId === metadata.id || status === 'installing';
  const label = statusLabel(status);

  if (isBusy) {
    return (
      <span className="inline-flex items-center gap-2 text-[11px] text-slate-400">
        <Loader2 className="w-3 h-3 animate-spin" />
        Working...
      </span>
    );
  }

  const btnBase = "px-3 py-1.5 rounded-md text-[10px] font-bold uppercase tracking-wider transition-all";
  const btnInstall = "bg-alden-gold/15 text-alden-gold border border-alden-gold/30 hover:bg-alden-gold/25";
  const btnUninstall = "bg-alden-cyan/20 text-alden-cyan border border-alden-cyan/40 hover:bg-alden-cyan/30";
  const btnUpdate = "bg-emerald-500/15 text-emerald-300 border border-emerald-500/30 hover:bg-emerald-500/25";
  const btnCheckout = "bg-purple-500/20 text-purple-300 border border-purple-500/40 hover:bg-purple-500/30";

  // Library Mode Logic (Checkout/Active)
  if (mode === 'library') {
      if (label === 'installed' || label === 'update_available') {
          return (
              <div className="flex gap-2">
                 <button onClick={() => onCheckout?.(metadata.id)} className={`${btnBase} ${btnCheckout}`}>
                    Checkout
                 </button>
                 {label === 'update_available' && (
                     <button onClick={() => onInstall(metadata.id)} className={`${btnBase} ${btnUpdate}`}>
                         Update
                     </button>
                 )}
              </div>
          );
      }
      return null; // Should not happen given filtering, but safe fallback
  }

  // Marketplace Mode Logic
  if (label === 'available') {
    return (
      <button onClick={() => onInstall(entry)} className={`${btnBase} ${btnInstall}`}>
        Install
      </button>
    );
  }
  if (label === 'installed') {
    return (
      <button onClick={() => onUninstall(metadata.id)} className={`${btnBase} ${btnUninstall}`}>
        Uninstall
      </button>
    );
  }
  if (label === 'update_available') {
    return (
      <button onClick={() => onInstall(entry)} className={`${btnBase} ${btnUpdate}`}>
        Update
      </button>
    );
  }

  return (
    <button onClick={() => onInstall(entry)} className={`${btnBase} ${btnInstall}`}>
      Retry
    </button>
  );
};

export const SkillMarketplace: React.FC<SkillMarketplaceProps> = ({ 
    personaName = 'Persona', 
    mode = 'marketplace',
    onCheckout,
    initialSearch
}) => {
  const [plugins, setPlugins] = useState<MarketplaceEntry[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [operatingId, setOperatingId] = useState<string | null>(null);
  const [searchQuery, setSearchQuery] = useState(initialSearch || '');
  
  useEffect(() => {
    if (initialSearch !== undefined) setSearchQuery(initialSearch);
  }, [initialSearch]);

  const loadPlugins = async () => {
    setLoading(true);
    setError(null);
    try {
      const data = await MarketplaceBridge.list();
      setPlugins(data);
    } catch (err) {
      console.error(err);
      setError('Unable to load marketplace plugins right now.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    void loadPlugins();
  }, []);

  const handleInstall = async (entry: MarketplaceEntry) => {
    setOperatingId(entry.metadata.id);
    setError(null);
    try {
      await MarketplaceBridge.install(entry.metadata.id, entry.metadata.permissions);
      await loadPlugins();
    } catch (err) {
      console.error(err);
      setError(resolveErrorMessage(err, 'Install failed. Please try again.'));
    } finally {
      setOperatingId(null);
    }
  };

  const handleUninstall = async (pluginId: string) => {
    setOperatingId(pluginId);
    setError(null);
    try {
      await MarketplaceBridge.uninstall(pluginId);
      await loadPlugins();
    } catch (err) {
      console.error(err);
      setError(resolveErrorMessage(err, 'Uninstall failed. Please try again.'));
    } finally {
      setOperatingId(null);
    }
  };

  const filteredPlugins = useMemo(() => {
    let result = plugins;

    // Filter by mode
    if (mode === 'library') {
        result = result.filter(p => {
             const status = statusLabel(p.status);
             return status === 'installed' || status === 'update_available';
        });
    }

    if (!searchQuery) return result;
    const query = searchQuery.toLowerCase();
    return result.filter(({ metadata }) => {
      return (
        metadata.name.toLowerCase().includes(query) ||
        metadata.description.toLowerCase().includes(query) ||
        metadata.category.toLowerCase().includes(query) ||
        metadata.author.toLowerCase().includes(query) ||
        metadata.id.toLowerCase().includes(query)
      );
    });
  }, [plugins, searchQuery, mode]);

  if (loading && plugins.length === 0) {
    return (
      <div className="flex flex-col items-center justify-center py-4">
        <div
          className="w-12 h-12 rounded-full animate-pulse mb-4"
          style={{ backgroundColor: 'var(--mimic-accent)', opacity: 0.3 }}
        />
        <p className="text-sm text-muted-foreground">Loading {mode === 'marketplace' ? 'marketplace' : 'library'}...</p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
        <div>
          <h2 className="text-xl font-semibold text-foreground">
              {mode === 'marketplace' ? 'Skill Marketplace' : 'Skill Library'}
          </h2>
          <p className="text-sm text-muted-foreground">
            {filteredPlugins.length} skills {mode === 'marketplace' ? 'available' : 'ready'} {personaName ? `for ${personaName}` : ''}
          </p>
        </div>
        <div className="relative max-w-xs w-full">
          <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-muted-foreground" />
          <input
            type="text"
            placeholder={mode === 'marketplace' ? "Search plugins..." : "Search library..."}
            value={searchQuery}
