import { invoke } from '@tauri-apps/api/tauri';
import type { CalendarEvent, EmailAccount, EmailMessage, Task } from './types';

export const getStaticUserId = (): string => {
  return (window as unknown as { __hearthlink_userId?: string }).__hearthlink_userId || 'default_user';
};

export const createAuthenticatedRequest = <T>(payload: T, userId: string = getStaticUserId()) => ({
  auth_token: `demo_token_${userId}`,
  payload,
});

export const normalizeEmailAccounts = (accounts: any[]): EmailAccount[] => {
  return accounts.map(account => ({
    id: account.account_id,
    email_address: account.email_address,
    display_name: account.display_name,
    protocol: account.protocol,
    server: account.server,
    port: account.port,
    ssl_enabled: account.ssl_enabled,
    last_sync: account.last_sync,
    status: account.status,
  }));
};

export const normalizeEmailMessages = (messages: any[]): EmailMessage[] => {
  return messages.map(message => ({
    id: message.message_id,
    account_id: message.account_id,
    from: message.from,
    to: message.to,
    cc: message.cc,
    bcc: message.bcc,
    subject: message.subject,
    body: message.body,
    body_html: message.body_html,
    received_date: message.received_date,
    read: message.read,
    starred: message.starred,
    folder: message.folder,
    labels: message.labels,
    attachments: message.attachments,
  }));
};

