/**
 * Sentry Agent Bridge Implementation (Tauri IPC)
 *
 * Provides React/TypeScript bridge for Sentry agent system including:
 * - Security event monitoring
 * - Compliance rule management
 * - Audit report generation
 * - Oversight and governance
 * - Authentication and session management
 *
 * This bridge uses Tauri IPC (invoke) to communicate with the Rust backend
 * instead of HTTP requests or Python subprocess calls.
 *
 * @version 2.0.0
 * @date 2025-10-31
 */

import { invoke } from '@tauri-apps/api/tauri';
import { AsyncBaseBridge } from './AsyncBaseBridge';
import { BridgeConfig } from './BaseBridge';
import {
  SentryProps,
  SentrySecurityEvent,
  SentryComplianceRule,
  SentryAuditReport,
  AgentId,
  BridgeResponse,
} from '../types/agents';

type JsonObject = Record<string, unknown>;

/**
 * Security event filter options
 */
export interface SecurityEventFilter {
  userId?: string;
  agentId?: string;
  eventType?: string;
  severity?: 'low' | 'medium' | 'high' | 'critical';
  resolved?: boolean;
  limit?: number;
}

/**
 * Request type for creating a security event
 */
export interface CreateSecurityEventRequest {
  userId: string;
  agentId?: string;
  eventType: string;
  severity: 'low' | 'medium' | 'high' | 'critical';
  description: string;
  details: JsonObject;
}

/**
 * Request type for creating a compliance rule
 */
export interface CreateComplianceRuleRequest {
  name: string;
  description: string;
  severity: 'low' | 'medium' | 'high' | 'critical';
  conditions: JsonObject;
  actions: JsonObject;
}

/**
 * Request type for generating an audit report
 */
export interface GenerateAuditReportRequest {
  userId: string;
  type: 'security' | 'compliance' | 'activity' | 'system';
  startDate?: string;
  endDate?: string;
  filters: JsonObject;
}

/**
 * Authentication credentials
 */
export interface AuthCredentials {
  username: string;
  password: string;
}

/**
 * Session information
 */
export interface AuthSession {
  userId: string;
  authToken: string;
  createdAt?: string;
  lastActivity?: string;
  isAuthenticated?: boolean;
}

interface LoginResponse {
  success: boolean;
  userId?: string;
  authToken?: string;
  error?: string;
}

interface UserSession {
  userId: string;
  authToken: string;
  createdAt: string;
  lastActivity: string;
  isAuthenticated: boolean;
}

export class SentryBridge extends AsyncBaseBridge<SentryProps> {
  private events: SentrySecurityEvent[] = [];
  private rules: SentryComplianceRule[] = [];
  private reports: SentryAuditReport[] = [];
  private currentSession: AuthSession | null = null;
  private dataLoaded = false;
  private errorCodePrefix = 'SENTRY';

  constructor(config?: Partial<BridgeConfig>) {
    super({
      agentId: 'sentry',
      ...config,
    });
  }

  private getNormalizedUserId(): string {
    return this.userId || 'default_user';
  }

  /**
   * Initialize Sentry bridge - sets up initial state only (no IPC calls)
   *
   * IMPORTANT: No Tauri IPC calls should be made here to avoid deadlock.
   * IPC calls are deferred until after component mount via lazy loading in getProps().
   */
  protected async onInitialize(): Promise<void> {
    console.log('[SentryBridge.onInitialize] START - Initializing Sentry bridge');

    // Initialize with empty arrays - data will be loaded lazily
    this.events = [];
    this.rules = [];
    this.reports = [];
    this.currentSession = null;
    this.dataLoaded = false;

    console.log('[SentryBridge.onInitialize] END - Initialized successfully (data will load lazily)');
  }

  /**
   * Cleanup resources
   */
  protected async onDestroy(): Promise<void> {
    this.events = [];
    this.rules = [];
    this.reports = [];
    this.currentSession = null;
    this.dataLoaded = false;
  }

  /**
   * Get props for Sentry React component
   *
   * Performs lazy loading of security data on first call after component mount.
   * This ensures Tauri IPC is ready and avoids initialization deadlock.
   */
  async getProps(): Promise<SentryProps> {
    // Lazy load security data on first call (safe to use IPC after mount)
    if (!this.dataLoaded) {
      console.log('[SentryBridge.getProps] Lazy loading security data (first call after mount)');
      try {
        // Check for existing session
        const sessionResponse = await this.getSession();
        if (sessionResponse.success && sessionResponse.data) {
          this.currentSession = sessionResponse.data;
        }

        // Load recent security events (limit 100)
        const eventsResponse = await this.getEvents({ limit: 100 });
        if (eventsResponse.success && eventsResponse.data) {
          this.events = eventsResponse.data;
        }

        // Load compliance rules
        const rulesResponse = await this.getRules();
        if (rulesResponse.success && rulesResponse.data) {
          this.rules = rulesResponse.data;
        }

        // Load recent audit reports (limit 10)
        const reportsResponse = await this.getReports(10);
        if (reportsResponse.success && reportsResponse.data) {
          this.reports = reportsResponse.data;
        }

        this.dataLoaded = true;
        console.log('[SentryBridge.getProps] Security data loaded successfully');
      } catch (error) {
        console.warn('[SentryBridge.getProps] Failed to load security data, continuing with empty state:', error);
        // Don't throw - allow degraded mode
      }
    }

    return {
      events: this.events,
      rules: this.rules,
      reports: this.reports,
      onEventResolve: this.resolveEvent.bind(this),
      onRuleToggle: this.toggleRule.bind(this),
      onReportGenerate: (schedule: SentryAuditReport['type']) =>
        this.generateReport({
          userId: this.userId || 'default_user',
          type: this.mapReportType(schedule),
          filters: { schedule },
        }),
    };
  }

  // ============================================================================
  // Authentication & Session Management
  // ============================================================================

  /**
   * Get current session
   * Invokes: sentry_auth_get_session
   */
  async getSession(): Promise<BridgeResponse<AuthSession | null>> {
    try {
      const session = await invoke<UserSession | null>('sentry_auth_get_session');
      this.currentSession = this.toAuthSession(session);
      return { success: true, data: this.currentSession };
    } catch (error) {
      return this.createErrorResponse<AuthSession | null>(error, 'Failed to get session', 'SESSION_GET_ERROR');
    }
  }

  /**
   * Login with credentials
   * Invokes: sentry_auth_login
   */
  async login(credentials: AuthCredentials): Promise<BridgeResponse<AuthSession>> {
    try {
      const response = await invoke<LoginResponse>('sentry_auth_login', {
        request: {
          username: credentials.username,
          password: credentials.password,
        },
      });

      if (!response.success || !response.userId || !response.authToken) {
        return {
          success: false,
          error: response.error || 'Login failed',
          code: 'LOGIN_ERROR',
        };
      }

      const session: AuthSession = {
        userId: response.userId,
        authToken: response.authToken,
        isAuthenticated: true,
      };

      this.currentSession = session;
      this.notifySubscribers({ type: 'session_created', data: session });
      return { success: true, data: session };
    } catch (error) {
      return this.createErrorResponse<AuthSession>(error, 'Failed to login', 'LOGIN_ERROR');
    }
  }

  /**
   * Logout current session
   * Invokes: sentry_auth_logout
   */
  async logout(): Promise<BridgeResponse<void>> {
    try {
      await invoke<void>('sentry_auth_logout');
      this.currentSession = null;
      this.notifySubscribers({ type: 'session_ended', data: {} });
      return { success: true, data: undefined };
    } catch (error) {
      return this.createErrorResponse<void>(error, 'Failed to logout', 'LOGOUT_ERROR');
    }
  }

  /**
   * Verify current session
   * Invokes: sentry_auth_verify_session
   */
  async verifySession(authToken: string): Promise<BridgeResponse<AuthSession>> {
    try {
      const session = await invoke<UserSession>('sentry_auth_verify_session', { authToken });
      const normalized = this.toAuthSession(session);
      if (!normalized) {
        return { success: false, error: 'Session verification failed: Invalid session', code: 'INVALID_SESSION' };
      }
      this.currentSession = normalized;
      return { success: true, data: normalized };
    } catch (error) {
      return this.createErrorResponse<AuthSession>(error, 'Failed to verify session', 'SESSION_VERIFY_ERROR');
    }
  }

  /**
   * Auto-login (biometric or cached credentials)
   * Invokes: sentry_auth_auto_login
   */
  async autoLogin(): Promise<BridgeResponse<AuthSession>> {
    try {
      const session = await invoke<UserSession>('sentry_auth_auto_login');
      const normalized = this.toAuthSession(session);
      if (!normalized) {
        return { success: false, error: 'Auto-login failed: No session found', code: 'AUTO_LOGIN_FAILED' };
      }
      this.currentSession = normalized;
      this.notifySubscribers({ type: 'session_created', data: normalized });
      return { success: true, data: normalized };
    } catch (error) {
      return this.createErrorResponse<AuthSession>(error, 'Failed to auto-login', 'AUTO_LOGIN_ERROR');
    }
  }

  /**
   * Get current user ID from session
   * Invokes: sentry_auth_get_user_id
   */
  async getUserId(): Promise<BridgeResponse<string | null>> {
    try {
      const userId = await invoke<string | null>('sentry_auth_get_user_id');
      return { success: true, data: userId };
    } catch (error) {
      return this.createErrorResponse<string | null>(error, 'Failed to get user ID', 'USER_ID_ERROR');
    }
  }

  private toAuthSession(session: UserSession | null): AuthSession | null {
    if (!session) return null;
    return {
      userId: session.userId,
      authToken: session.authToken,
      createdAt: session.createdAt,
      lastActivity: session.lastActivity,
      isAuthenticated: session.isAuthenticated,
    };
  }

  // ============================================================================
  // Security Event Management
  // ============================================================================

  /**
   * Get security events with optional filters
   * Invokes: sentry_get_events
   */
  async getEvents(filter?: SecurityEventFilter): Promise<BridgeResponse<SentrySecurityEvent[]>> {
    try {
      this.events = await invoke<SentrySecurityEvent[]>('sentry_get_events', {
        userId: filter?.userId || null,
        agentId: filter?.agentId || null,
        eventType: filter?.eventType || null,
        severity: filter?.severity || null,
        resolved: filter?.resolved ?? null,
        limit: filter?.limit || null,
      });

      return { success: true, data: this.events };
    } catch (error) {
      return this.createErrorResponse<SentrySecurityEvent[]>(
