/**
 * Sentry Health Panel - Real-time Metrics Visualization
 *
 * Displays system health metrics, performance baselines, and status indicators
 * with real-time updates and visual feedback.
 *
 * @version 1.0.0
 * @date 2026-01-22
 */

import React, { useEffect, useState, useCallback } from 'react';
import { invoke } from '@tauri-apps/api/tauri';

// MythTech Design Tokens
const COLORS = {
  navy: '#0b1221',
  cyan: '#00e5ff',
  gold: '#d6b369',
  success: '#10b981',
  warning: '#f59e0b',
  error: '#ef4444',
  slate: { 400: '#94a3b8', 500: '#64748b', 700: '#334155', 800: '#1e293b' },
} as const;

interface HealthMetric {
  id: string;
  label: string;
  value: number;
  unit: string;
  threshold: { warning: number; critical: number };
  trend?: 'up' | 'down' | 'stable';
}

interface SystemHealth {
  overall: 'healthy' | 'degraded' | 'critical';
  uptime_seconds: number;
  last_incident?: string;
  active_alerts: number;
}

interface SentryHealthPanelProps {
  userId?: string;
  refreshInterval?: number;
}

const getStatusColor = (status: string): string => {
  switch (status) {
    case 'healthy': return COLORS.success;
    case 'degraded': return COLORS.warning;
    case 'critical': return COLORS.error;
    default: return COLORS.slate[500];
  }
};

const getMetricColor = (value: number, threshold: { warning: number; critical: number }): string => {
  if (value >= threshold.critical) return COLORS.error;
  if (value >= threshold.warning) return COLORS.warning;
  return COLORS.success;
};

const formatUptime = (seconds: number): string => {
  const days = Math.floor(seconds / 86400);
  const hours = Math.floor((seconds % 86400) / 3600);
  const mins = Math.floor((seconds % 3600) / 60);
  if (days > 0) return `${days}d ${hours}h`;
  if (hours > 0) return `${hours}h ${mins}m`;
  return `${mins}m`;
};

const TrendIndicator: React.FC<{ trend?: 'up' | 'down' | 'stable' }> = ({ trend }) => {
  if (!trend) return null;
  const arrows = { up: 'Ã¢â€ â€˜', down: 'Ã¢â€ â€œ', stable: 'Ã¢â€ â€™' };
  const colors = { up: COLORS.error, down: COLORS.success, stable: COLORS.slate[400] };
  return <span style={{ color: colors[trend], marginLeft: 4 }}>{arrows[trend]}</span>;
};

export const SentryHealthPanel: React.FC<SentryHealthPanelProps> = ({
  userId,
  refreshInterval = 15000,
}) => {
  const [health, setHealth] = useState<SystemHealth | null>(null);
  const [metrics, setMetrics] = useState<HealthMetric[]>([]);
  const [lastUpdated, setLastUpdated] = useState<Date | null>(null);
  const [isLoading, setIsLoading] = useState(true);

  const fetchHealthData = useCallback(async () => {
    try {
      const token = `demo_token_${userId || 'default'}`;

      // Fetch system resources
      const resources = await invoke<Record<string, number>>('sentry_get_system_resources', {
        request: { auth_token: token, payload: {} },
      }).catch(() => ({ cpu_percent: 0, memory_percent: 0, disk_percent: 0 }));

      // Build metrics from resources
      const newMetrics: HealthMetric[] = [
        {
          id: 'cpu',
          label: 'CPU Usage',
          value: resources.cpu_percent ?? 0,
          unit: '%',
          threshold: { warning: 70, critical: 90 },
          trend: 'stable',
        },
        {
          id: 'memory',
          label: 'Memory',
          value: resources.memory_percent ?? 0,
          unit: '%',
          threshold: { warning: 75, critical: 90 },
          trend: 'stable',
        },
        {
          id: 'disk',
          label: 'Disk I/O',
          value: resources.disk_percent ?? 0,
          unit: '%',
          threshold: { warning: 80, critical: 95 },
          trend: 'stable',
        },
      ];

      // Derive overall health
      const maxMetric = Math.max(...newMetrics.map(m => m.value));
      const overallStatus: SystemHealth['overall'] =
        maxMetric >= 90 ? 'critical' : maxMetric >= 70 ? 'degraded' : 'healthy';

      setMetrics(newMetrics);
      setHealth({
        overall: overallStatus,
        uptime_seconds: Math.floor((Date.now() - new Date().setHours(0, 0, 0, 0)) / 1000),
        active_alerts: maxMetric >= 70 ? 1 : 0,
      });
      setLastUpdated(new Date());
    } catch (err) {
      console.error('[SentryHealthPanel] Fetch error:', err);
    } finally {
      setIsLoading(false);
    }
  }, [userId]);

  useEffect(() => {
    fetchHealthData();
    const interval = setInterval(fetchHealthData, refreshInterval);
    return () => clearInterval(interval);
  }, [fetchHealthData, refreshInterval]);

  if (isLoading) {
    return (
      <div className="module-card p-6" style={{ backgroundColor: COLORS.navy }}>
        <div className="flex items-center justify-center py-8">
          <div
            className="w-6 h-6 border-2 rounded-full animate-spin"
            style={{ borderColor: COLORS.cyan, borderTopColor: 'transparent' }}
          />
          <span className="ml-3 text-slate-400">Loading health metrics...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="module-card p-4 mb-6" style={{ backgroundColor: COLORS.navy }}>
      {/* Header */}
      <div className="flex items-center justify-between mb-4">
        <div className="flex items-center gap-3">
          <div
            className="w-3 h-3 rounded-full animate-pulse"
            style={{ backgroundColor: getStatusColor(health?.overall || 'healthy') }}
          />
          <h2 className="text-lg font-semibold" style={{ color: COLORS.cyan }}>
            System Health
          </h2>
        </div>
        <div className="flex items-center gap-4">
          {health && (
            <span
              className="px-2 py-1 text-xs font-medium rounded-full uppercase"
              style={{
                backgroundColor: `${getStatusColor(health.overall)}20`,
                color: getStatusColor(health.overall),
              }}
            >
              {health.overall}
            </span>
          )}
          <span className="text-xs" style={{ color: COLORS.slate[500] }}>
            {lastUpdated ? `Updated ${lastUpdated.toLocaleTimeString()}` : 'Updating...'}
          </span>
        </div>
      </div>

      {/* Summary Stats */}
      <div className="grid grid-cols-3 gap-3 mb-4">
        <div className="rounded-lg border p-3" style={{ borderColor: COLORS.slate[700], backgroundColor: 'rgba(0,0,0,0.3)' }}>
          <div className="text-xs uppercase tracking-wider" style={{ color: COLORS.slate[500] }}>Uptime</div>
          <div className="text-xl font-bold mt-1" style={{ color: COLORS.cyan }}>
            {health ? formatUptime(health.uptime_seconds) : '--'}
          </div>
        </div>
        <div className="rounded-lg border p-3" style={{ borderColor: COLORS.slate[700], backgroundColor: 'rgba(0,0,0,0.3)' }}>
          <div className="text-xs uppercase tracking-wider" style={{ color: COLORS.slate[500] }}>Active Alerts</div>
          <div className="text-xl font-bold mt-1" style={{ color: health?.active_alerts ? COLORS.warning : COLORS.success }}>
            {health?.active_alerts ?? 0}
          </div>
        </div>
        <div className="rounded-lg border p-3" style={{ borderColor: COLORS.slate[700], backgroundColor: 'rgba(0,0,0,0.3)' }}>
          <div className="text-xs uppercase tracking-wider" style={{ color: COLORS.slate[500] }}>Services</div>
          <div className="text-xl font-bold mt-1" style={{ color: COLORS.success }}>
            {metrics.length}/3
          </div>
        </div>
      </div>

      {/* Metrics Grid */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-3">
        {metrics.map((metric) => {
          const color = getMetricColor(metric.value, metric.threshold);
          const percentage = Math.min(100, metric.value);

          return (
            <div
              key={metric.id}
              className="rounded-lg border p-3"
              style={{ borderColor: COLORS.slate[700], backgroundColor: 'rgba(0,0,0,0.3)' }}
            >
              <div className="flex items-center justify-between mb-2">
                <span className="text-xs uppercase tracking-wider" style={{ color: COLORS.slate[500] }}>
                  {metric.label}
                </span>
                <TrendIndicator trend={metric.trend} />
              </div>
              <div className="flex items-end gap-1 mb-2">
                <span className="text-2xl font-bold" style={{ color }}>
                  {metric.value.toFixed(1)}
                </span>
                <span className="text-sm mb-0.5" style={{ color: COLORS.slate[400] }}>
                  {metric.unit}
                </span>
              </div>
              {/* Progress Bar */}
              <div className="h-1.5 rounded-full overflow-hidden" style={{ backgroundColor: COLORS.slate[700] }}>
                <div
                  className="h-full rounded-full transition-all duration-500"
                  style={{ width: `${percentage}%`, backgroundColor: color }}
                />
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default SentryHealthPanel;

