/**
 * Base Agent Bridge Implementation
 *
 * Provides common functionality for all agent bridges including:
 * - API communication
 * - Event subscription
 * - Error handling
 * - State management
 *
 * @version 1.3.0
 * @date 2025-10-09
 */

import { invoke } from '@tauri-apps/api/tauri';
import { AgentId, AgentBridge, BridgeResponse, AgentCommand, CommandRequest } from '../types/agents';
import { isBrowserMode, warnBrowserMode } from '../utils/tauri';

type JsonObject = Record<string, unknown>;

export interface BridgeConfig {
  agentId: AgentId;
  baseUrl?: string;
  timeout?: number;
  retryAttempts?: number;
  userId?: string; // Optional, defaults to 'default_user'
}

export abstract class BaseBridge<TProps = unknown> implements AgentBridge<TProps> {
  public readonly agentId: AgentId;
  protected baseUrl: string;
  protected timeout: number;
  protected retryAttempts: number;
  protected subscribers: Set<(data: unknown) => void> = new Set();
  protected initialized = false;
  protected userId: string;

  constructor(config: BridgeConfig) {
    this.agentId = config.agentId;
    this.baseUrl = config.baseUrl || '/api';
    this.timeout = config.timeout || 30000;
    this.retryAttempts = config.retryAttempts || 3;
    this.userId = config.userId || 'default_user';
  }

  /**
   * Initialize the bridge connection
   */
  async initialize(): Promise<void> {
    if (this.initialized) {
      return;
    }

    try {
      // Perform agent-specific initialization
      await this.onInitialize();
      this.initialized = true;
      console.log(`[Bridge:${this.agentId}] Initialized successfully`);
    } catch (error) {
      console.error(`[Bridge:${this.agentId}] Initialization failed:`, error);
      throw error;
    }
  }

  /**
   * Get props for the agent component
   */
  abstract getProps(): Promise<TProps>;

  /**
   * Send a message to the agent
   */
  async sendMessage(message: unknown): Promise<BridgeResponse<unknown>> {
    if (!this.initialized) {
      return {
        success: false,
        error: 'Bridge not initialized',
        code: 'NOT_INITIALIZED',
      };
    }

    try {
      const command = `${this.agentId}_send_message`;
      const request: CommandRequest = {
        user_id: this.userId,
        message,
      };

      const response = await this.invokeCommand(command as AgentCommand, { request });
      this.notifySubscribers({ type: 'message_sent', data: message });
      return { success: true, data: response };
    } catch (error) {
      const messageText = error instanceof Error ? error.message : 'Failed to send message';
      const code = typeof error === 'object' && error !== null && 'code' in error ? (error as { code?: string }).code : undefined;
      return {
        success: false,
        error: messageText,
        code: code || 'SEND_ERROR',
      };
    }
  }

  /**
   * Subscribe to bridge events
   */
  subscribe(callback: (data: unknown) => void): () => void {
    this.subscribers.add(callback);
    return () => {
      this.subscribers.delete(callback);
    };
  }

  /**
   * Destroy the bridge and cleanup resources
   */
  async destroy(): Promise<void> {
    try {
      await this.onDestroy();
      this.subscribers.clear();
      this.initialized = false;
      console.log(`[Bridge:${this.agentId}] Destroyed successfully`);
    } catch (error) {
      console.error(`[Bridge:${this.agentId}] Destruction failed:`, error);
      throw error;
    }
  }

  // Protected methods for subclasses

  /**
   * Hook for agent-specific initialization
   */
  protected abstract onInitialize(): Promise<void>;

  /**
   * Hook for agent-specific cleanup
   */
  protected abstract onDestroy(): Promise<void>;

  /**
   * Invoke a Tauri IPC command
   * @param command - The Tauri command name (e.g., 'alden_tasks_list')
   * @param params - The parameters to pass to the command
   * @param attempt - Current retry attempt (for internal use)
   * @returns The command result
   */
  protected async invokeCommand<T = unknown>(
    command: AgentCommand,
    params?: JsonObject,
    attempt = 1
  ): Promise<T> {
    const callId = `${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;

    // Browser mode: return mock data instead of failing
    if (isBrowserMode()) {
      warnBrowserMode('BaseBridge');
      console.log(`[BaseBridge.invokeCommand][${callId}] BROWSER MODE - Returning mock data for command: ${command}`);
      return this.getMockResponse<T>(command, params);
    }

    console.log(`[BaseBridge.invokeCommand][${callId}] START command: ${command}`, JSON.stringify(params));

    // Prepare payload with injected auth token for dev
    const payload = { ...(params || {}) } as JsonObject;
    if (!('auth_token' in payload)) {
      // Inject demo token matching the current userId to pass ownership checks
      // This will only work in Debug builds due to backend checks
      payload.auth_token = `demo_token_${this.userId}`;
    }

    try {
      console.log(`[BaseBridge.invokeCommand][${callId}] About to call Tauri invoke() with command: ${command}`);
      const startTime = performance.now();

      const result = await invoke<T>(command, payload);

      const elapsed = performance.now() - startTime;
      console.log(`[BaseBridge.invokeCommand][${callId}] SUCCESS after ${elapsed.toFixed(2)}ms for command: ${command}`);
      console.log(`[BaseBridge.invokeCommand][${callId}] Result type:`, typeof result);
      console.log(`[BaseBridge.invokeCommand][${callId}] Result is array:`, Array.isArray(result));

      return result;
    } catch (error) {
      const safeNow = () => {
        try {
          return (typeof performance !== 'undefined' && performance?.now?.()) || Date.now();
        } catch {
          return Date.now();
        }
      };
      const elapsed = safeNow();
      console.error(`[BaseBridge.invokeCommand][${callId}] ERROR after ${elapsed.toFixed(2)}ms for command: ${command}`);
      console.error(`[BaseBridge.invokeCommand][${callId}] Error details:`, error);
      console.error(`[BaseBridge.invokeCommand][${callId}] Error type:`, typeof error);
      if (error instanceof Error) {
        console.error(`[BaseBridge.invokeCommand][${callId}] Error message:`, error.message);
        console.error(`[BaseBridge.invokeCommand][${callId}] Error stack:`, error.stack);
      }

      // Retry on transient errors
      if (attempt < this.retryAttempts && this.isRetryableError(error)) {
        console.log(`[BaseBridge.invokeCommand][${callId}] Retrying command ${command} (attempt ${attempt + 1}/${this.retryAttempts})`);
        await this.delay(1000 * attempt); // Exponential backoff
        return this.invokeCommand(command, params, attempt + 1);
      }

      console.error(`[BaseBridge.invokeCommand][${callId}] Throwing error for command: ${command}`);
      throw error;
    }
  }

  /**
   * Get mock response for browser mode development
   * Override in subclasses to provide agent-specific mock data
   */
  protected getMockResponse<T>(command: AgentCommand, params?: JsonObject): T {
    console.log(`[BaseBridge] Mock response for ${command}`, params);

    // Default mock responses for common patterns
    if (command.includes('list') || command.includes('get_all')) {
      return [] as T;
    }

    if (command.includes('get') || command.includes('fetch')) {
      return { success: true, data: null } as T;
    }

    if (command.includes('create') || command.includes('update') || command.includes('delete')) {
      return { success: true, id: 'mock-id-' + Date.now() } as T;
    }

    // Generic success response
    return { success: true } as T;
  }

  /**
   * Check if an error is retryable
   */
  private isRetryableError(error: unknown): boolean {
    const message =
      error instanceof Error
        ? error.message
        : typeof error === 'string'
        ? error
        : error !== null && typeof error === 'object'
        ? (error as { message?: string }).message || ''
        : '';

    return message.includes('timeout') || message.includes('network') || message.includes('connection');
  }

  /**
   * @deprecated Use invokeCommand() instead. HTTP requests are no longer supported.
   * This method is kept for backward compatibility but will be removed in future versions.
   */
  protected async makeRequest<T = unknown>(
    method: 'GET' | 'POST' | 'PATCH' | 'DELETE',
    endpoint: string,
    body?: JsonObject | string,
    _attempt = 1
  ): Promise<T> {
    console.warn(`[Bridge:${this.agentId}] makeRequest() is deprecated. Use invokeCommand() instead.`);
    throw new Error('HTTP requests are no longer supported. Bridge must use Tauri IPC commands.');
  }

  /**
   * Notify all subscribers of an event
   */
  protected notifySubscribers(data: unknown): void {
    this.subscribers.forEach(callback => {
      try {
        callback(data);
      } catch (error) {
        console.error(`[Bridge:${this.agentId}] Subscriber callback error:`, error);
      }
    });
  }

  /**
   * Delay helper for retry logic
   */
  protected delay(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  /**
   * Validate response data against expected schema
   */
  protected validateResponse<T>(data: unknown, requiredFields: string[]): T {
    if (typeof data !== 'object' || data === null) {
      throw new Error('Invalid response type');
    }
    for (const field of requiredFields) {
      if (!(field in data)) {
        throw new Error(`Missing required field: ${field}`);
      }
    }
    return data as T;
  }

  /**
   * Create a standardized error response
   */
  protected createErrorResponse<T>(error: unknown, fallback?: string, code?: string): BridgeResponse<T> {
    let message: string;
    if (error instanceof Error) {
      message = error.message;
    } else if (typeof error === 'string') {
      message = error;
    } else if (typeof error === 'object' && error !== null && 'message' in error) {
      const maybeMessage = (error as { message?: unknown }).message;
      message = typeof maybeMessage === 'string' ? maybeMessage : (fallback || 'Unknown error');
    } else {
      message = fallback || 'Unknown error';
    }

    return {
      success: false,
      error: message,
      code: code || 'BRIDGE_ERROR',
    };
  }
}

/**
 * Bridge Factory
 * Creates and initializes agent bridges
 */
export class BridgeFactory {
  private static bridges: Map<AgentId, AgentBridge> = new Map();
  private static bridgeRefs: Map<AgentId, number> = new Map();

  static async createBridge<TProps>(
    Bridge: new (config: BridgeConfig) => AgentBridge<TProps>,
    agentId: AgentId,
    userId = 'default_user' // TODO: Replace with actual user ID from auth system
  ): Promise<AgentBridge<TProps>> {
    // Return existing bridge if already created
    if (this.bridges.has(agentId)) {
      const refCount = this.bridgeRefs.get(agentId) ?? 0;
      this.bridgeRefs.set(agentId, refCount + 1);
      return this.bridges.get(agentId) as AgentBridge<TProps>;
    }

    // Create and initialize new bridge
    const bridge = new Bridge({ agentId, userId });
    await bridge.initialize();
    this.bridges.set(agentId, bridge);
    this.bridgeRefs.set(agentId, 1);

    return bridge;
  }

  static async destroyBridge(agentId: AgentId): Promise<void> {
    const refCount = this.bridgeRefs.get(agentId) ?? 0;

    if (refCount > 1) {
      this.bridgeRefs.set(agentId, refCount - 1);
      return;
    }

    this.bridgeRefs.delete(agentId);

    const bridge = this.bridges.get(agentId);
    if (bridge) {
      this.bridges.delete(agentId);
      await bridge.destroy();
    }
  }

  static async destroyAll(): Promise<void> {
    const promises = Array.from(this.bridges.keys()).map(agentId => this.destroyBridge(agentId));
    await Promise.all(promises);
    this.bridgeRefs.clear();
  }

  static getBridge(agentId: AgentId): AgentBridge | undefined {
    return this.bridges.get(agentId);
  }
}

