  { id: 'log-005', component: 'Sentry', severity: 'Info', message: 'Diagnostics snapshot captured.', timestamp: new Date(Date.now() - 5400000).toISOString() },
];

const ALL_COMPONENTS: Array<LogComponent | 'All'> = ['All', 'Alden', 'Alice', 'Vault', 'Synapse', 'Core', 'Sentry', 'Mimic'];
const ALL_SEVERITIES: Array<LogSeverity | 'All'> = ['All', 'Debug', 'Info', 'Warning', 'Error'];

export const OperationalLogsPanel: React.FC = () => {
  const [componentFilter, setComponentFilter] = useState<LogComponent | 'All'>('All');
  const [severityFilter, setSeverityFilter] = useState<LogSeverity | 'All'>('All');

  const filteredLogs = useMemo(() => {
    return MOCK_LOGS.filter((log) => {
      if (componentFilter !== 'All' && log.component !== componentFilter) return false;
      if (severityFilter !== 'All' && log.severity !== severityFilter) return false;
      return true;
    });
  }, [componentFilter, severityFilter]);

  return (
    <div className="h-full bg-alden-panel ring-1 ring-[rgba(255,255,255,0.1)] rounded-xl p-6 relative overflow-hidden flex flex-col w-full">
      <div className="flex items-center justify-between mb-5">
        <div className="flex items-center gap-3">
          <div className="p-2 rounded-lg bg-red-500/10 border border-red-500/20 text-red-400">
            <FileText size={20} />
          </div>
          <h2 className="text-xl font-semibold text-red-100 uppercase tracking-widest font-orbitron-force">Operational Logs</h2>
        </div>
      </div>

      <div className="flex flex-wrap gap-3 mb-4">
        <select
          value={componentFilter}
          onChange={(e) => setComponentFilter(e.target.value as LogComponent | 'All')}
          className="rounded-md bg-black/40 border border-red-500/30 text-red-100 text-xs px-3 py-2 focus:outline-none focus:ring-2 focus:ring-red-500/40"
        >
          {ALL_COMPONENTS.map((component) => (
            <option key={component} value={component}>{component}</option>
          ))}
        </select>
        <select
          value={severityFilter}
          onChange={(e) => setSeverityFilter(e.target.value as LogSeverity | 'All')}
          className="rounded-md bg-black/40 border border-red-500/30 text-red-100 text-xs px-3 py-2 focus:outline-none focus:ring-2 focus:ring-red-500/40"
        >
          {ALL_SEVERITIES.map((severity) => (
            <option key={severity} value={severity}>{severity}</option>
          ))}
        </select>
      </div>

      <div className="flex-1 overflow-y-auto space-y-3 pr-2">
        {filteredLogs.length === 0 ? (
          <div className="text-center text-sm text-red-200/60 py-10 border border-dashed border-red-900/40 rounded-lg">
            No operational logs for the selected filters.
          </div>
        ) : (
          filteredLogs.map((log) => (
            <div key={log.id} className="rounded-lg border border-red-900/40 bg-black/30 p-4 space-y-2">
              <div className="flex items-center justify-between gap-3">
                <div className="flex items-center gap-3">
                  <span className="text-[10px] px-2 py-0.5 rounded uppercase tracking-wider bg-red-500/20 text-red-300 border border-red-500/40">
                    {log.severity}
                  </span>
                  <span className="text-xs uppercase tracking-wider text-red-200/70">{log.component}</span>
                </div>
                <span className="text-[11px] text-red-200/60 font-mono">{log.timestamp}</span>
              </div>
              <p className="text-sm text-red-100">{log.message}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default OperationalLogsPanel;

