   * Invokes: sentry_resolve_event
   */
  async resolveEvent(eventId: string, resolution: string): Promise<BridgeResponse<SentrySecurityEvent>> {
    try {
      const resolved = await invoke<SentrySecurityEvent>('sentry_resolve_event', {
        eventId,
        resolution,
      });

      const index = this.events.findIndex(e => e.event_id === eventId);
      if (index !== -1) {
        this.events[index] = resolved;
      }

      this.notifySubscribers({ type: 'event_resolved', data: resolved });

      return { success: true, data: resolved };
