639:             <input type="text" 
640:                    class="search-input" 
641:                    placeholder="Search events..." 
642:                    value="${this.searchTerm}"
643:                    oninput="setSearch(this.value)">
644:         </div>
645:         
646:         <div class="filter-container">
647:             ${filterButtons}
648:         </div>
649:     </div>
650:     
651:     <div class="stream">
652:         ${filteredEvents.length === 0 
653:             ? `<div class="empty">
654:                  <div class="empty-icon">Ã°Å¸â€œÂ­</div>
655:                  <div>No events yet</div>
656:                  <div style="margin-top: 8px; font-size: 10px;">
657:                     ${this.activeFilter !== 'all' || this.searchTerm 
