672:     </div>
673: 
674:     <script>
675:         const vscode = acquireVsCodeApi();
676:         let searchTimeout;
677: 
678:         function executeAction(command, args) {
679:             vscode.postMessage({ command: 'executeAction', action: command, args: args });
680:         }
681: 
682:         function openFile(file) {
683:             vscode.postMessage({ command: 'openFile', file: file });
684:         }
685: 
686:         function clearStream() {
687:             vscode.postMessage({ command: 'clearStream' });
688:         }
689: 
690:         function setFilter(filter) {
691:             vscode.postMessage({ command: 'setFilter', filter: filter });
692:         }
693: 
694:         function setSearch(search) {
695:             clearTimeout(searchTimeout);
696:             searchTimeout = setTimeout(() => {
697:                 vscode.postMessage({ command: 'setSearch', search: search });
698:             }, 300);
699:         }
700: 
701:         // Keyboard shortcuts
702:         document.addEventListener('keydown', (e) => {
703:             if (e.target.tagName === 'INPUT') {
704:                 if (e.key === 'Escape') {
705:                     e.target.value = '';
706:                     setSearch('');
707:                     e.target.blur();
708:                 }
709:                 return;
710:             }
711: 
712:             switch(e.key.toLowerCase()) {
713:                 case '/':
714:                     e.preventDefault();
715:                     document.querySelector('.search-input').focus();
716:                     break;
717:                 case 'c':
718:                     clearStream();
719:                     break;
720:                 case '?':
721:                     // Show help dialog
722:                     break;
723:             }
724:         });
725: 
726:         // Auto-scroll to top on new events
727:         const observer = new MutationObserver((mutations) => {
728:             mutations.forEach((mutation) => {
729:                 if (mutation.addedNodes.length > 0) {
730:                     const stream = document.querySelector('.stream');
731:                     if (stream) {
732:                         stream.scrollTop = 0;
733:                     }
734:                 }
735:             });
736:         });
737: 
738:         observer.observe(document.querySelector('.stream'), {
739:             childList: true,
740:             subtree: true
741:         });
742:     </script>
743: </body>
744: </html>`;
