746: 
747:     private escapeHtml(text: string): string {
748:         const map: Record<string, string> = {
749:             '&': '&amp;',
750:             '<': '&lt;',
751:             '>': '&gt;',
752:             '"': '&quot;',
753:             "'": '&#039;'
754:         };
755:         return text.replace(/[&<>"']/g, (m) => map[m]);
756:     }
757: 
758:     private formatTime(isoString: string): string {
759:         const date = new Date(isoString);
760:         return date.toLocaleTimeString('en-US', {
761:             hour: '2-digit',
762:             minute: '2-digit',
763:             second: '2-digit',
764:             hour12: false
765:         });
766:     }
