import React, { useState, useEffect, useCallback, useMemo, useRef } from 'react';
import { Rnd } from 'react-rnd';
import { Activity, Brain, Heart, Zap, Shield, GripHorizontal, ArrowLeft, RefreshCw } from '@/components/ui/HearthlinkIcons';
import { AliceBridge, BehavioralSignal } from '../../bridges/AliceBridge';
import { MetricCard } from '@/components/shared/premium/MetricCard';
import type { AliceInsight, AliceBehavioralProfile } from '../../types/agents';
import { BackToLauncher } from '../shared/BackToLauncher';

// --- Types & Interfaces ---

export interface AliceDashboardProps {
  userId?: string;
  onBackToLauncher?: () => void;
}

// MetricCard consolidated to shared component
interface LayoutItem {
  id: string;
  x: number;
  y: number;
  w: number;
  h: number;
  zIndex: number;
}

// --- Main Component ---

export const AliceDashboard: React.FC<AliceDashboardProps> = ({ userId = 'default-user', onBackToLauncher }) => {
  // Data State
  const [insights, setInsights] = useState<AliceInsight[]>([]);
  const [profile, setProfile] = useState<AliceBehavioralProfile | null>(null);
  const [signals, setSignals] = useState<BehavioralSignal[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [successMessage, setSuccessMessage] = useState<string | null>(null);

  // Layout State
  const [layout, setLayout] = useState<LayoutItem[]>([
    { id: 'focus', x: 20, y: 80, w: 300, h: 160, zIndex: 10 },
    { id: 'stress', x: 340, y: 80, w: 300, h: 160, zIndex: 10 },
    { id: 'load', x: 660, y: 80, w: 300, h: 160, zIndex: 10 },
    { id: 'energy', x: 980, y: 80, w: 300, h: 160, zIndex: 10 },
    { id: 'analysis', x: 20, y: 260, w: 800, h: 400, zIndex: 10 },
    { id: 'interventions', x: 840, y: 260, w: 440, h: 400, zIndex: 10 },
  ]);
  const [draggingId, setDraggingId] = useState<string | null>(null);
  const maxZIndexRef = useRef(10);

  // --- Data Logic ---

  const createBridge = useCallback(() => new AliceBridge({ userId }), [userId]);

  const loadAliceData = useCallback(async () => {
    setError(null);
    try {
      // Don't set loading true on refresh to avoid flickering if we have data
      if (insights.length === 0) setLoading(true);
      
      const bridge = createBridge();
      await bridge.initialize();
      const [insightsResponse, profileResponse, signalsResponse] = await Promise.all([
        bridge.getInsights(),
        bridge.getProfile(),
        bridge.getSignals(10),
      ]);

      if (insightsResponse.success && insightsResponse.data) {
        setInsights(insightsResponse.data);
      } else if (!insightsResponse.success) {
        console.warn('Failed to load insights:', insightsResponse.error);
        // Don't block UI on partial failure
      }

      if (profileResponse.success && profileResponse.data) {
        setProfile(profileResponse.data);
      } else if (!profileResponse.success) {
        console.warn('Failed to load profile:', profileResponse.error);
      }

      if (signalsResponse.success && signalsResponse.data) {
        setSignals(signalsResponse.data);
      } else if (!signalsResponse.success) {
        console.warn('Failed to load signals:', signalsResponse.error);
      }

    } catch (err: unknown) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to load Alice data.';
      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  }, [createBridge, insights.length]);

  useEffect(() => {
    loadAliceData();
  }, [loadAliceData]);

  useEffect(() => {
    if (successMessage) {
      const timer = setTimeout(() => setSuccessMessage(null), 3000);
      return () => clearTimeout(timer);
    }
  }, [successMessage]);

  const handleApplyRecommendation = async (insight: AliceInsight) => {
    setError(null);
    try {
      const bridge = createBridge();
      await bridge.initialize();
      const response = await bridge.generateInsights({
        applied_recommendation: insight.observation,
        insight_id: insight.insight_id,
      });
      if (response.success) {
        setSuccessMessage(`Recommendation applied successfully!`);
        loadAliceData();
      } else {
        setError(response.error || 'Failed to apply recommendation.');
      }
    } catch (err: unknown) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to apply recommendation.';
      setError(errorMessage);
    }
  };

  const handleFeedback = async (insight: AliceInsight, helpful: boolean) => {
    setError(null);
    try {
      const bridge = createBridge();
      await bridge.initialize();
      if (!helpful) {
        const response = await bridge.dismissInsight(insight.insight_id);
        if (response.success) {
          setSuccessMessage(`Feedback recorded. Insight dismissed.`);
          loadAliceData();
        } else {
          setError(response.error || 'Failed to record feedback.');
        }
      } else {
        setSuccessMessage(`Feedback recorded as helpful. Thank you!`);
      }
    } catch (err: unknown) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to record feedback.';
      setError(errorMessage);
    }
  };
 
  const handleSimulatePulse = async () => {
    setError(null);
    try {
      const bridge = createBridge();
      await bridge.initialize();
      
      // Check for consent first
      const consentCheck = await bridge.checkConsent(['behavioral_insights']);
      if (consentCheck.success && !consentCheck.data) {
        console.log('[AliceDashboard] Consent missing, recording default consent...');
        await bridge.recordConsent(true, ['behavioral_insights'], 'Auto-granted for pulse simulation');
      }
      
      // Generate some semi-random data to see the math work
      const obs = {
        transcript: "Working on code review and some documentation.",
        completed_tasks: Math.floor(Math.random() * 5),
        total_tasks: 5,
        interruptions: Math.floor(Math.random() * 3),
        context_switches: Math.floor(Math.random() * 4),
        sentiment: (Math.random() * 2) - 1.0, // -1 to 1
        self_reported_stress: Math.floor(Math.random() * 100),
        self_reported_energy: Math.floor(Math.random() * 100),
        focus_rating: Math.floor(Math.random() * 100),
      };
      
      const response = await bridge.recordObservation(obs);
      if (response.success) {
        setSuccessMessage(`Observation recorded! Pulse updated.`);
        loadAliceData();
      } else {
        setError(response.error || 'Failed to record observation.');
      }
    } catch (err: unknown) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to simulate pulse.';
      setError(errorMessage);
    }
  };
 
  // --- Layout Logic ---

  const updateLayout = useCallback((id: string, updates: Partial<LayoutItem>) => {
    setLayout(prev => prev.map(item => 
      item.id === id ? { ...item, ...updates } : item
    ));
  }, []);

  const handleDragStart = useCallback((id: string) => {
    setDraggingId(id);
    const newZIndex = ++maxZIndexRef.current;
    updateLayout(id, { zIndex: newZIndex });
  }, [updateLayout]);

  const handleDragEnd = useCallback(() => {
    setDraggingId(null);
  }, []);

  const createDragHandlers = useCallback((id: string) => ({
    onDragStart: () => handleDragStart(id),
    onDragStop: (e: any, d: { x: number; y: number }) => {
      updateLayout(id, { x: d.x, y: d.y });
      handleDragEnd();
    },
    onResizeStart: () => handleDragStart(id),
    onResizeStop: (e: any, direction: any, ref: HTMLElement, delta: any, position: { x: number; y: number }) => {
      updateLayout(id, { 
        w: parseInt(ref.style.width), 
        h: parseInt(ref.style.height),
        x: position.x,
        y: position.y
      });
      handleDragEnd();
    },
  }), [handleDragStart, handleDragEnd, updateLayout]);

  // --- Widget Content ---

  const widgetContent = useMemo(() => {
    // Latest signal data
    const latestSignal = signals.length > 0 ? signals[0] : null;
    
    // Derived or raw metrics
    const focusScore = latestSignal 
      ? Math.round(latestSignal.focus)
      : profile?.attention_patterns?.focus_duration_avg 
        ? Math.round(profile.attention_patterns.focus_duration_avg / 60 * 10) / 10 
        : 0;
    
    const stressLevel = latestSignal ? Math.round(latestSignal.stress) : 0;
    const cognitiveLoad = latestSignal 
      ? latestSignal.cognitive_load > 75 ? "High" : latestSignal.cognitive_load > 45 ? "Medium" : "Low"
      : "Low";
    const engagementScore = latestSignal ? Math.round(latestSignal.engagement) : 0;

    return {
      focus: (
        <MetricCard 
          title="Focus Score" 
          value={focusScore.toString()} 
          subtitle={focusScore >= 75 ? "Flow State" : focusScore >= 45 ? "Stable" : "Distracted"} 
          accentColor={focusScore >= 75 ? "violet" : focusScore >= 45 ? "teal" : "rose"} 
          icon={<Brain size={20} />}
          active={focusScore >= 75}
          className={draggingId === 'focus' ? 'cursor-grabbing scale-105' : ''}
        />
      ),
      stress: (
        <MetricCard 
          title="Stress Level" 
          value={`${stressLevel}%`} 
          subtitle={stressLevel >= 75 ? "High Risk" : stressLevel >= 45 ? "Elevated" : "Optimal"} 
          accentColor={stressLevel >= 75 ? "rose" : stressLevel >= 45 ? "gold" : "teal"} 
          icon={<Heart size={20} />}
          active={stressLevel >= 75}
          className={draggingId === 'stress' ? 'cursor-grabbing scale-105' : ''}
        />
      ),
      load: (
        <MetricCard 
          title="Cognitive Load" 
          value={cognitiveLoad} 
          subtitle={cognitiveLoad === "High" ? "Critical" : cognitiveLoad === "Medium" ? "Busy" : "Fluent"} 
          accentColor={cognitiveLoad === "High" ? "rose" : cognitiveLoad === "Medium" ? "gold" : "violet"} 
          icon={<Activity size={20} />}
          active={cognitiveLoad === "High"}
          className={draggingId === 'load' ? 'cursor-grabbing scale-105' : ''}
        />
      ),
      energy: (
        <MetricCard 
          title="Engagement" 
          value={`${engagementScore}%`} 
          subtitle={engagementScore >= 75 ? "Peak" : engagementScore >= 45 ? "Active" : "Low"} 
          accentColor={engagementScore >= 75 ? "cyan" : engagementScore >= 45 ? "teal" : "teal"} 
          icon={<Zap size={20} />}
          active={engagementScore >= 75}
          className={draggingId === 'energy' ? 'cursor-grabbing scale-105' : ''}
        />
      ),
      analysis: (
        <div className="w-full h-full bg-slate-900/80 backdrop-blur-xl border border-violet-500/20 rounded-xl overflow-hidden flex flex-col shadow-[0_0_30px_rgba(0,0,0,0.3)]">
          <div className="h-10 bg-white/5 border-b border-white/5 flex items-center justify-between px-4 drag-handle cursor-move">
            <div className="flex items-center gap-2 text-violet-400">
              <Activity size={16} />
              <span className="text-xs font-bold uppercase tracking-widest font-orbitron-force">Live Analysis</span>
            </div>
            <GripHorizontal size={16} className="text-white/20" />
          </div>
          <div className="flex-1 p-4 overflow-y-auto scrollbar-thin scrollbar-thumb-violet-500/20 scrollbar-track-transparent">
             <div className="mb-4">
               <button 
                 onClick={handleSimulatePulse}
                 className="w-full py-2 bg-violet-600/20 hover:bg-violet-600/40 text-violet-300 border border-violet-500/30 rounded-lg text-xs font-bold uppercase tracking-wider transition-all flex items-center justify-center gap-2"
               >
                 <Zap size={14} className="animate-pulse" />
                 Simulate Behavioral Pulse
               </button>
             </div>
 
             {insights.length === 0 ? (
               <div className="flex items-center justify-center h-full text-slate-500 italic text-sm">
                 Waiting for behavioral telemetry...
               </div>
             ) : (
               <div className="space-y-3">
                 {insights.map(insight => (
                   <div key={insight.insight_id} className="p-3 rounded bg-white/5 border border-white/10 hover:bg-white/10 transition-colors">
                     <div className="flex justify-between items-start mb-1">
                       <span className="text-xs font-bold text-violet-300 uppercase">{insight.category}</span>
                       <span className="text-[10px] text-slate-500">{new Date(insight.timestamp).toLocaleTimeString()}</span>
                     </div>
                     <p className="text-sm text-slate-300 mb-2">{insight.observation}</p>
                     <div className="flex gap-2 justify-end">
                       <button 
                         onClick={() => handleFeedback(insight, true)}
                         className="text-[10px] px-2 py-1 rounded bg-green-500/10 text-green-400 hover:bg-green-500/20 transition-colors"
                       >
                         Helpful
                       </button>
                       <button 
                         onClick={() => handleFeedback(insight, false)}
                         className="text-[10px] px-2 py-1 rounded bg-red-500/10 text-red-400 hover:bg-red-500/20 transition-colors"
                       >
                         Dismiss
                       </button>
                     </div>
                   </div>
                 ))}
               </div>
             )}
          </div>
        </div>
      ),
      interventions: (
        <div className="w-full h-full bg-slate-900/80 backdrop-blur-xl border border-teal-500/20 rounded-xl overflow-hidden flex flex-col shadow-[0_0_30px_rgba(0,0,0,0.3)]">
          <div className="h-10 bg-white/5 border-b border-white/5 flex items-center justify-between px-4 drag-handle cursor-move">
            <div className="flex items-center gap-2 text-teal-400">
              <Shield size={16} />
              <span className="text-xs font-bold uppercase tracking-widest font-orbitron-force">Interventions</span>
            </div>
            <GripHorizontal size={16} className="text-white/20" />
          </div>
          <div className="flex-1 p-4 space-y-3 overflow-y-auto scrollbar-thin scrollbar-thumb-teal-500/20 scrollbar-track-transparent">
            {/* Recommendations from Insights */}
            {insights.filter(i => i.recommended_actions && i.recommended_actions.length > 0).map(insight => (
              <div key={`rec-${insight.insight_id}`} className="p-4 bg-teal-500/10 border border-teal-500/20 rounded-lg group hover:bg-teal-500/20 transition-colors">
                 <h3 className="text-teal-400 font-bold mb-1 text-sm group-hover:text-teal-300">Recommendation</h3>
                 <p className="text-[10px] text-slate-400 mb-2">{insight.observation}</p>
                 <button 
                   onClick={() => handleApplyRecommendation(insight)}
                   className="w-full py-1.5 bg-teal-600 hover:bg-teal-500 text-white rounded text-[10px] uppercase tracking-wider font-bold transition-colors shadow-lg shadow-teal-900/20"
                 >
                   Apply
                 </button>
              </div>
            ))}

            {/* Static Interventions */}
            <div className="p-4 bg-violet-500/10 border border-violet-500/20 rounded-lg group hover:bg-violet-500/20 transition-colors cursor-pointer">
              <h3 className="text-violet-400 font-bold mb-1 text-sm group-hover:text-violet-300">Focus Timer</h3>
              <p className="text-[10px] text-slate-400 mb-3">Initiate a 25-minute deep work session.</p>
              <button className="w-full py-1.5 bg-violet-600 hover:bg-violet-500 text-white rounded text-[10px] uppercase tracking-wider font-bold transition-colors shadow-lg shadow-violet-900/20">
                Start Session
              </button>
            </div>
          </div>
        </div>
      ),
    };
  }, [profile, insights, draggingId, handleFeedback, handleApplyRecommendation]);

  // --- Render ---

  if (loading && insights.length === 0 && !profile) {
    return (
      <div className="w-full h-full p-6 flex items-center justify-center overflow-hidden relative">
        <div className="absolute inset-0 bg-[radial-gradient(circle_at_50%_50%,rgba(139,92,246,0.1),transparent_70%)]" />
        <div className="text-center relative z-10">
          <div className="relative inline-block mb-6">
            <div className="absolute inset-0 rounded-full animate-pulse bg-violet-500/20 blur-xl" />
            <img
              src="/assets/Alice.png"
              alt="Alice"
              className="relative w-24 h-24 object-contain animate-pulse drop-shadow-[0_0_15px_rgba(139,92,246,0.5)]"
            />
          </div>
          <div className="text-violet-300 text-lg font-medium font-orbitron-force tracking-widest">INITIALIZING ALICE...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="w-full h-full relative overflow-hidden font-sans text-slate-200">
      {/* Background Ambient Glow */}
      <div className="absolute top-0 left-0 w-full h-full bg-[radial-gradient(circle_at_50%_50%,rgba(139,92,246,0.05),transparent_70%)] pointer-events-none" />
      
      {/* Header / Controls */}
      <div className="absolute top-4 left-4 z-50 flex items-center gap-4">
        {onBackToLauncher && (
          <button 
            onClick={onBackToLauncher}
            className="flex items-center gap-2 px-4 py-2 rounded-lg bg-slate-900/50 backdrop-blur-md border border-white/10 hover:bg-white/10 transition-colors text-sm font-medium text-slate-300"
          >
            <ArrowLeft size={16} />
            Back
          </button>
        )}
        <button 
          onClick={loadAliceData}
          className="p-2 rounded-lg bg-slate-900/50 backdrop-blur-md border border-white/10 hover:bg-white/10 transition-colors text-slate-300"
          title="Refresh Data"
          aria-label="Refresh Data"
        >
          <RefreshCw size={16} className={loading ? 'animate-spin' : ''} />
        </button>
      </div>

      {/* Toasts */}
      {successMessage && (
        <div className="fixed top-4 right-4 z-50 px-6 py-3 rounded-lg bg-teal-500/20 border border-teal-500/50 text-teal-300 backdrop-blur-xl shadow-lg animate-in slide-in-from-top">
          {successMessage}
        </div>
      )}
      {error && (
        <div className="fixed top-4 right-4 z-50 px-6 py-3 rounded-lg bg-red-500/20 border border-red-500/50 text-red-300 backdrop-blur-xl shadow-lg animate-in slide-in-from-top">
          {error}
          <button onClick={() => setError(null)} className="ml-4 hover:text-white underline">Dismiss</button>
        </div>
      )}

      {/* Draggable Canvas */}
      <div className="relative w-full h-screen overflow-hidden">
        {layout.map(item => {
          const handlers = createDragHandlers(item.id);
          return (
            <Rnd
              key={item.id}
              size={{ width: item.w, height: item.h }}
              position={{ x: item.x, y: item.y }}
              onDragStart={handlers.onDragStart}
              onDragStop={handlers.onDragStop}
              onResizeStart={handlers.onResizeStart}
              onResizeStop={handlers.onResizeStop}
              bounds="parent"
              dragHandleClassName="drag-handle"
              className={`transition-shadow ${draggingId === item.id ? 'z-50 shadow-2xl' : ''}`}
              style={{ zIndex: item.zIndex }}
              enableResizing={{
                top: false, right: true, bottom: true, left: false,
                topRight: false, bottomRight: true, bottomLeft: false, topLeft: false,
              }}
              resizeHandleClasses={{
                right: "bg-purple-500/30 hover:bg-purple-500/50 w-1 cursor-ew-resize",
                bottom: "bg-purple-500/30 hover:bg-purple-500/50 h-1 cursor-ns-resize",
                bottomRight: "bg-purple-500/50 hover:bg-purple-500 w-3 h-3 cursor-nwse-resize rounded-full"
              }}
            >
              <div className="w-full h-full">
                {widgetContent[item.id as keyof typeof widgetContent]}
              </div>
            </Rnd>
          );
        })}
      </div>
    </div>
  );
};

export default AliceDashboard;

