/**
 * Tauri Command Mocks for E2E Testing
 *
 * Provides mock implementations of all Tauri IPC commands to enable
 * E2E testing without requiring the full Rust backend.
 *
 * Usage:
 * ```typescript
 * import { mockTauriCommands, resetMocks } from './mocks/tauri-commands';
 *
 * test.beforeEach(async ({ page }) => {
 *   await mockTauriCommands(page);
 * });
 *
 * test.afterEach(async () => {
 *   resetMocks();
 * });
 * ```
 */

import { Page } from '@playwright/test';

// Mock data storage
let mockStorage = {
  tasks: [] as any[],
  projects: [] as any[],
  memories: [] as any[],
  chatHistory: [] as any[],
  cliHistory: [] as any[],
  calendarEvents: [] as any[],
  kanbanCards: [] as any[],
  risks: [] as any[],
  entities: [] as any[],
  relationships: [] as any[],
  emailMessages: [] as any[],
};

/**
 * Reset all mock data to initial state
 */
export function resetMocks() {
  mockStorage = {
    tasks: [],
    projects: [],
    memories: [],
    chatHistory: [],
    cliHistory: [],
    calendarEvents: [],
    kanbanCards: [],
    risks: [],
    entities: [],
    relationships: [],
    emailMessages: [],
  };
}

/**
 * Seed mock data with sample content
 */
export function seedMockData(data: Partial<typeof mockStorage>) {
  mockStorage = { ...mockStorage, ...data };
}

/**
 * Mock Tauri command handlers
 */
const commandHandlers = {
  // ========================================
  // Alden Commands
  // ========================================

  // Chat commands
  alden_chat_send: async (args: any) => {
    const message = {
      id: `msg_${Date.now()}`,
      content: args.message,
      role: 'user',
      timestamp: Date.now(),
    };
    mockStorage.chatHistory.push(message);

    // Mock AI response
    setTimeout(() => {
      mockStorage.chatHistory.push({
        id: `msg_${Date.now() + 1}`,
        content: `Mock response to: ${args.message}`,
        role: 'assistant',
        timestamp: Date.now(),
      });
    }, 500);

    return message;
  },

  alden_chat_history: async () => {
    return mockStorage.chatHistory;
  },

  alden_chat_clear: async () => {
    mockStorage.chatHistory = [];
    return { success: true };
  },

  alden_health_check: async () => {
    return { status: 'ok', version: '1.0.0' };
  },

  // CLI commands
  alden_cli_execute: async (args: any) => {
    const result = {
      id: `cmd_${Date.now()}`,
      command: args.command || args.request?.command,
      output: `Mock output for: ${args.command || args.request?.command}`,
      exitCode: 0,
      timestamp: Date.now(),
    };
    mockStorage.cliHistory.push(result);
    return result;
  },

  alden_cli_history: async () => {
    return mockStorage.cliHistory;
  },

  alden_cli_custom_commands_list: async () => {
    return [
      { id: 'cmd_1', command: 'hello', description: 'Says hello', response_template: 'Hello there!' }
    ];
  },

  alden_cli_custom_commands_create: async (args: any) => {
    return {
      id: `custom_cmd_${Date.now()}`,
      ...args.request
    };
  },

  alden_cli_custom_commands_delete: async () => {
    return { success: true };
  },

  // Calendar commands
  alden_calendar_sync: async (args: any) => {
    return {
      success: true,
      provider: args.provider,
      eventsCount: 10,
      message: `Synced ${args.provider} calendar`,
    };
  },

  alden_calendar_get_availability: async (args: any) => {
    return {
      freeBusy: [
        { start: '2025-11-16T09:00:00Z', end: '2025-11-16T10:00:00Z', status: 'busy' },
        { start: '2025-11-16T14:00:00Z', end: '2025-11-16T15:00:00Z', status: 'busy' },
      ],
    };
  },

  // Task commands
  alden_tasks_create: async (args: any) => {
    const task = {
      id: `task_${Date.now()}`,
      ...args.request,
      createdAt: Date.now(),
      status: 'pending',
    };
    mockStorage.tasks.push(task);
    return task;
  },

  alden_tasks_list: async () => {
    return mockStorage.tasks;
  },

  // Email commands
  alden_email_accounts_get: async (args: any) => {
    return [
      {
        account_id: 'acc_1',
        email_address: 'alden@example.com',
        display_name: 'Alden',
        protocol: 'imap',
        server: 'imap.example.com',
        port: 993,
        ssl_enabled: true,
        last_sync: new Date().toISOString(),
        status: 'active',
      }
    ];
  },

  alden_email_messages_get: async (args: any) => {
    // If mockStorage has explicit messages, return them (for dynamic scenarios)
    // Otherwise return some defaults if none exist
    if (mockStorage.emailMessages && mockStorage.emailMessages.length > 0) {
      return mockStorage.emailMessages;
    }
    
    // Default messages if storage is empty
    return [
      {
        message_id: 'msg_1',
        account_id: 'acc_1',
        from: 'boss@example.com',
        to: ['alden@example.com'],
        subject: 'Project Update',
        body: 'Please provide an update on the Q1 roadmap.',
        received_date: new Date(Date.now() - 3600000).toISOString(),
        read: false,
        starred: true,
        folder: 'Inbox',
      },
      {
        message_id: 'msg_2',
        account_id: 'acc_1',
        from: 'newsletter@tech.com',
        to: ['alden@example.com'],
        subject: 'Weekly Tech News',
        body: 'Check out the latest tech trends...',
        received_date: new Date(Date.now() - 86400000).toISOString(),
        read: true,
        starred: false,
        folder: 'Inbox',
      }
    ];
  },

  alden_email_send_create: async (args: any) => {
    return {
      message_id: `msg_sent_${Date.now()}`,
      status: 'sent',
      timestamp: new Date().toISOString()
    };
  },

  // ========================================
  // Vault Commands
  // ========================================

  vault_memory_store: async (args: any) => {
    const memory = {
      id: `mem_${Date.now()}`,
      ...args,
      created_at: Date.now(),
      access_count: 0,
    };
    mockStorage.memories.push(memory);
    return memory;
  },

  vault_memories_search_semantic: async (args: any) => {
    const query = args.query?.toLowerCase() || '';
    
    // Return empty results for 'nonexistent' queries to simulate no matches
    if (query.includes('nonexistent')) {
      return [];
    }

    // Mock semantic search with similarity scores
    return mockStorage.memories.map((mem, idx) => ({
      ...mem,
      similarity_score: Math.random() * 0.7 + 0.3, // 0.3-1.0
    })).slice(0, args.limit || 10);
  },

  vault_memories_get: async (args: any) => {
    return mockStorage.memories.filter((mem) => {
      if (args.status && mem.status !== args.status) return false;
      return true;
    }).slice(0, args.limit || 100);
  },

  vault_entity_create: async (args: any) => {
    const entity = {
      id: `entity_${Date.now()}`,
      ...args,
      created_at: Date.now(),
    };
    mockStorage.entities.push(entity);
    return entity;
  },

  vault_entities_find: async (args: any) => {
    return mockStorage.entities.filter((e) => {
      if (args.entityType && e.entity_type !== args.entityType) return false;
      return true;
    });
  },

  vault_relationship_create: async (args: any) => {
    const rel = {
      id: `rel_${Date.now()}`,
      ...args,
      created_at: Date.now(),
    };
    mockStorage.relationships.push(rel);
    return rel;
  },

  vault_relationships_get: async () => {
    return mockStorage.relationships;
  },

  vault_graph_stats: async () => {
    return {
      entity_types: { concept: 5, person: 3, project: 2 },
      relationship_types: { related_to: 8, influences: 2 },
      total_entities: mockStorage.entities.length,
      total_relationships: mockStorage.relationships.length,
    };
  },

  // ========================================
  // Core Commands (Project Management)
  // ========================================

  core_project_create: async (args: any) => {
    const project = {
      id: `proj_${Date.now()}`,
      ...args,
      createdAt: Date.now(),
      status: 'active',
    };
    mockStorage.projects.push(project);
    return project;
  },

  core_projects_list: async () => {
    return mockStorage.projects;
  },

  core_kanban_columns_get: async () => {
    return [
      { id: 'todo', title: 'To Do', position: 0 },
      { id: 'in-progress', title: 'In Progress', position: 1 },
      { id: 'done', title: 'Done', position: 2 },
    ];
  },

  core_kanban_cards_get: async () => {
    return mockStorage.kanbanCards;
  },

  core_kanban_card_create: async (args: any) => {
    const card = {
      id: `card_${Date.now()}`,
      ...args,
      createdAt: Date.now(),
    };
    mockStorage.kanbanCards.push(card);
    return card;
  },

  core_kanban_card_move: async (args: any) => {
    const card = mockStorage.kanbanCards.find((c) => c.id === args.cardId);
    if (card) {
      card.columnId = args.targetColumnId;
      card.position = args.position;
    }
    return card;
  },

  core_risk_create: async (args: any) => {
    const risk = {
      id: `risk_${Date.now()}`,
      ...args,
      createdAt: Date.now(),
    };
    mockStorage.risks.push(risk);
    return risk;
  },

  core_risks_list: async () => {
    return mockStorage.risks;
  },

  // ========================================
  // Core Conference Commands
  // ========================================

  core_conference_create: async (args: any) => {
    const conference = {
      conference_id: `conf_${Date.now()}`,
      title: args.title,
      purpose: args.purpose,
      conference_type: args.conferenceType || args.conference_type || 'brainstorm',
      state: 'active',
      participants: args.participants || [],
      created_at: new Date().toISOString(),
      owner_id: args.userId || args.user_id || 'test_user_123',
    };
    return conference;
  },

  core_conferences_list: async () => {
    return [
      {
        conference_id: 'conf_001',
        title: 'Security Review',
        purpose: 'Review Q4 security posture',
        conference_type: 'review',
        state: 'active',
        participants: [
          { agent_id: 'alden', role: 'facilitator' },
          { agent_id: 'sentry', role: 'contributor' },
        ],
        created_at: new Date().toISOString(),
        owner_id: 'test_user_123',
      },
    ];
  },

  core_conference_pause: async (args: any) => {
    return {
      conference_id: args.conference_id,
      state: 'paused',
      paused_at: new Date().toISOString(),
    };
  },

  core_conference_resume: async (args: any) => {
    return {
      conference_id: args.conference_id,
      state: 'active',
      resumed_at: new Date().toISOString(),
    };
  },

