      },
      learning_preferences: {
        preferred_formats: ['visual', 'interactive'],
        comprehension_pace: 'moderate',
        feedback_style: 'constructive',
      },
    };
  }

  /**
   * Get userId from auth context (if available)
   * Falls back to 'default_user' for backwards compatibility
   */
  private getUserId(): string {
    return this.userId || (window as unknown as { __hearthlink_userId?: string }).__hearthlink_userId || 'default_user';
  }

  /**
   * Helper to wrap requests with authentication token
   */
  private createAuthenticatedRequest<T>(payload: T): { auth_token: string; payload: T } {
    return {
      auth_token: `demo_token_${this.getUserId()}`,
      payload,
    };
  }

  /**
   * Initialize Alice bridge and load behavioral profile
   */
  protected async onInitialize(): Promise<void> {
    try {
