   */
  protected async onDestroy(): Promise<void> {
    this.profile = null;
    this.insights = [];
  }

  /**
   * Get props for Alice React component
   */
  async getProps(): Promise<AliceProps> {
    if (!this.profile) {
      console.warn('[AliceBridge.getProps] WARNING - Behavioral profile not loaded, using fallback');
      // Fallback to default profile to prevent UI crash
      this.profile = {
        user_id: this.userId || 'default_user',
