/**
 * Shared type definitions for FailSafe extension
 *
 * These types are used across Genesis, QoreLogic, and Sentinel components.
 */

// =============================================================================
// RISK GRADING
// =============================================================================

export type RiskGrade = "L1" | "L2" | "L3";

export interface RiskClassification {
  grade: RiskGrade;
  confidence: number;
  triggers: string[];
  reasoning: string;
}

// =============================================================================
// TRUST DYNAMICS
// =============================================================================

export type TrustStage = "CBT" | "KBT" | "IBT";

export interface TrustScore {
  did: string;
  score: number;
  stage: TrustStage;
  influenceWeight: number;
  isProbationary: boolean;
  verificationsCompleted: number;
  lastUpdated: string;
}

export interface TrustUpdate {
  did: string;
  previousScore: number;
  newScore: number;
  previousStage: TrustStage;
  newStage: TrustStage;
  reason: string;
  timestamp: string;
}

// =============================================================================
// AGENT IDENTITY
// =============================================================================

export type PersonaType = "scrivener" | "sentinel" | "judge" | "overseer";

export interface AgentIdentity {
  did: string;
  persona: PersonaType;
  publicKey: string;
  trustScore: number;
  trustStage: TrustStage;
  isQuarantined: boolean;
  verificationsCompleted: number;
  createdAt: string;
}

// =============================================================================
// SENTINEL TYPES
// =============================================================================

export type SentinelMode = "heuristic" | "llm-assisted" | "hybrid";
export type OperationalMode = "normal" | "lean" | "surge" | "safe";
export type VerdictDecision =
  | "PASS"
  | "WARN"
  | "BLOCK"
  | "ESCALATE"
  | "QUARANTINE";

export interface SentinelStatus {
  running: boolean;
  mode: SentinelMode;
  operationalMode: OperationalMode;
  uptime: number;
  filesWatched: number;
  eventsProcessed: number;
  queueDepth: number;
  lastVerdict: SentinelVerdict | null;
  llmAvailable: boolean;
}

export interface SentinelEvent {
  id: string;
  timestamp: string;
  priority: "critical" | "high" | "normal" | "low";
  source: "file_watcher" | "agent_message" | "editor" | "mcp" | "manual";
  type: SentinelEventType;
  payload: Record<string, unknown>;
}

export type SentinelEventType =
  | "FILE_CREATED"
  | "FILE_MODIFIED"
  | "FILE_DELETED"
  | "AGENT_CLAIM"
  | "CODE_SUBMITTED"
  | "DEPENDENCY_CHANGED"
  | "SPEC_CHANGED"
  | "MANUAL_AUDIT";

export interface SentinelVerdict {
  id: string;
  eventId: string;
  timestamp: string;
  decision: VerdictDecision;
  riskGrade: RiskGrade;
  confidence: number;
