/// Get behavioral profile for user
#[tauri::command]
pub async fn alice_get_profile(
    auth: State<'_, Arc<SecureAuthenticator>>,
    request: AuthenticatedRequest<GetProfileRequest>,
    db: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
) -> Result<AliceBehavioralProfile, String> {
    let authenticated_user = require_auth!(auth, &request.auth_token);
    require_ownership!(authenticated_user, &request.payload.user_id);
    let db = db.read().await;
    let db = db.as_ref().ok_or("Database not initialized")?;

    let query = r#"
        SELECT profile_id, user_id, communication_style, attention_patterns,
               emotional_indicators, learning_preferences, created_at, updated_at
        FROM alice_behavioral_profiles
        WHERE user_id = ?
        ORDER BY updated_at DESC
        LIMIT 1
    "#;

    let result = sqlx::query_as::<_, AliceBehavioralProfileRow>(query)
        .bind(&request.payload.user_id)
        .fetch_optional(&db.read_pool)
        .await
        .map_err(|e| format!("Failed to fetch Alice profile: {}", e))?;

    match result {
        Some(row) => Ok(AliceBehavioralProfile {
            profile_id: row.profile_id,
            user_id: row.user_id,
            communication_style: row.communication_style,
            attention_patterns: row.attention_patterns,
            emotional_indicators: row.emotional_indicators,
            learning_preferences: row.learning_preferences,
            created_at: row.created_at,
            updated_at: row.updated_at,
        }),
        Option::None => {
            // Create default profile if none exists
            alice_create_default_profile(db, &request.payload.user_id).await
        }
    }
}

/// Create a default behavioral profile for new users
async fn alice_create_default_profile(
    db: &DatabaseManager,
    user_id: &str,
) -> Result<AliceBehavioralProfile, String> {
    let profile_id = format!("profile_{}", chrono::Utc::now().timestamp_millis());
    let created_at = chrono::Utc::now().to_rfc3339();

    let default_attention_patterns = serde_json::to_string(&HashMap::from([
        ("focus_duration_avg".to_string(), 45.0),
        ("peak_productivity".to_string(), 0.8),
    ]))
    .map_err(|e| e.to_string())?;

    let default_emotional_indicators = serde_json::to_string(&HashMap::from([
        ("stress_level".to_string(), 0.3),
        ("satisfaction".to_string(), 0.7),
    ]))
    .map_err(|e| e.to_string())?;

    let default_learning_preferences = serde_json::to_string(&HashMap::from([
        ("preferred_format".to_string(), "visual".to_string()),
        ("pace".to_string(), "moderate".to_string()),
    ]))
    .map_err(|e| e.to_string())?;

    let query = r#"
        INSERT INTO alice_behavioral_profiles (
            profile_id, user_id, communication_style, attention_patterns,
            emotional_indicators, learning_preferences, created_at, updated_at
        ) VALUES (?, ?, ?, ?, ?, ?, ?)
    "#;

    sqlx::query(query)
        .bind(&profile_id)
        .bind(user_id)
        .bind("analytical")
        .bind(&default_attention_patterns)
        .bind(&default_emotional_indicators)
        .bind(&default_learning_preferences)
        .bind(&created_at)
        .bind(&created_at)
        .execute(&db.write_pool)
        .await
        .map_err(|e| format!("Failed to create default Alice profile: {}", e))?;

    Ok(AliceBehavioralProfile {
        profile_id,
        user_id: user_id.to_string(),
        communication_style: "analytical".to_string(),
        attention_patterns: default_attention_patterns,
        emotional_indicators: default_emotional_indicators,
        learning_preferences: default_learning_preferences,
        created_at: created_at.clone(),
        updated_at: created_at,
    })
}

/// Update behavioral profile
#[tauri::command]
pub async fn alice_update_profile(
    auth: State<'_, Arc<SecureAuthenticator>>,
    request: AuthenticatedRequest<ProfileUpdateRequest>,
    db: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
) -> Result<AliceBehavioralProfile, String> {
    let authenticated_user = require_auth!(auth, &request.auth_token);
    require_ownership!(authenticated_user, &request.payload.user_id);
    let ProfileUpdateRequest {
        user_id,
        communication_style,
        attention_patterns,
        emotional_indicators,
        learning_preferences,
    } = request.payload;
    let db_lock = db.read().await;
    let db = db_lock.as_ref().ok_or("Database not initialized")?;

    // First get existing profile by querying directly
    let query = r#"
        SELECT profile_id, user_id, communication_style, attention_patterns,
               emotional_indicators, learning_preferences, created_at, updated_at
        FROM alice_behavioral_profiles
        WHERE user_id = ?
        ORDER BY updated_at DESC
        LIMIT 1
    "#;

    let existing = sqlx::query_as::<_, AliceBehavioralProfileRow>(query)
        .bind(&user_id)
        .fetch_optional(&db.read_pool)
        .await
        .map_err(|e| format!("Failed to fetch Alice profile: {}", e))?
        .ok_or_else(|| format!("Profile not found for user {}", user_id))?;

    let existing = AliceBehavioralProfile {
        profile_id: existing.profile_id,
        user_id: existing.user_id,
        communication_style: existing.communication_style,
        attention_patterns: existing.attention_patterns,
        emotional_indicators: existing.emotional_indicators,
        learning_preferences: existing.learning_preferences,
        created_at: existing.created_at,
        updated_at: existing.updated_at,
    };

    // Update fields if provided
    let communication_style = communication_style.unwrap_or(existing.communication_style);
    let attention_patterns = attention_patterns.unwrap_or(existing.attention_patterns);
    let emotional_indicators = emotional_indicators.unwrap_or(existing.emotional_indicators);
    let learning_preferences = learning_preferences.unwrap_or(existing.learning_preferences);

    let updated_at = chrono::Utc::now().to_rfc3339();

    let query = r#"
        UPDATE alice_behavioral_profiles
        SET communication_style = ?, attention_patterns = ?, emotional_indicators = ?,
            learning_preferences = ?, updated_at = ?
        WHERE profile_id = ?
    "#;

    sqlx::query(query)
        .bind(&communication_style)
        .bind(&attention_patterns)
        .bind(&emotional_indicators)
        .bind(&learning_preferences)
        .bind(&updated_at)
        .bind(&existing.profile_id)
        .execute(&db.write_pool)
        .await
        .map_err(|e| format!("Failed to update Alice profile: {}", e))?;

    Ok(AliceBehavioralProfile {
        profile_id: existing.profile_id,
        user_id: existing.user_id,
        communication_style,
        attention_patterns,
        emotional_indicators,
        learning_preferences,
        created_at: existing.created_at,
        updated_at,
    })
}

