    def _score_focus(self, observation: BehavioralObservation, transcript: str) -> float:
        base = 60.0
        base += 25.0 * (observation.completion_ratio - 0.5)
        base += 8.0 * self._keyword_delta(transcript, self._focus_positive, self._focus_negative)
        base -= 6.0 * observation.interruptions
        base -= 4.0 * observation.context_switches
        base += 12.0 * observation.sentiment
        if observation.focus_rating is not None:
            base = (base * 0.6) + (0.4 * observation.focus_rating)
        return _clamp(base)

    def _score_stress(self, observation: BehavioralObservation, transcript: str) -> float:
        base = 35.0
        base += 18.0 * observation.interruptions
        base += 12.0 * observation.context_switches
        base += 40.0 * max(0.0, -observation.sentiment)
        base += 12.0 * self._keyword_hits(transcript, self._stress_keywords)
        base -= 10.0 * self._keyword_hits(transcript, self._calm_keywords)
        if observation.stress_level() is not None:
            base = (base * 0.5) + (50.0 * observation.stress_level())
        return _clamp(base)

    def _score_engagement(self, observation: BehavioralObservation, transcript: str) -> float:
        base = 55.0
        base += 20.0 * observation.sentiment
        base += 10.0 * observation.completion_ratio
        base -= 6.0 * observation.interruptions
        base += 6.0 * self._keyword_delta(transcript, self._engagement_positive, self._engagement_negative)
        base += 20.0 * (observation.energy_level() - 0.5)
        return _clamp(base)

    def _score_cognitive_load(self, observation: BehavioralObservation, transcript: str) -> float:
        base = 45.0
        base += 14.0 * observation.context_switches
        base += 10.0 * observation.interruptions
        base += 20.0 * (1.0 - observation.completion_ratio)
        base += 6.0 * self._keyword_hits(transcript, self._focus_negative)
        base -= 4.0 * self._keyword_hits(transcript, self._focus_positive)
        base += 8.0 * max(0.0, -observation.sentiment)
        return _clamp(base)

    @staticmethod
    def _keyword_hits(transcript: str, keywords: Iterable[str]) -> float:
        return sum(transcript.count(keyword) for keyword in keywords)

    def _keyword_delta(
        self, transcript: str, positive_keywords: Iterable[str], negative_keywords: Iterable[str]
    ) -> float:
        return self._keyword_hits(transcript, positive_keywords) - self._keyword_hits(transcript, negative_keywords)

    def _recommendations(self, signal: BehavioralSignal) -> Tuple[List[str], List[str]]:
        recommendations: List[str] = []
        alerts: List[str] = []
        if signal.stress >= 80:
            recommendations.append("Initiate guided breathing exercise and schedule short decompression break.")
            alerts.append("escalate:alden")
        elif signal.stress >= 65:
            recommendations.append("Suggest hydration and light movement pause to reduce stress load.")

        if signal.focus <= 40:
            recommendations.append("Offer focus timer with structured break intervals and minimise notifications.")
            if "escalate:alden" not in alerts:
                alerts.append("escalate:alden")
        elif signal.focus >= 75 and signal.stress <= 50:
            recommendations.append("Celebrate sustained focus; capture successful strategy for reuse.")

        if signal.engagement <= 45:
            recommendations.append("Prompt collaborative check-in or switch to interactive task mode.")
