def _utcnow() -> datetime:
    return datetime.now(UTC)


def _clamp(value: float, minimum: float = 0.0, maximum: float = 100.0) -> float:
    return max(minimum, min(maximum, value))


def _merge_tags(existing: Sequence[str], new_tags: Iterable[str]) -> Tuple[str, ...]:
    ordered: List[str] = list(existing)
    for tag in new_tags:
        normalised = tag.strip().lower()
        if normalised and normalised not in ordered:
            ordered.append(normalised)
    return tuple(ordered)


@dataclass
class BehavioralObservation:
    """Input telemetry used to infer behavioural insights."""

    transcript: str
    completed_tasks: int = 0
    total_tasks: int = 0
    interruptions: int = 0
    context_switches: int = 0
    sentiment: float = 0.0  # -1.0 (negative) to 1.0 (positive)
    self_reported_stress: Optional[int] = None  # 0-100 scale
    self_reported_energy: Optional[int] = None  # 0-100 scale
    focus_rating: Optional[int] = None  # 0-100 self reflection
    notes: Optional[str] = None
    captured_at: datetime = field(default_factory=_utcnow)

    @property
    def completion_ratio(self) -> float:
        if self.total_tasks <= 0:
            return 0.0
        return max(0.0, min(1.0, self.completed_tasks / self.total_tasks))

    def energy_level(self) -> float:
        if self.self_reported_energy is None:
            return 0.5
        return max(0.0, min(1.0, self.self_reported_energy / 100))

    def stress_level(self) -> Optional[float]:
        if self.self_reported_stress is None:
            return None
        return max(0.0, min(1.0, self.self_reported_stress / 100))

    def to_payload(self) -> Dict[str, object]:
        return {
            "transcript": self.transcript,
            "completed_tasks": self.completed_tasks,
            "total_tasks": self.total_tasks,
            "interruptions": self.interruptions,
            "context_switches": self.context_switches,
            "sentiment": self.sentiment,
            "self_reported_stress": self.self_reported_stress,
            "self_reported_energy": self.self_reported_energy,
            "focus_rating": self.focus_rating,
            "notes": self.notes,
