+
+export type VoiceSynthesisResponse = {
+  success: boolean;
+  audio_base64?: string;
+  format?: string;
+  sample_rate?: number;
+  engine?: string;
+  voice_id?: string;
+  error?: string;
+};
+
+const buildAuth = <T extends { user_id: string }>(payload: T) => ({
+  auth_token: `demo_token_${payload.user_id}`,
+  ...payload,
+});
+
+export const voiceStartListening = async (request: VoiceStartListeningRequest): Promise<string> => {
+  return invoke('voice_start_listening', buildAuth(request));
+};
+
+export const voiceStop = async (request: VoiceStopRequest): Promise<string> => {
+  return invoke('voice_stop', buildAuth(request));
+};
+
+export const voiceSynthesize = async (request: VoiceSynthesisRequest): Promise<VoiceSynthesisResponse> => {
+  return invoke('voice_synthesize', buildAuth(request));
+};
