/**
 * Todoist API Integration
 *
 * Real API integration for Todoist task synchronization.
 * Implements OAuth flow, task CRUD operations, and sync conflict handling.
 *
 * @version 1.0.0
 * @date 2026-01-23
 */

import type { Task } from '../../types/agents';

// ============================================================================
// Types and Interfaces
// ============================================================================

export interface TodoistCredentials {
  accessToken: string;
  refreshToken?: string;
  clientId?: string;
  clientSecret?: string;
}

export interface TodoistTask {
  id: string;
  content: string;
  description?: string;
  due?: {
    date?: string;
    datetime?: string;
    is_recurring: boolean;
  };
  priority: number; // 1-4 (4=urgent, 1=normal)
  completed: boolean;
