  is_shared: boolean;
  is_favorite: boolean;
}

export interface TodoistSyncResponse {
  sync_token: string;
  full_sync: boolean;
  items: TodoistTask[];
  projects: TodoistProject[];
  notes: any[];
  labels: any[];
}

export interface TodoistAPIError {
  error: string;
  error_code?: number;
  error_extra?: any;
}

// ============================================================================
// Todoist API Client
// ============================================================================

/**
 * Todoist API Client
 * Handles all interactions with the Todoist REST API
 */
export class TodoistAPI {
  private credentials: TodoistCredentials;
  private baseUrl = 'https://api.todoist.com/rest/v2';
  private syncUrl = 'https://api.todoist.com/sync/v9';
  private rateLimitDelay = 1000; // 1 second between requests
  private lastRequestTime = 0;

  constructor(credentials: TodoistCredentials) {
    this.credentials = credentials;
  }

  /**
   * Enforce rate limiting
   */
  private async enforceRateLimit(): Promise<void> {
    const now = Date.now();
    const timeSinceLastRequest = now - this.lastRequestTime;
    
    if (timeSinceLastRequest < this.rateLimitDelay) {
      await new Promise(resolve => 
        setTimeout(resolve, this.rateLimitDelay - timeSinceLastRequest)
      );
    }
    
    this.lastRequestTime = Date.now();
  }

  /**
   * Make authenticated API request
   */
  private async request<T>(
    endpoint: string,
    options: RequestInit = {}
  ): Promise<T> {
    await this.enforceRateLimit();

    const url = `${this.baseUrl}${endpoint}`;
    const response = await fetch(url, {
      ...options,
      headers: {
        'Authorization': `Bearer ${this.credentials.accessToken}`,
        'Content-Type': 'application/json',
        ...options.headers,
      },
    });

    if (!response.ok) {
      const error: TodoistAPIError = await response.json().catch(() => ({
        error: response.statusText,
      }));
      throw new Error(`Todoist API error: ${error.error}`);
    }

    return response.json();
  }

  /**
   * Sync endpoint request (for full sync operations)
   */
  private async syncRequest<T>(
    endpoint: string,
    body: Record<string, unknown>
  ): Promise<T> {
    await this.enforceRateLimit();

    const url = `${this.syncUrl}${endpoint}`;
    const response = await fetch(url, {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${this.credentials.accessToken}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(body),
    });

    if (!response.ok) {
      const error: TodoistAPIError = await response.json().catch(() => ({
        error: response.statusText,
      }));
      throw new Error(`Todoist Sync error: ${error.error}`);
    }

    return response.json();
  }

  /**
   * Get all tasks
   */
  async getAllTasks(): Promise<TodoistTask[]> {
    return this.request<TodoistTask[]>('/tasks');
  }

  /**
   * Get active tasks (not completed)
   */
  async getActiveTasks(): Promise<TodoistTask[]> {
    return this.request<TodoistTask[]>('/tasks?filter=!completed');
  }

  /**
   * Get task by ID
   */
  async getTask(taskId: string): Promise<TodoistTask> {
    return this.request<TodoistTask>(`/tasks/${taskId}`);
  }

  /**
   * Create new task
   */
  async createTask(task: Partial<Task>, projectId?: string): Promise<TodoistTask> {
    const todoistTask: Partial<TodoistTask> = {
      content: task.title || 'Untitled',
      description: task.description,
      priority: this.mapPriority(task.priority),
      due: task.due_date ? { date: task.due_date, is_recurring: false } : undefined,
      project_id: projectId || this.credentials.clientId || 'inbox',
    };

    return this.request<TodoistTask>('/tasks', {
      method: 'POST',
      body: JSON.stringify(todoistTask),
    });
  }

  /**
   * Update existing task
   */
  async updateTask(taskId: string, updates: Partial<Task>): Promise<TodoistTask> {
    const todoistUpdates: Partial<TodoistTask> = {};

    if (updates.title !== undefined) {
      todoistUpdates.content = updates.title;
    }
    if (updates.description !== undefined) {
      todoistUpdates.description = updates.description;
    }
    if (updates.priority !== undefined) {
      todoistUpdates.priority = this.mapPriority(updates.priority);
    }
    if (updates.due_date !== undefined) {
      todoistUpdates.due = { date: updates.due_date, is_recurring: false };
    }
    if (updates.status !== undefined) {
      todoistUpdates.completed = updates.status === 'done';
    }

    return this.request<TodoistTask>(`/tasks/${taskId}`, {
      method: 'POST',
      body: JSON.stringify(todoistUpdates),
    });
  }

  /**
   * Delete task
   */
  async deleteTask(taskId: string): Promise<void> {
    await this.request<void>(`/tasks/${taskId}`, {
      method: 'DELETE',
    });
  }

  /**
   * Close task (mark as completed without deleting)
   */
  async closeTask(taskId: string): Promise<TodoistTask> {
    return this.request<TodoistTask>(`/tasks/${taskId}/close`, {
      method: 'POST',
    });
  }

  /**
   * Reopen task (mark as incomplete)
   */
  async reopenTask(taskId: string): Promise<TodoistTask> {
    return this.request<TodoistTask>(`/tasks/${taskId}/reopen`, {
      method: 'POST',
    });
  }

  /**
   * Get all projects
   */
  async getProjects(): Promise<TodoistProject[]> {
    return this.request<TodoistProject[]>('/projects');
  }

  /**
   * Full sync (get all data)
   */
  async fullSync(syncToken?: string): Promise<TodoistSyncResponse> {
    const body: Record<string, unknown> = {
      resource_types: ['items', 'projects', 'labels', 'notes'],
    };

    if (syncToken) {
      body.sync_token = syncToken;
    } else {
      body.sync_token = '*'; // Full sync
    }

    return this.syncRequest<TodoistSyncResponse>('/sync', body);
  }

  /**
   * Quick add task (simple API)
   */
  async quickAdd(text: string, projectId?: string): Promise<TodoistTask> {
    const body: Record<string, unknown> = { text };
    if (projectId) {
      body.project_id = projectId;
    }

    return this.request<TodoistTask>('/quick/add', {
      method: 'POST',
      body: JSON.stringify(body),
    });
  }

  /**
   * Map Hearthlink priority to Todoist priority
   * Hearthlink: low, medium, high
   * Todoist: 1 (normal), 2 (high), 3 (urgent), 4 (very urgent)
   */
  private mapPriority(priority?: string): number {
    switch (priority) {
      case 'high':
        return 4;
      case 'medium':
        return 2;
      case 'low':
      default:
        return 1;
    }
  }

  /**
   * Map Todoist priority to Hearthlink priority
   */
  static mapPriorityFromTodoist(todoistPriority: number): 'low' | 'medium' | 'high' | 'critical' {
    if (todoistPriority >= 4) return 'high';
    if (todoistPriority >= 2) return 'medium';
    return 'low';
  }

  /**
   * Convert Todoist task to Hearthlink task
   */
  static toHearthlinkTask(todoistTask: TodoistTask): Task {
    return {
      id: todoistTask.id,
      title: todoistTask.content,
      description: todoistTask.description || '',
      status: todoistTask.completed ? 'completed' : 'todo',
      priority: this.mapPriorityFromTodoist(todoistTask.priority),
      due_date: todoistTask.due?.date || todoistTask.due?.datetime || undefined,
      created_at: todoistTask.created_at,
      updated_at: todoistTask.updated_at,
    };
  }

  /**
   * Refresh access token
   */
  async refreshToken(): Promise<TodoistCredentials> {
    if (!this.credentials.refreshToken || !this.credentials.clientId || !this.credentials.clientSecret) {
      throw new Error('Refresh token or client credentials not available');
    }

    const response = await fetch('https://todoist.com/oauth/access_token', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        client_id: this.credentials.clientId,
        client_secret: this.credentials.clientSecret,
        grant_type: 'refresh_token',
        refresh_token: this.credentials.refreshToken,
      }),
    });

    if (!response.ok) {
      throw new Error('Failed to refresh Todoist access token');
    }

    const data = await response.json();
    this.credentials.accessToken = data.access_token;
    this.credentials.refreshToken = data.refresh_token;

    return this.credentials;
  }

  /**
   * Revoke token (logout)
   */
  async revokeToken(): Promise<void> {
    await fetch('https://todoist.com/oauth/revoke', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        client_id: this.credentials.clientId,
        client_secret: this.credentials.clientSecret,
        token: this.credentials.accessToken,
      }),
    });
  }

  /**
   * Test connection
   */
  async testConnection(): Promise<boolean> {
    try {
      await this.getProjects();
      return true;
    } catch (error) {
      return false;
    }
  }
}

// ============================================================================
// Factory Functions
// ============================================================================

/**
 * Create Todoist API client with credentials
 */
export function createTodoistAPI(credentials: TodoistCredentials): TodoistAPI {
  return new TodoistAPI(credentials);
}

/**
 * Create Todoist API client with access token only
 */
export function createTodoistAPITokenOnly(accessToken: string): TodoistAPI {
  return new TodoistAPI({ accessToken });
}

