import * as vscode from 'vscode';
import { EnforcementEngine } from './governance/EnforcementEngine';
import { IntentService } from './governance/IntentService';
import { Verdict } from './governance/types/IntentTypes';

const ENFORCEMENT_TIMEOUT_MS = 750; // keep UX snappy

function withTimeout<T>(promise: Promise<T>, ms: number, fallback: T): Promise<T> {
  let timer: NodeJS.Timeout | undefined;
  return Promise.race([
    promise,
    new Promise<T>((resolve) => {
      timer = setTimeout(() => resolve(fallback), ms);
    }),
  ]).finally(() => {
    if (timer) clearTimeout(timer);
  });
}

export function registerWillSaveEnforcement(
  context: vscode.ExtensionContext,
  intentService: IntentService,
  enforcement: EnforcementEngine
): void {
  const sub = vscode.workspace.onWillSaveTextDocument((event) => {
    event.waitUntil((async () => {
      const activeIntent = intentService.getActiveIntent();
      if (!activeIntent) return; // no intent -> allow

      const verdict = await withTimeout(
        enforcement.evaluateAction({
          kind: 'SAVE',
          uri: event.document.uri.toString(),
          languageId: event.document.languageId,
          intentId: activeIntent.id,
        }),
        ENFORCEMENT_TIMEOUT_MS,
        { status: Verdict.ALLOW, reason: 'Timeout fallback' }
      );

      if (verdict.status === Verdict.BLOCK) {
        // Block save and inform user
        vscode.window.showErrorMessage(
          verdict.reason || 'Save blocked by FailSafe governance.'
        );
        throw new Error('FailSafe BLOCK verdict');
      }
    })());
  });

  context.subscriptions.push(sub);
}
