## 2) GovernanceStatusBar class
```ts
import * as vscode from 'vscode';
import { Intent } from './governance/types/IntentTypes';

type StatusColor = string | vscode.ThemeColor;

export class GovernanceStatusBar {
  private readonly item: vscode.StatusBarItem;

  constructor() {
    this.item = vscode.window.createStatusBarItem(
      vscode.StatusBarAlignment.Left,
      100
    );
    this.item.command = 'failsafe.showMenu';
    this.item.show();
  }

  update(intent: Intent | null): void {
    if (!intent) {
      this.item.text = 'FailSafe: Inactive';
      this.item.tooltip = 'No active intent';
      this.item.color = undefined;
      return;
    }

    this.item.text = `FailSafe: ${intent.status}`;
    this.item.tooltip = `Intent: ${intent.title}\nStatus: ${intent.status}\nScope: ${intent.scope}`;
    this.item.color = this.colorForStatus(intent.status);
  }

  dispose(): void {
    this.item.dispose();
  }

  private colorForStatus(status: Intent['status']): StatusColor {
    switch (status) {
      case 'PULSE':
        return new vscode.ThemeColor('charts.yellow');
      case 'PASS':
        return new vscode.ThemeColor('charts.green');
      case 'SEALED':
        return new vscode.ThemeColor('charts.blue');
      default:
        return undefined;
    }
  }
}
