import * as vscode from 'vscode';
import { IntentService } from './governance/IntentService';

export function registerCommands(
  context: vscode.ExtensionContext,
  intentService: IntentService
): void {
  context.subscriptions.push(
    vscode.commands.registerCommand('failsafe.createIntent', async () => {
      // Placeholder: create an intent with minimal UI
      const title = await vscode.window.showInputBox({
        prompt: 'Intent title',
        placeHolder: 'e.g., Refactor auth flow safely'
      });
      if (!title) return;

      await intentService.createIntent({ title });
      vscode.window.showInformationMessage('FailSafe intent created.');
    })
  );

  context.subscriptions.push(
    vscode.commands.registerCommand('failsafe.sealIntent', async () => {
      const active = intentService.getActiveIntent();
      if (!active) {
        vscode.window.showWarningMessage('No active intent to seal.');
        return;
      }

      await intentService.sealIntent(active.id);
      vscode.window.showInformationMessage('FailSafe intent sealed.');
    })
  );
}
