import * as vscode from 'vscode';
import { IntentService } from './governance/IntentService';
import { EnforcementEngine } from './governance/EnforcementEngine';
import { GovernanceStatusBar } from './GovernanceStatusBar';
import { registerWillSaveEnforcement } from './WillSaveEnforcement';
import { registerCommands } from './commands';

export async function activate(context: vscode.ExtensionContext): Promise<void> {
  const intentService = new IntentService(context.globalStorageUri.fsPath);
  const enforcement = new EnforcementEngine(intentService);
  const statusBar = new GovernanceStatusBar();

  // Initial render
  statusBar.update(intentService.getActiveIntent());

  // Wire commands
  registerCommands(context, intentService);

  // Wire save enforcement
  registerWillSaveEnforcement(context, intentService, enforcement);

  // Wire intent changes to status bar
  const intentSub = intentService.onDidChangeIntent((intent) => {
    statusBar.update(intent);
  });

  // Optional: Verdict router logging (conceptual)
  const verdictSub = enforcement.onDidVerdict((verdict) => {
    console.log('[FailSafe] Verdict:', verdict);
  });

  context.subscriptions.push(statusBar, intentSub, verdictSub);
}

export function deactivate(): void {}
