import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';

interface VoiceContextType {
  micGain: number;
  setMicGain: (gain: number) => void;
  speakerVolume: number;
  setSpeakerVolume: (volume: number) => void;
  isMicMuted: boolean;
  setIsMicMuted: (muted: boolean) => void;
  toggleMicMute: () => void;
  isSpeakerMuted: boolean;
  setIsSpeakerMuted: (muted: boolean) => void;
  toggleSpeakerMute: () => void;
}

const VoiceContext = createContext<VoiceContextType | undefined>(undefined);

export const VoiceProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const [micGain, setMicGain] = useState(() => {
    const saved = localStorage.getItem('voice_mic_gain');
    return saved ? parseFloat(saved) : 0.7;
  });

  const [speakerVolume, setSpeakerVolume] = useState(() => {
    const saved = localStorage.getItem('voice_speaker_volume');
    return saved ? parseFloat(saved) : 0.8;
  });

  const [isMicMuted, setIsMicMuted] = useState(() => {
    const saved = localStorage.getItem('voice_mic_muted');
    return saved === 'true';
  });

  const [isSpeakerMuted, setIsSpeakerMuted] = useState(() => {
    const saved = localStorage.getItem('voice_speaker_muted');
    return saved === 'true';
  });

  useEffect(() => {
    localStorage.setItem('voice_mic_gain', micGain.toString());
  }, [micGain]);

  useEffect(() => {
    localStorage.setItem('voice_speaker_volume', speakerVolume.toString());
  }, [speakerVolume]);

  useEffect(() => {
    localStorage.setItem('voice_mic_muted', isMicMuted.toString());
  }, [isMicMuted]);

  useEffect(() => {
    localStorage.setItem('voice_speaker_muted', isSpeakerMuted.toString());
  }, [isSpeakerMuted]);

  const toggleMicMute = () => setIsMicMuted(prev => !prev);
  const toggleSpeakerMute = () => setIsSpeakerMuted(prev => !prev);

  return (
    <VoiceContext.Provider
      value={{
        micGain,
        setMicGain,
        speakerVolume,
        setSpeakerVolume,
        isMicMuted,
        setIsMicMuted,
        toggleMicMute,
        isSpeakerMuted,
        setIsSpeakerMuted,
        toggleSpeakerMute,
      }}
    >
      {children}
    </VoiceContext.Provider>
  );
};

export const useVoiceContext = () => {
  const context = useContext(VoiceContext);
  if (context === undefined) {
    throw new Error('useVoiceContext must be used within a VoiceProvider');
  }
  return context;
};

