            <div className="h-screen w-full flex items-center justify-center bg-[#030712]">
              <HearthlinkLoadingSequence />
            </div>
          }>
            {!isSequenceComplete ? (
              <div className="h-screen w-full flex items-center justify-center bg-[#030712]">
                <HearthlinkLoadingSequence onComplete={() => setIsSequenceComplete(true)} />
              </div>
            ) : (
              renderMainContent()
            )}
          </Suspense>

          {/* Toast Notifications (for celebration toasts, etc) */}
          <Toaster 
            position="top-right" 
            richColors 
            toastOptions={{
              success: {
                'data-testid': 'toast-success',
              },
            } as any}
          />

          {/* Debug Mode Indicator */}
          {settings.debugMode && (
            <div className="fixed bottom-4 left-4 bg-yellow-900/20 border border-yellow-400 text-yellow-400 px-3 py-1 rounded text-xs font-mono">
              DEBUG MODE | UI: {settings.uiMode.toUpperCase()} | User: {settings.userId}
            </div>
          )}

          {/* Mode Selector Overlay */}
          {showModeSelector && renderUISelector()}

          {/* First 7 Days Tutorial Overlay */}
          <FirstSevenDaysOverlay
            isOpen={tutorialTour.isOpen}
            onClose={tutorialTour.closeTour}
            state={tutorialTour.state}
            stepsByDay={tutorialTour.stepsByDay}
            currentSteps={tutorialTour.currentSteps}
            goToDay={tutorialTour.goToDay}
            completeStep={tutorialTour.completeStep}
            completeDay={tutorialTour.completeDay}
            skipDay={tutorialTour.skipDay}
          />

          {/* Tutorial Launcher (Top-Left Chip) - Only show on Launcher Page */}
          {settings.uiMode === 'hierarchical' && tutorialTour.shouldPrompt && !showOnboarding && !tutorialTour.isOpen && (
            <button
              onClick={tutorialTour.openTour}
              className="fixed top-4 right-4 z-50 flex items-center gap-3 px-4 py-2 rounded-full bg-cyan-500/15 border border-cyan-400/60 text-cyan-100 shadow-[0_0_16px_rgba(34,211,238,0.35)] hover:bg-cyan-500/25 transition-all"
              aria-label="Open First 7 Days tutorial"
              title="Continue your First 7 Days journey"
            >
              <BookOpen className="w-5 h-5" />
              <div className="flex flex-col items-start leading-tight">
                <span className="text-[10px] uppercase tracking-[0.3em] text-cyan-200">First 7 Days</span>
                <span className="text-xs text-slate-200">
                  Day {tutorialTour.state.currentDay} Ã¢â‚¬Â¢ {tutorialTour.progress.completedSteps}/{tutorialTour.progress.totalSteps}
                </span>
              </div>
            </button>
          )}

          {/* Quick Mode Toggle for Development */}
          {settings.debugMode && (
            <div className="fixed top-4 left-4 flex gap-2">
              <button
                onClick={() => updateSettings({ uiMode: 'hierarchical' })}
                className={`px-2 py-1 text-xs rounded ${settings.uiMode === 'hierarchical' ? 'bg-green-600 text-white' : 'bg-gray-700 text-gray-300'}`}
              >
                Hierarchical
              </button>
              <button
                onClick={() => updateSettings({ uiMode: 'enhanced' })}
                className={`px-2 py-1 text-xs rounded ${settings.uiMode === 'enhanced' ? 'bg-blue-600 text-white' : 'bg-gray-700 text-gray-300'}`}
              >
                Enhanced
              </button>
              <button
                onClick={() => updateSettings({ uiMode: 'existing' })}
                className={`px-2 py-1 text-xs rounded ${settings.uiMode === 'existing' ? 'bg-purple-600 text-white' : 'bg-gray-700 text-gray-300'}`}
              >
                Existing
              </button>
              <button
                onClick={() => updateSettings({ uiMode: 'testbed' })}
                className={`px-2 py-1 text-xs rounded ${settings.uiMode === 'testbed' ? 'bg-yellow-600 text-white' : 'bg-gray-700 text-gray-300'}`}
              >
                Testbed
              </button>
            </div>
          )}

          {/* Quick Capture Modal */}
          <QuickCaptureModal
            isOpen={showQuickCapture}
            onClose={handleCloseQuickCapture}
            userId={settings.userId}
          />
        </div>
      </CoreOrchestrationProvider>
    </AuthProvider>
  );
};

export default App;

