+import React, { createContext, useCallback, useContext, useMemo, useState } from 'react';
+import type { ReactNode } from 'react';
+import type { VoiceOrbState } from '../components/voice/VoiceOrb';
+import { voiceStartListening, voiceStop, voiceSynthesize, type VoiceSynthesisResponse } from '../bridges/VoiceBridge';
+
+interface VoiceStateContextValue {
+  voiceState: VoiceOrbState;
+  setVoiceState: (state: VoiceOrbState) => void;
+  isOrbVisible: boolean;
+  setOrbVisible: (visible: boolean) => void;
+  startListening: () => Promise<void>;
+  stopListening: () => Promise<void>;
+  synthesize: (text: string, voiceId?: string) => Promise<VoiceSynthesisResponse>;
+  error: string | null;
+}
+
+const VoiceStateContext = createContext<VoiceStateContextValue | undefined>(undefined);
+
+export const VoiceStateProvider: React.FC<{ children: ReactNode; userId: string }> = ({ children, userId }) => {
+  const [voiceState, setVoiceState] = useState<VoiceOrbState>('idle');
+  const [isOrbVisible, setOrbVisible] = useState(true);
+  const [error, setError] = useState<string | null>(null);
+
+  const startListening = useCallback(async () => {
+    setError(null);
+    setVoiceState('listening');
+    try {
+      await voiceStartListening({ user_id: userId });
+    } catch (err) {
+      console.error('[VoiceStateContext] Failed to start listening:', err);
+      setError(err instanceof Error ? err.message : 'Failed to start listening');
+      setVoiceState('error');
+    }
+  }, [userId]);
+
+  const stopListening = useCallback(async () => {
+    setError(null);
+    setVoiceState('processing');
+    try {
+      await voiceStop({ user_id: userId });
+      setVoiceState('idle');
+    } catch (err) {
+      console.error('[VoiceStateContext] Failed to stop listening:', err);
+      setError(err instanceof Error ? err.message : 'Failed to stop listening');
+      setVoiceState('error');
+    }
+  }, [userId]);
+
+  const synthesize = useCallback(
+    async (text: string, voiceId?: string) => {
+      setError(null);
+      setVoiceState('speaking');
+      try {
+        const response = await voiceSynthesize({ user_id: userId, text, voice_id: voiceId });
+        if (!response.success) {
+          setError(response.error ?? 'Voice synthesis failed');
+          setVoiceState('error');
+        } else {
+          setVoiceState('idle');
+        }
+        return response;
+      } catch (err) {
+        console.error('[VoiceStateContext] Failed to synthesize voice:', err);
+        setError(err instanceof Error ? err.message : 'Failed to synthesize voice');
+        setVoiceState('error');
+        throw err;
+      }
+    },
+    [userId]
+  );
+
+  const value = useMemo(
+    () => ({
+      voiceState,
+      setVoiceState,
+      isOrbVisible,
+      setOrbVisible,
+      startListening,
+      stopListening,
+      synthesize,
+      error,
+    }),
+    [voiceState, isOrbVisible, startListening, stopListening, synthesize, error]
+  );
+
+  return <VoiceStateContext.Provider value={value}>{children}</VoiceStateContext.Provider>;
+};
