{"command":"@'
# REPORT: AGENT 1 (CODEX)

Summary:
Implemented a typed MCP server adapter contract that registers strictly validated tools for Sentinel and Ledger, including audit/claim validation, status, project status, and ledger queries with audit logging for decision-producing tools.

Deliverables:
```ts
import { z } from 'zod';
import { SentinelDaemon } from '../sentinel/SentinelDaemon';
import { LedgerManager } from '../qorelogic/ledger/LedgerManager';
import { LedgerEntry, LedgerEventType, SentinelStatus, SentinelVerdict } from '../shared/types';

export type MCPTextContent = { type: 'text'; text: string };
export type MCPJsonContent = { type: 'json'; json: unknown };
export type MCPContent = MCPTextContent | MCPJsonContent;

export interface MCPToolResult {
  content: MCPContent[];
  isError?: boolean;
}

export interface MCPRequestContext {
  agentDid?: string;
  requestId?: string;
}

export interface MCPTool<TInput> {
  name: string;
  description: string;
  inputSchema: z.ZodType<TInput>;
  handler: (input: TInput, context: MCPRequestContext) => Promise<MCPToolResult>;
}

export interface MCPServer {
  registerTool<TInput>(tool: MCPTool<TInput>): void;
}

export interface FailSafeServerDeps {
  sentinel: SentinelDaemon;
  ledger: LedgerManager;
}

const ToolNames = {
  sentinelStatus: 'sentinel.status',
  sentinelAuditFile: 'sentinel.auditFile',
  sentinelValidateClaim: 'sentinel.validateClaim',
  projectStatus: 'failsafe.projectStatus',
  ledgerRecent: 'ledger.recentEntries',
  ledgerByType: 'ledger.entriesByType',
  ledgerByAgent: 'ledger.entriesByAgent',
  ledgerVerify: 'ledger.verifyChain'
} as const;

export type FailSafeToolName = (typeof ToolNames)[keyof typeof ToolNames];

const ledgerEventTypeValues = [
  'PROPOSAL',
  'AUDIT_PASS',
  'AUDIT_FAIL',
  'L3_QUEUED',
  'L3_APPROVED',
  'L3_REJECTED',
  'TRUST_UPDATE',
  'PENALTY_APPLIED',
  'QUARANTINE_START',
  'QUARANTINE_END',
  'DIVERGENCE_DECLARED',
  'DIVERGENCE_RESOLVED',
  'SYSTEM_EVENT'
] as const;

const emptySchema = z.object({}).strict();
const limitSchema = z.number().int().min(1).max(500).optional();
const ledgerEventTypeSchema = z.enum(ledgerEventTypeValues);

export class FailSafeServer {
  private sentinel: SentinelDaemon;
  private ledger: LedgerManager;

  constructor(deps: FailSafeServerDeps) {
    this.sentinel = deps.sentinel;
    this.ledger = deps.ledger;
  }

  register(server: MCPServer): void {
    for (const tool of this.buildTools()) {
      server.registerTool(tool);
    }
  }

  private buildTools(): MCPTool<unknown>[] {
    return [
      this.createSentinelStatusTool(),
      this.createSentinelAuditFileTool(),
      this.createSentinelValidateClaimTool(),
      this.createProjectStatusTool(),
      this.createLedgerRecentTool(),
      this.createLedgerByTypeTool(),
      this.createLedgerByAgentTool(),
      this.createLedgerVerifyTool()
    ];
  }

  private createSentinelStatusTool(): MCPTool<z.infer<typeof emptySchema>> {
    return this.makeTool({
      name: ToolNames.sentinelStatus,
      description: 'Get current Sentinel daemon status.',
      schema: emptySchema,
      handler: async () => this.jsonResult(this.sentinel.getStatus())
    });
  }

  private createProjectStatusTool(): MCPTool<z.infer<typeof emptySchema>> {
    return this.makeTool({
      name: ToolNames.projectStatus,
      description: 'Get combined Sentinel status and ledger health stats.',
      schema: emptySchema,
      handler: async () => this.jsonResult(await this.getProjectStatus())
    });
  }

  private createSentinelAuditFileTool(): MCPTool<z.infer<typeof this.auditFileSchema>> {
    return this.makeTool({
      name: ToolNames.sentinelAuditFile,
      description: 'Run a Sentinel audit against a file path.',
      schema: this.auditFileSchema,
      handler: async (input, context) => this.runAuditFile(input, context)
    });
  }

  private createSentinelValidateClaimTool(): MCPTool<z.infer<typeof this.validateClaimSchema>> {
    return this.makeTool({
      name: ToolNames.sentinelValidateClaim,
      description: 'Validate an agent claim through Sentinel arbitration.',
      schema: this.validateClaimSchema,
      handler: async (input, context) => this.runValidateClaim(input, context)
    });
  }

  private createLedgerRecentTool(): MCPTool<z.infer<typeof this.recentEntriesSchema>> {
    return this.makeTool({
      name: ToolNames.ledgerRecent,
      description: 'Fetch recent ledger entries.',
      schema: this.recentEntriesSchema,
      handler: async (input) => this.jsonResult(await this.getLedgerRecent(input))
    });
  }

  private createLedgerByTypeTool(): MCPTool<z.infer<typeof this.entriesByTypeSchema>> {
    return this.makeTool({
      name: ToolNames.ledgerByType,
      description: 'Fetch ledger entries by event type.',
      schema: this.entriesByTypeSchema,
      handler: async (input) => this.jsonResult(await this.getLedgerByType(input))
    });
  }

  private createLedgerByAgentTool(): MCPTool<z.infer<typeof this.entriesByAgentSchema>> {
    return this.makeTool({
      name: ToolNames.ledgerByAgent,
      description: 'Fetch ledger entries for a specific agent.',
      schema: this.entriesByAgentSchema,
      handler: async (input) => this.jsonResult(await this.getLedgerByAgent(input))
    });
  }

  private createLedgerVerifyTool(): MCPTool<z.infer<typeof emptySchema>> {
    return this.makeTool({
      name: ToolNames.ledgerVerify,
      description: 'Verify the ledger hash chain integrity.',
      schema: emptySchema,
      handler: async () => this.jsonResult(this.verifyLedger())
    });
  }

  private get auditFileSchema(): z.ZodType<{ filePath: string; agentDid?: string; reason?: string }> {
    return z.object({
      filePath: z.string().min(1),
      agentDid: z.string().min(3).optional(),
      reason: z.string().min(1).optional()
    }).strict();
  }

  private get validateClaimSchema(): z.ZodType<{ agentDid: string; claim: Record<string, unknown>; context?: Record<string, unknown> }> {
    return z.object({
      agentDid: z.string().min(3),
      claim: z.record(z.unknown()),
      context: z.record(z.unknown()).optional()
    }).strict();
  }

  private get recentEntriesSchema(): z.ZodType<{ limit?: number }> {
    return z.object({ limit: limitSchema }).strict();
  }

  private get entriesByTypeSchema(): z.ZodType<{ eventType: LedgerEventType; limit?: number }> {
    return z.object({
      eventType: ledgerEventTypeSchema,
      limit: limitSchema
    }).strict();
  }

  private get entriesByAgentSchema(): z.ZodType<{ agentDid: string; limit?: number }> {
    return z.object({
      agentDid: z.string().min(3),
      limit: limitSchema
    }).strict();
  }

  private makeTool<TSchema extends z.ZodTypeAny>(params: {
    name: FailSafeToolName;
    description: string;
    schema: TSchema;
    handler: (input: z.infer<TSchema>, context: MCPRequestContext) => Promise<MCPToolResult>;
  }): MCPTool<z.infer<TSchema>> {
    return {
      name: params.name,
      description: params.description,
      inputSchema: params.schema,
      handler: async (input, context) => {
        const parsed = params.schema.safeParse(input);
        if (!parsed.success) {
          return this.validationError(parsed.error.message);
        }
        return params.handler(parsed.data, context);
      }
    };
  }

  private async getProjectStatus(): Promise<{ sentinel: SentinelStatus; ledger: { entryCount: number; chainValid: boolean } }> {
    const sentinel = this.sentinel.getStatus();
    const entryCount = this.ledger.getEntryCount();
    const chainValid = this.ledger.verifyChain();
    return { sentinel, ledger: { entryCount, chainValid } };
  }

  private async runAuditFile(
    input: { filePath: string; agentDid?: string; reason?: string },
    context: MCPRequestContext
  ): Promise<MCPToolResult> {
    const verdict = await this.sentinel.auditFile(input.filePath);
    await this.logDecision({
      verdict,
      agentDid: this.resolveAgentDid(input.agentDid, context),
      artifactPath: input.filePath,
      tool: ToolNames.sentinelAuditFile,
      reason: input.reason
    });
    return this.jsonResult(verdict);
  }

