import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Mic, Radio, Loader2, Volume2 } from '../ui/HearthlinkIcons';
import { cn } from '../../lib/utils'; // Adjust path if needed
// import { useVoiceBridge } from '../../hooks/useVoiceBridge'; // Future hook

interface VoiceOrbProps {
  onActivate?: () => void;
  onDeactivate?: () => void;
  visible?: boolean; // Controlled by parent (e.g. window minimizing)
  initialState?: VoiceOrbState;
  state?: VoiceOrbState;
  onStateChange?: (state: VoiceOrbState) => void;
}

export type VoiceOrbState = 'idle' | 'listening' | 'processing' | 'speaking' | 'error';

/**
 * Voice Orb - Minimized Interaction Surface
 * 
 * A reduced-UI component for always-on voice interaction.
 * Designed to float or dock when the main window is minimized.
 */
export const VoiceOrb: React.FC<VoiceOrbProps> = ({ 
  onActivate, 
  onDeactivate,
  visible = true,
  initialState = 'idle',
  state: controlledState,
  onStateChange
}) => {
  const [uncontrolledState, setUncontrolledState] = useState<VoiceOrbState>(initialState);
  const [isHovered, setIsHovered] = useState(false);
  const [volume, setVolume] = useState(0); // Audio amplitude (0-1)
  const state = controlledState ?? uncontrolledState;

  const setState = (next: VoiceOrbState) => {
    if (controlledState === undefined) {
      setUncontrolledState(next);
    }
    onStateChange?.(next);
  };

  // Simulation for breathing/pulse effects
  useEffect(() => {
    let interval: NodeJS.Timeout;
    
    if (state === 'listening' || state === 'speaking') {
      interval = setInterval(() => {
        // Random volume simulation for now
        setVolume(Math.random() * 0.5 + 0.3);
      }, 100);
    } else {
      setVolume(0);
    }

    return () => clearInterval(interval);
  }, [state]);

  const handleInteraction = () => {
    if (state === 'idle') {
      setState('listening');
      onActivate?.();
    } else if (state === 'listening') {
      setState('processing');
      // Simulate processing completion
      setTimeout(() => setState('speaking'), 1500);
    } else if (state === 'speaking') {
        setState('idle');
        onDeactivate?.();
    }
  };

  if (!visible) return null;

  return (
    <AnimatePresence>
      <motion.div
        drag
        dragMomentum={false}
        initial={{ opacity: 0, scale: 0.8, y: 50 }}
        animate={{ opacity: 1, scale: 1, y: 0 }}
        exit={{ opacity: 0, scale: 0.8, y: 50 }}
        className="fixed bottom-8 right-8 z-[9999]"
      >
        <div 
          className="relative group"
          onMouseEnter={() => setIsHovered(true)}
          onMouseLeave={() => setIsHovered(false)}
        >
          {/* Main Orb Sphere */}
          <motion.div
            onClick={handleInteraction}
            animate={{
              scale: state === 'listening' ? 1.2 : 1,
              boxShadow: state === 'listening' 
                ? `0 0 ${20 + volume * 20}px rgba(0, 229, 255, ${0.4 + volume * 0.4})`
                : state === 'speaking'
                  ? `0 0 ${20 + volume * 30}px rgba(0, 255, 128, 0.4)`
                  : '0 0 15px rgba(0, 229, 255, 0.2)',
            }}
            transition={{ type: 'spring', stiffness: 300, damping: 20 }}
            className={cn(
              "w-16 h-16 rounded-full cursor-pointer flex items-center justify-center backdrop-blur-md border border-white/10 transition-colors duration-300",
              state === 'idle' && "bg-black/60 hover:bg-black/80 border-cyan-500/30",
              state === 'listening' && "bg-cyan-900/40 border-cyan-400/80",
              state === 'processing' && "bg-indigo-900/40 border-indigo-400/80",
              state === 'speaking' && "bg-emerald-900/40 border-emerald-400/80",
              state === 'error' && "bg-red-900/40 border-red-400/80"
            )}
          >
            {/* Inner Icon / Visualization */}
            <div className="relative z-10 text-white/90">
                {state === 'idle' && (
                    <Mic className={cn("w-6 h-6", isHovered && "text-cyan-400")} />
                )}
                {state === 'listening' && (
                    <VoiceVisualizer active volume={volume} color="#00e5ff" />
                )}
                {state === 'processing' && (
                    <Loader2 className="w-8 h-8 animate-spin text-indigo-400" />
                )}
                {state === 'speaking' && (
                    <VoiceVisualizer active volume={volume} color="#10b981" />
                )}
            </div>

            {/* Orbiting Ring (Processing) */}
            {state === 'processing' && (
                <motion.div
                    animate={{ rotate: 360 }}
                    transition={{ repeat: Infinity, duration: 2, ease: "linear" }}
                    className="absolute inset-0 rounded-full border-t-2 border-indigo-400 opacity-50"
                />
            )}
          </motion.div>

          {/* Status Label (Hover Only) */}
          <AnimatePresence>
            {isHovered && (
                <motion.div
                    initial={{ opacity: 0, x: -10 }}
                    animate={{ opacity: 1, x: -20 }}
                    exit={{ opacity: 0, x: -10 }}
                    className="absolute right-full top-1/2 -translate-y-1/2 px-2 py-1 bg-black/80 border border-white/10 rounded text-xs text-white whitespace-nowrap backdrop-blur"
                >
                    {state.charAt(0).toUpperCase() + state.slice(1)}
                </motion.div>
            )}
          </AnimatePresence>
        </div>
      </motion.div>
    </AnimatePresence>
  );
};

// Simple visualizer stub
const VoiceVisualizer: React.FC<{ active: boolean; volume: number; color: string }> = ({ active, volume, color }) => {
    return (
        <div className="flex items-center gap-0.5 h-8">
            {[1, 2, 3, 4, 5].map((i) => (
                <motion.div
                    key={i}
                    animate={{ 
                        height: active ? Math.max(4, volume * 32 * (Math.random() * 0.5 + 0.5)) : 4 
                    }}
                    style={{ backgroundColor: color }}
                    className="w-1 rounded-full opacity-80"
                />
            ))}
        </div>
    );
}

