import React, { useState, useEffect, useCallback, Suspense } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { Toaster } from 'sonner';
import { type FirstRunConfig } from './components/setup/FirstRunSetup';
import { HearthlinkLoadingSequence } from './components/shared';

import { useOnboarding } from './hooks/useOnboarding';
import { useFirstSevenDaysTour } from './hooks/useFirstSevenDaysTour';
import { QuickCaptureModal } from './components/vault/QuickCaptureModal';
import { FirstSevenDaysOverlay } from './components/tutorial/FirstSevenDaysOverlay';
import { AuthProvider } from './contexts/AuthContext';
import { CoreOrchestrationProvider } from './context/CoreOrchestrationContext';
import { VoiceStateProvider, useVoiceState } from './context/VoiceStateContext';
import { UISelector } from './components/debug/UISelector';
import { BookOpen } from './components/ui/HearthlinkIcons';
import { subscribeTutorialGoTo } from './utils/tutorialEvents';
import { setAppReadyState } from './utils/appReady';
import { VoiceOrb } from './components/voice/VoiceOrb';

// Lazy Load Components
const HearthlinkApp = React.lazy(() => import('./components/HearthlinkApp'));
const SecureIPCTest = React.lazy(() => import('./components/SecureIPCTest').then(module => ({ default: module.SecureIPCTest })));
const HierarchicalLaunchPage = React.lazy(() => import('./components/HierarchicalLaunchPage'));
const UnifiedHearthlinkApp = React.lazy(() => import('./components/UnifiedHearthlinkApp'));
const OnboardingWizard = React.lazy(() => import('./components/OnboardingWizard'));
const FirstRunSetup = React.lazy(() => import('./components/setup/FirstRunSetup'));



/**
 * Main Hearthlink Application Entry Point
 * Provides switching between different UI modes and feature sets
 */

type UIMode = 'mythtech' | 'enhanced' | 'hierarchical' | 'existing' | 'unified' | 'testbed' | 'help';

interface AppSettings {
  uiMode: UIMode;
  userId: string;
  focusFormulaLicenseToken?: string;
  debugMode: boolean;
}

const App: React.FC = () => {
  const navigate = useNavigate();
  
  // Loading Sequence State - Blocks main content until animation completes
  // Can be bypassed via URL param (?skipBoot=true) or localStorage (for E2E tests)
  const [isSequenceComplete, setIsSequenceComplete] = useState(() => {
    const params = new URLSearchParams(window.location.search);
    const skipViaUrl = params.get('skipBoot') === 'true';
    const skipViaStorage = localStorage.getItem('hearthlink_skip_boot') === 'true';
    return skipViaUrl || skipViaStorage;
  });
  const location = useLocation();
  const { showOnboarding, isComplete, config, completeOnboarding, skipOnboarding } = useOnboarding();

  // DEBUG: Log onboarding state
  console.log('[App.tsx] Onboarding state:', { showOnboarding, isComplete, hasConfig: !!config });

  // First 7 Days Interactive Tutorial
  const tutorialTour = useFirstSevenDaysTour();

  const [settings, setSettings] = useState<AppSettings>({
    uiMode: (() => {
        const path = window.location.pathname.toLowerCase();
        const validModules = ['/alden', '/alice', '/mimic', '/core', '/vault', '/sentry', '/synapse', '/help'];
        return validModules.some(m => path.includes(m)) ? 'existing' : 'hierarchical';
    })(),
    userId: config?.firstName || 'default-user',
    debugMode: false
  });

  // Sync uiMode with URL for browser navigation (Back/Forward)
  useEffect(() => {
    const path = location.pathname.toLowerCase();
    
    // If we went back to root, switch to hierarchical
    if ((path === '/' || path === '/launch') && settings.uiMode !== 'hierarchical') {
      console.log('[App.tsx] Detected root URL, switching to hierarchical mode');
      updateSettings({ uiMode: 'hierarchical' });
    }
    // If we deep linked into a module via back/forward, switch to existing
    // Note: This logic duplicates the initializer but runs on updates
    const validModules = ['/alden', '/alice', '/mimic', '/core', '/vault', '/sentry', '/synapse', '/help'];
    if (validModules.some(m => path.includes(m)) && settings.uiMode === 'hierarchical') {
       console.log('[App.tsx] Detected module URL, switching to existing mode');
       updateSettings({ uiMode: 'existing' });
    }
  }, [location.pathname]);

  const [showModeSelector, setShowModeSelector] = useState(false);
  const [showQuickCapture, setShowQuickCapture] = useState(false);

  useEffect(() => {
    // Load settings from localStorage
    const savedSettings = localStorage.getItem('hearthlink_app_settings');
    if (savedSettings) {
      try {
        const parsed = JSON.parse(savedSettings);
        // IMPORTANT: Always start at hierarchical launcher, ignore saved uiMode
        delete parsed.uiMode;
        setSettings(prev => ({ ...prev, ...parsed }));
        console.log('[App.tsx] Loaded settings from localStorage (uiMode reset to hierarchical)');
      } catch (err) {
        console.warn('Failed to parse saved settings:', err);
      }
    }

    // Update userId from onboarding config
    if (config?.firstName) {
      setSettings(prev => ({ ...prev, userId: config.firstName }));
    }

    // Check for debug parameter
    const urlParams = new URLSearchParams(window.location.search);
    const debugMode = urlParams.get('debug') === 'true';
    if (debugMode) {
      setSettings(prev => ({ ...prev, debugMode: true }));
      setShowModeSelector(true);
    }
  }, [config]);

  useEffect(() => {
    setAppReadyState(!showOnboarding);
  }, [showOnboarding]);

  const updateSettings = (newSettings: Partial<AppSettings>) => {
    const updated = { ...settings, ...newSettings };
    setSettings(updated);
    localStorage.setItem('hearthlink_app_settings', JSON.stringify(updated));
  };

  const handleOpenQuickCapture = () => {
    setShowQuickCapture(true);
    tutorialTour.emitTutorialEvent('hl:event:vault:quick_capture');
  };

  const handleCloseQuickCapture = () => {
    setShowQuickCapture(false);
  };

  const flashTarget = useCallback((selector: string, attempts = 12) => {
    if (typeof document === 'undefined') return;
    let tries = 0;
    const timer = window.setInterval(() => {
      const target = document.querySelector(selector) as HTMLElement | null;
      if (target) {
        target.classList.remove('hl-tutorial-flash');
        void target.offsetWidth;
        target.classList.add('hl-tutorial-flash');
        window.setTimeout(() => target.classList.remove('hl-tutorial-flash'), 1600);
        window.clearInterval(timer);
      }
      tries += 1;
      if (tries >= attempts) {
        window.clearInterval(timer);
      }
    }, 250);
  }, []);

  const handleTutorialGoTo = useCallback(
    (stepId: string) => {
      const goLauncher = () => updateSettings({ uiMode: 'hierarchical' });
      const goExisting = () => updateSettings({ uiMode: 'existing' });

      switch (stepId) {
        case 'day1-chat':
        case 'day1-switch':
          goExisting();
          navigate('/alden');
          flashTarget('[data-hl-tour="chat-input"]');
          flashTarget('[data-hl-tour="persona-switcher"]');
          break;
        case 'day1-help':
          goLauncher();
          flashTarget('[data-hl-tour="help-entry"]');
          break;
        case 'day2-quick-capture':
          goLauncher();
          flashTarget('[data-hl-tour="quick-capture"]');
          break;
        case 'day2-vault-view':
          goLauncher();
          flashTarget('[data-hl-tour="vault-nav"]');
          break;
        case 'day2-search':
          goExisting();
          navigate('/vault');
          flashTarget('[data-hl-tour="vault-search"]');
          break;
        case 'day3-alden-open':
        case 'day3-task-change':
        case 'day3-briefing':
        case 'day6-delegate':
        case 'day6-compare':
        case 'day6-pin':
          goExisting();
          navigate('/alden');
          break;
        case 'day4-alice-open':
        case 'day4-focus-coach':
        case 'day4-escalation':
          goExisting();
          navigate('/alice');
          break;
        case 'day5-synapse-open':
        case 'day5-insight':
        case 'day5-save':
          goExisting();
          navigate('/synapse');
          break;
        case 'day7-settings':
        case 'day7-toggle':
        case 'day7-recap':
          goLauncher();
          flashTarget('[data-hl-tour="settings"]');
          break;
        default:
          break;
      }
    },
    [flashTarget, navigate, updateSettings]
  );

  useEffect(() => {
    return subscribeTutorialGoTo(handleTutorialGoTo);
  }, [handleTutorialGoTo]);

  const renderUISelector = () => (
    <UISelector
      settings={settings}
      updateSettings={updateSettings}
      onClose={() => setShowModeSelector(false)}
    />
  );

  const VoiceOrbSurface: React.FC = () => {
    const { voiceState, isOrbVisible, startListening, stopListening } = useVoiceState();
    return (
      <VoiceOrb
