#[derive(Debug, Clone, serde::Serialize, serde::Deserialize)]
pub struct BiometricReading {
    #[serde(rename = "type")]
    pub data_type: Option<String>,
    pub timestamp: f64,
    pub heart_rate: i32,
    pub hrv: Option<i32>,
    pub source: String,
}

/// Start the Python biometric bridge as a subprocess.
/// Reads stdout line-by-line and emits events to the frontend.
#[tauri::command]
pub async fn alice_start_biometric_stream(
    app_handle: tauri::AppHandle,
    mode: Option<String>,
) -> Result<String, String> {
    let python_exe = if cfg!(windows) { "python" } else { "python3" };
    let script_path = app_handle
        .path_resolver()
        .resolve_resource("python/biometric_bridge.py")
        .unwrap_or_else(|| std::path::PathBuf::from("src-tauri/python/biometric_bridge.py"));

    let mode_arg = mode.unwrap_or_else(|| "simulation".to_string());

    let child = Command::new(python_exe)
        .arg(script_path.to_string_lossy().to_string())
        .arg("--mode")
        .arg(&mode_arg)
        .stdout(Stdio::piped())
        .stderr(Stdio::piped())
        .spawn()
        .map_err(|e| format!("Failed to start biometric bridge: {}", e))?;

    // Spawn a thread to read stdout and emit events
    let stdout = child.stdout.ok_or("Failed to capture stdout")?;
    let app_handle_clone = app_handle.clone();
    std::thread::spawn(move || {
        let reader = BufReader::new(stdout);
        for line in reader.lines().flatten() {
            if let Ok(reading) = serde_json::from_str::<BiometricReading>(&line) {
                let _ = app_handle_clone.emit_all("alice://biometric-update", &reading);
            }
        }
    });

    Ok(format!("Biometric stream started in {} mode", mode_arg))
}

