async fn alice_get_deep_work_analysis_internal(
    db: &DatabaseManager,
    user_id: &str,
) -> Result<DeepWorkAnalysis, String> {
    let sessions = sqlx::query_as::<_, AliceDeepWorkSession>(
        r#"
        SELECT session_id, user_id, start_time, end_time, duration_seconds, paused_at, paused_total_seconds, goal, status, created_at
        FROM alice_deep_work_sessions
        WHERE user_id = ?
        ORDER BY created_at DESC
        LIMIT 200
    "#,
    )
    .bind(user_id)
    .fetch_all(&db.read_pool)
    .await
    .map_err(|e| format!("Failed to fetch Deep Work sessions: {}", e))?;

    let total_sessions = sessions.len() as i64;
    let completed_sessions = sessions.iter().filter(|s| s.status == "completed").count() as i64;
    let aborted_sessions = sessions.iter().filter(|s| s.status == "aborted").count() as i64;
    let active_sessions = sessions.iter().filter(|s| s.status == "active").count() as i64;

    let durations: Vec<i64> = sessions.iter().filter_map(|s| s.duration_seconds).collect();
    let average_session_duration_seconds = if durations.is_empty() {
        0.0
    } else {
        durations.iter().sum::<i64>() as f64 / durations.len() as f64
    };

    let completion_rate = if total_sessions == 0 {
        0.0
    } else {
        completed_sessions as f64 / total_sessions as f64
    };

    #[derive(FromRow)]
    struct SignalAverages {
        focus_avg: Option<f64>,
        stress_avg: Option<f64>,
        engagement_avg: Option<f64>,
    }

    let thirty_days_ago = (chrono::Utc::now() - chrono::Duration::days(30)).to_rfc3339();

    let averages = sqlx::query_as::<_, SignalAverages>(
        r#"
        SELECT
            AVG(focus) as focus_avg,
            AVG(stress) as stress_avg,
            AVG(engagement) as engagement_avg
        FROM alice_behavioral_signals
        WHERE user_id = ? AND created_at >= ?
    "#,
    )
    .bind(user_id)
    .bind(thirty_days_ago)
    .fetch_one(&db.read_pool)
    .await
    .unwrap_or(SignalAverages {
        focus_avg: None,
        stress_avg: None,
        engagement_avg: None,
    });

    Ok(DeepWorkAnalysis {
        total_sessions,
        completed_sessions,
        aborted_sessions,
        active_sessions,
        average_session_duration_seconds,
        completion_rate,
        focus_score: averages.focus_avg.unwrap_or(0.0),
        stress_score: averages.stress_avg.unwrap_or(0.0),
        engagement_score: averages.engagement_avg.unwrap_or(0.0),
    })
}

async fn build_deep_work_status(
    db: &DatabaseManager,
    user_id: &str,
) -> Result<DeepWorkStatusResponse, String> {
    let active_session = sqlx::query_as::<_, AliceDeepWorkSession>(
        r#"
        SELECT session_id, user_id, start_time, end_time, duration_seconds, paused_at, paused_total_seconds, goal, status, created_at
        FROM alice_deep_work_sessions
        WHERE user_id = ? AND status IN ('active', 'paused')
        ORDER BY created_at DESC
        LIMIT 1
    "#,
    )
    .bind(user_id)
    .fetch_optional(&db.read_pool)
    .await
    .map_err(|e| format!("Failed to fetch active Deep Work session: {}", e))?;

    let latest_session = if active_session.is_some() {
        active_session.clone()
    } else {
        alice_get_latest_deep_work_session(db, user_id).await?
    };

    // Keep distraction state aligned with current session state
    let mut distraction_blocking = get_distraction_block_state(db, user_id).await?;
    if active_session.is_some() && !distraction_blocking.enabled {
        distraction_blocking = persist_distraction_block_state(db, user_id, true).await?;
    } else if active_session.is_none() && distraction_blocking.enabled {
        distraction_blocking = persist_distraction_block_state(db, user_id, false).await?;
    }

    Ok(DeepWorkStatusResponse {
        active: active_session.is_some(),
        current_session: active_session,
        last_session: latest_session,
        distraction_blocking,
    })
}

